INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (414, 'Академия биатлона', 'Biathlon Academy', true, 'Академия биатлона- всесезонный спортивный комплекс для развития спорта в Красноярском крае и подготовки спортсменов международного класса с эффективной организационной структурой  и базой для проведения соревнований. Также Академия биатлона создана для пропаганды спортивных достижений и условий для работы спортивных секций для детей и юношества. Высокий уровень профессионализма тренеров, судей, федерации биатлона в г.Красноярске при наличии собственной материальной базы, соответствующей требованиям организации тренировочного процесса, проведения соревнований, позволит качественно подготавливать спортсменов для соревнований высокого уровня. Объект является социальным и создан с целью качественной подготовки спортсменов- биатлонистов на всех этапах, включая учебно-тренировочные сборы и для проведения соревнований различного уровня.. Основной экономической целью создания собственной материальной базы является минимизация бюджетных затрат на организацию учебно-тренировочных сборов и проведение соревнований  спортсменов. Академия биатлона является центром подготовки биатлонистов, членов сборной команды Красноярского края, кандидатов в сборные команды России по биатлону, лыжным гонкам, большому теннису, баскетболу, волейболу, настольному теннису, тяжелой атлетике, фитнесу, аэробике. Занятия членов сборной команды Красноярского края, кандидатов в сборные команды России по биатлону, лыжным гонкам, занятия по большому теннису, баскетболу, волейболу, настольному теннису, тяжелой атлетике, фитнесу, аэробике. Даты крупных соревнований и значимых событий: 5-9 ноября 2014 г. ПУЛЕВАЯ СТРЕЛЬБА Первенство Сибирского федерального округа &#40 юниоры до 21 года&#41 .', 'Наливное спортивное покрытие, пулеуловитель в тире', 'Academy of biatlon- all season sports complex for sports development in the Krasnoyarsk Territory and training world-class athletes with effective organizational structure and the basis for the competition.&lt br&gt &lt br&gt Also the Academy of biathlon was created to promote sporting achievements and working conditions for sports clubs for children and youth. High level of professionalism of coaches, referees, Biathlon Federation in Krasnoyarsk having their own material base, meeting the requirements of the organization of the training process, the competition, will enable to prepare for athletes for high level competitions.&lt br&gt &lt br&gt These social facilitiesяare created to quality training of sportsmen biathletes at all stages, including training camps and competitions at various levels . The main economic goal of creating their own material base is to minimize the cost of expenses for the organization of training camps and competitions for athletes.&lt br&gt &lt br&gt Also the Academy is a training center for biathlon, team members of the Krasnoyarsk Territory, the candidates in the national teams of Russia''s biathlon, cross country skiing, tennis, basketball, volleyball, table tennis, weightlifting, fitness, aerobics.&lt br&gt &lt br&gt Classes of team members of the Krasnoyarsk Territory, the candidates in the national teams of Russia''s biathlon, cross country skiing, playing tennis, basketball, volleyball, table tennis, weightlifting, fitness, aerobics', NULL, 'г.о. город Красноярск', 'Красноярский край', 'регионально-значимый', 'г. Красноярск', 'Krasnoyarsk', 'Красноярский край, г. Красноярск, ул. Биатлонная, 37', '37 Biatlonnaya str., Krasnoyarsk', 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-04-06', '2012-12-28', 726950000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'комплекс биатлонно-лыжный', '8 (391) 263-88-10', 'http://akademiabiatlona-krsk.ru/', 'баскетбол, биатлон, волейбол, лыжные гонки, настольный теннис, теннис, тяжёлая атлетика, фитнес-аэробика', 92.7321, 55.9946, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (423, 'Спортивно-тренировочный комплекс «Академия зимних видов спорта» ', 'Sports complex «Academy of winter sports»', true, 'Для более успешной подготовки спортсменов олимпийского уровня по таким видам спорта как прыжки на лыжах с трамплина, горные лыжи и сноуборд, фристайл, лыжные гонки, лыжное двоеборье и т.д. необходимо создание соответствующих условий, в том числе укрепление и развитие тренировочной базы. В этих целях на территории Красноярского края в период с 2010 по 2018 годы планируется создание крупного спортивного комплекса в рамках СКГАУ «Академия зимних видов спорта». Строительство объекта предполагается выполнять в четыре пусковых комплекса. Первый пусковой комплекс спортивного краевого государственного автономного  учреждения «Академия зимних видов спорта» &#40 далее - СКГАУ «Академия зимних видов спорта»&#41 - «Комплекс объектов для СКГУ «Академия биатлона» введен в эксплуатацию в 2011 году.  В составе второго пускового комплекса выделена первая очередь строительства, которая включает в себя: слаломную трассу, состоящую из двух склонов, разделенных подъемником, с индивидуальными стартовыми площадками, многофункциональный спортивный комплекс «Сопка», спортивно-тренерский блок «Горный». Спортивно – тренерский блок «Горный» является объектом II пускового комплекса спортивно-тренировочного комплекса «Академия зимних видов спорта». Спортивно-тренерский блок предназначен для использования федерацией горнолыжного спорта и сноуборда и СКГАУ «Академия зимних видов спорта». Он рассчитан на размещение 14 команд, в т.ч. 3 команды сноубордистов, 7 команд горнолыжников – юниоров, 4 команды горнолыжников- взрослых. ', 'При строительстве использовались типовые технологические решения.', 'For a successful preparation of Olympic-level athletes in sports such as ski jumping from a springboard, skiing and snowboarding, freestyle skiing, Nordic combined, etc. necessary to create the appropriate conditions, including the consolidation and development of the training base. For this purpose in the Krasnoyarsk Territory in the period from 2010 to 2018 it is planned to create a large sports complex within &quot Academy of winter sports.&quot &lt br&gt Construction of the facility is supposed to perform in four launch complexes. The first start-up complex of the regional sporting public autonomous institution &quot Academy of winter sports&quot  (hereinafter - SKGAU &quot Academy of winter sports&quot ) - &quot Complex of facilities for SKSU&quot  Academy of biathlon &quot was commissioned in 2011.&lt br&gt As part of the second start-up the first phase of construction is highlihted, which includes: a slalom track, consisting of two slopes, separated by a lift, the individual launch pads, multi-purpose sports complex &quot Sopka&quot , sports coaching block &quot Gorny&quot . Sports - coaching block &quot Gorny&quot  is the object II of the launch complex of sports training complex &quot Academy winter sports.&quot  Sports and coaching unit is intended for use by the Federation of skiing and snowboarding and &quot Academy winter sports.&quot  It can accommodate 14 teams, including 3 teams of snowboarders, 7 teams of junior-skiers , 4 teams adult-skiers&lt br&gt ', 'During the construction standard technological solutions were used.', 'г.о. город Красноярск', 'Красноярский край', NULL, 'Николаевская сопка', 'Nikolaevskaya sopka', 'Красноярский край, Николаевская сопка, ул. Биатлонная', 'Biatlonnaya str., Nikolaev sopka, Oktyabrsk region, Krasnoyarsk Territory', 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-01-14', '2015-12-30', 176360000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'комплекс горнолыжный', NULL, NULL, 'горнолыжный спорт, сноуборд', 92.7357, 56.0014, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (445, 'Крытый футбольный манеж', 'Indoor football arena', true, 'Создание крытого спортивного сооружения, ядром которого будет футбольное поле с искусственным покрытием, в значительной мере способно решить комплекс проблем по проведению учебно-тренировочных занятий и соревнований по футболу, по подготовке спортивного резерва. Также в Красноярском крае имеется потребность в площадках большой площади, пригодных для проведения  масштабных краевых мероприятий, а в г. Красноярске, и Советском районе в частности – потребность в спортивных сооружениях, на базе которых население будет иметь возможность заниматься спортом и производить подготовку резерва по различным видам спорта. Футбольный манеж станет материальной базой для занятий СКГАУ «Футбольный клуб «Енисей», позволит проводить тренировки и соревнования по футболу городского, регионального, в том числе международного уровня, а также организации занятий для: спортивных игр &#40 футбол, мини-футбол, гандбол и д.р.&#41   общефизической подготовки  тренажерной подготовки, а также позволит повысить уровень регионального футбольного клуба и отделений детско-юношеских спортивных школ по футболу. ', 'Проектом предусмотрено строительство крытого футбольного манежа 2 категории разряда «С» по стандарту Российского футбольного союза «Футбольные стадионы. Классификация». Также проектом предусмотрено комплектация крытого футбольного манежа современным технологическим, инженерным и спортивным оборудованием. Проектом предусмотрена антитеррористическая защищенность крытого футбольного манежа. Установлена автоматизированная система с компьютерной технологией оповещения и управления оборудования.', 'Creating indoor sports facilities, the core of which is a football field with artificial turf, to a large extent able to solve complex problems for the training sessions and competitions in football, for the preparation of sports reserve. Also in the Krasnoyarsk region there is a need in the grounds of large area suitable for large-scale regional events and in Krasnoyarsk, and the Soviet area in particular - the need for sports facilities, on the basis of which the public will have the opportunity to play sports and to train sportsmen for various kinds of sports.&lt br&gt &lt br&gt &lt br&gt Football arena will be the material base for _ &quot Football Club&quot  Yenisei &quot , will allow for training and competition in football of urban, regional, including the international level, as well as organizing activities for: sports games (football, indoor soccer, handball and others) general physical training, simulator training, and will also increase the level of regional offices of the football club and youth football schools', 'The project provides the construction of a 2 category C class indoor football arena according to the Russian Football Union’s &quot Football Stadiums. Classification&quot  Standard. The project also provides the equipping of the indoor football arena with modern process, engineering and sports equipment. The project provides anti-terrorism security of the indoor football arena. An automated system with computer warning and equipment control technology is installed.', 'г.о. город Красноярск', 'Красноярский край', NULL, 'г. Красноярск', 'Krasnoyarsk', 'Красноярский край, г. Красноярск, ул. Новгородская, 5', '5 Novgorodskaya str., Sovetsk region, Krasnoyarsk Territory', 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-06-01', '2014-06-01', 1875380000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'футбольное поле ', '8 (391) 211-44-63', NULL, 'футбол', 92.9846, 56.0588, 2);
