INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (473, 'Многофункциональный спортивный комплекс «Арена Север»', 'Multi-purpose Sports Center «Arena Sever»', true, 'Объект является социальным и создан с целью обновления и расширения материальной базы для проведения тренировочных занятий, учебно-тренировочных сборов, краевых и региональных соревнований по хоккею, фигурному катанию, скалолазанию, игровых видов спорта. Является главной ареной для местной хоккейной команды «Сокол» Ледовая арена предназначена для учебно-тренировочных занятий ХК «Сокол» и соревнований по хоккею, для занятий фигурным катанием, уникальный крытый скалодром - для занятий воспитанников школы альпинизма и скалолазания, многофункциональная  спортивная площадка - для занятий игровыми видами спорта, также имеется зал для хореографии.', 'Техника для строительства и оборудование дворца соответствуют мировым стандартам, с поставками из Англии, Германии, Дании и Канады. В группе проектировщиков дворца — опытные московские архитекторы. Сибирские климатические условия учтены. В основе проектных решений систем холодоснабжения, кондиционирования и осушки воздуха, водо- и льдоподготовки заложены передовые технические решения, апробированные в последние годы на многих катках и в ледовых дворцах в России и за границей. Установлена автоматизированная система с компьютерной технологией оповещения и управления оборудованием.', 'The facilities are social and developed to update and expand the material base for the training sessions, training camps, provincial and regional competitions in hockey, figure skating, rock climbing, team sports. It is the main arena for the local hockey teams &quot Sokol&quot  Ice Arena is designed for training sessions of HC &quot Sokol&quot  and competitions in hockey, figure skating,   a unique indoor climbing wall for lessons ofя   pupils of the school of mountaineering and climbing, multifunctional sports ground , for employment in competitive sports, there is also a room for choreography.', 'The ice arena is designed for training sessions of HC &quot Sokol&quot , hockey competitions, and figure skating lessons. A unique indoor climbing wall is for training of the mountaineering and climbing school students and a multi-purpose sports hall is for team sports. The arena also contains a choreography room.', 'г.о. город Красноярск', 'Красноярский край', NULL, 'г. Красноярск', 'Krasnoyarsk', 'Красноярский край, г. Красноярск, ул. 9 мая, 74', '74 May 9th str, Krasnoyarsk', 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-15', '2011-12-29', 1402380000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'арена ледовая', NULL, NULL, 'альпинизм, игровые виды спорта (разное), фигурное катание, хоккей, хореография', 92.8946, 56.0505, 2);
