INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (739, 'Модульный спортивный зал «Олимпия»', 'Sports Center «Olimpia»', true, 'Модульный спортивный зал предназначен для занятий художественной гимнастикой. В спорткомплексе «Олимпия» занимается более 700 детей и подростков, проводятся соревнования городского, краевого, регионального уровня.', 'Здание состоит из двух блоков: - спортивного зала размером 43,0 х 24,0 м и высотой  8,62 м с балконом на 200 зрительских мест.  Модульный спортивный зал предназначен для занятий художественной гимнастикой с единовременной пропускной способностью 40 человек в смену и продолжительностью занятий 1,5 часа. Зал оборудован специализированным современным оборудованием и коврами для занятий художественной гимнастикой &#40 один основной, два вспомогательных&#41 , с удобными раздевалками, душевыми, тренерскими комнатами, кабинетом врача, другими подсобными помещениями. - двухэтажной пристройки вспомогательных помещений размерами 12 х 30 м и высотой этажа 3,3 м. На первом этаже пристройки располагается общий вестибюль с гардеробом для верхней одежды, буфет, четыре командно-групповых раздевалки с душевыми и санузлами, медицинский кабинет, тренерская.  На втором этаже находятся фойе для зрителей балкона, зал шейпинга, кабинеты административного и обслуживающего персонала,  зал хореографии с тремя зеркальными стенами. В спорткомплексе «Олимпия» занимается более 700 детей и подростков, проводятся соревнования городского, краевого, регионального уровня. ', 'The module sports hall is designed for going in for rhythmic-sportive gymnastics. More than 700 children and young people attend “Olympia” sports complex  competitions of municipal, territorial and regional levels are held.', NULL, 'Город Хабаровск', 'Хабаровский край', NULL, 'г. Хабаровск', 'Khabarovsk', 'Хабаровский край, г. Хабаровск, пер. Молдавский, 10', '10 Moldavsky lane, Khabarovsk, 680000', 8701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-15', '2008-12-01', 123000000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'художественная гимнастика', 135.0904, 48.4624, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (558, 'Стадион «Шахтер»', 'Football field «Shakhtyor»', true, 'Футбольное поле предназначено для проведения учебно-тренировочных занятий и спортивных соревнований по футболу. Позволяет проводить тренировки и соревнования по футболу городского, регионального, в том числе международного уровня, а также организации занятий для: спортивных игр &#40 футбол, мини-футбол, гандбол и д.р.&#41 .', 'Футбольное поле оснащено искусственным покрытием.', 'The football field is designed for educational and training activities and football competitions. It enables to hold trainings and football competitions of city, regional and international levels as well as organize trainings in sports games (football, mini-football, handball, etc.).', 'A football field is equipped with artificial turf.   ', 'Назаровский район', 'Красноярский край', NULL, 'г. Назарово', 'Nazarovo', 'Красноярский край, г. Назарово, ул. Парковая, 35/а', '35а Parkovaya str., Nazarovo, Krasnoyarsk Territory', 4726000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-01-15', '2009-12-30', 16900000, NULL, NULL, NULL, NULL, 'tutishkina@stmpkrsk.ru', 'футбольное поле ', NULL, NULL, 'гандбол, мини-футбол, футбол', 90.4083, 56.0035, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (562, 'ШВСМ по видам борьбы им. Д.Г. Миндиашвили', 'School named after D.G. Mindiashvili', true, 'В Академии тренируются члены сборных команд Красноярского края по пяти видам борьбы. Учебно-тренировочный процесс проводится в 3х возрастных группах – взрослые, молодёжь, старшие юноши / старшие девушки. Академия является филиалом Центра спортивной подготовки России На базе Академии борьбы регулярно проводятся краевые и всероссийские соревнования по видам борьбы и учебно-тренировочные сборы За годы работы учреждения воспитано: - 10 олимпийских чемпионов и 2 бронзовых призера Олимпийских игр  - 38 чемпионов мира и 29 призёров чемпионатов и первенств мира  - 43 чемпиона и 22 призера Европы  - 39 победителей Кубков мира.  Высокие спортивные результаты невозможны без достойных высокопрофессиональных наставников. За период существования учреждения звания застуженных тренеров СССР и России получили 37 человек. В настоящее время в Академии работают 15 заслуженных тренеров. ', 'При строительстве использовались типовые технологические решения.', '&lt br&gt &lt br&gt In the Academy trained members of national teams of the Krasnoyarsk Territory in five kinds of wrestling. The training process is carried out in 3 age groups - adults, youth, older youth / senior girls. &lt br&gt &lt br&gt The Academy is affiliated with the Center for Russian sports training At the Academy of wrestling are regularly held regional and nation-wide competitions and &lt br&gt &lt br&gt Wrestling training camp Over the years, establishment brought up:&lt br&gt  10 Olympic champions and 2 bronze Olympic medalist, &lt br&gt - 38 world champions and winners of 29 championships and world championships, &lt br&gt - 43 champion and medalist of 22 European countries, &lt br&gt - 39 winners of the World Cup. &lt br&gt &lt br&gt High sports results are impossible without highly deserving teachers. During the existence of establishment the title of Honored Coach of the USSR and Russia got 37 people. Currently, the Academy has honored 15 coaches', 'During the construction standard technological solutions were used.', 'г.о. город Красноярск', 'Красноярский край', NULL, 'г. Красноярск', 'Krasnoyarsk', 'Красноярский край, г. Красноярск, ул. Марковского, 88', '88 Markovsky, Krasnoyarsk', 4701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2006-01-15', '2007-12-29', 171300000, NULL, NULL, NULL, NULL, 'academia_priem@mail.ru', 'многофункциональный спортивный комплекс', '211-38-68 ', NULL, 'вольная борьба, греко-римская борьба, дзюдо, женская борьба, самбо, спортивная борьба', 92.864, 56.015, 9);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (571, 'Крытый каток с искусственным льдом', 'Indoor artificial ice rink', true, 'Жители города и района получили возможность заниматься зимними видами спорта в удобное для них время, развитие массового спорта,  место для организации досуга, созданы дополнительные рабочие места. Даты крупных соревнований и значимых событий: 6-8 марта 2015 г. - краевой финальный турнир среди малых городов и сельских команд Пермского края на призы клуба юных хоккеистов «Золотая шайба» им. А.В. Тарасова, юношей 2000 – 2001 годов рождения». ', 'Холодоснабжение ледового поля будет осуществляться по системе «свободный холод». В зимний период холодоноситель будет поступать на воздушный теплообменник &#40 охладитель жидкости&#41  с помощью циркуляционного насоса минуя установку охлаждения жидкости через обводную линию.  В летнее время помещение ледовой арены может использоваться для не ледовых видов спорта. На первом этаже расположены 2 раздевальные, помещения для сушки одежды и обуви, помещение для заточки коньков, тренерская, кабинет врача, кабинет директора, помещение для рабочих, служебные и технические помещения. На втором этаже расположены венткамеры и технический балкон.  Пропускная способность ледовой арены при учебно-тренировочных занятиях 20 чел/смену, также объект предназначен для массового катания на коньках.  Здание катка – каркасное. Основные несущие элементы – металлические. Размеры здания в осях – 60*40 м. Размеры ледового поля – 56*26 м. Общая площадь здания – 2 870,9 м2. Охлаждающая плита ледового поля – бетонная. Благодаря возможностям систем холодоснабжения и вентиляции эксплуатация объекта - круглогодичная.  Для предотвращения промерзания грунта в холодный период года и возникновения возможных деформаций ледового поля предусмотрена трубная система обогрева грунта.  ', NULL, 'Cooling supply of an ice rink will be carried out according to a &quot free cold&quot  system.  In winter, a cooling agent will be supplied to an air heat exchanger (liquid cooler) using a circulation pump bypassing a liquid cooling plant through a bypass line.In summer, an ice arena can be used for non-ice sports. On the first floor there are two changing rooms, rooms for clothes and shoes drying, skate sharpening room, coaching room, doctor''s office, director''s office, staff room, and service and technical rooms. On the second floor there are ventilation chambers and technical balcony.The capacity of the ice arena during training sessions is 20 people per shift. The facility is also intended for mass skating.', 'Красновишерский район', 'Пермский край', NULL, 'г. Красновишерск', 'Krasnovishersk', 'Пермский край, г. Красновишерск, ул. Куйбышева, позиц. 11/а', '11 Kuybysheva str., Krasnovishersk, Perm Territory', 57626101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-12-01', '2013-08-12', 96043000, '09:00-21:00', NULL, '10:00-11:30', NULL, 'nvruo@mail.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 57.0897, 60.4152, 10);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (589, 'Спортивно-оздоровительный комплекс', 'Sports and recreation center', true, 'В спортивно-оздоровительном комплексе предусмотрены учебно-тренировочные и оздоровительные занятия и соревнования. Спортивно - оздоровительный комплекс является наиболее эффективной организационной формой в развитии массового спорта по месту жительства, т.к.  предоставляет возможность  заниматься  спортом всем категориям граждан  &#40 в том числе людям с ограниченными возможностями здоровья&#41 . Социальное значение и  роль массового спорта определяются как  социально ценные виды и результаты спортивной деятельности, направленные на укрепление и развитие  человеческого потенциала и общества в целом.  Даты крупных соревнований и значимых событий на базе МБУ «СОК» за 2014г: 25 января, 08 февраля, 22 февраля 2014г. - Турнир по мини-футболу «Открытый Кубок Пермского края» среди детей 2002 – 2003 г.р. 09 февраля 2014г. - Первенство Пермского края по баскетболу «Юниорская женская лига». 28 февраля - 02 марта - Первенство ПФО по рукопашному бою среди юношей и девушек 14-15, 16-17 лет. 06 марта 2014г. - Первенство Пермского края по плаванию, среди людей с нарушениями опорно-двигательного аппарата. 12 – 15 марта 2014г. - Краевой турнир памяти Новикова по боксу. 29 марта 2014г. - Первенство Пермского края по дзюдо. 06 апреля 2014г. - Первенство Пермского края по кекусинкай каратэ. 26 апреля 2014г. - Всероссийский турнир по настольному теннису. 24 мая 2014г. - Фестиваль спортивных единоборств. 27 - 28 сентября 2014г. - Первенство Пермского края по смешанному боевому единоборству ММА среди юниоров. 09  ноября 2014г. - Первенство Пермского края  по футболу среди детей 2004 г.р. «Золотая осень». 15 -  16 ноября 2014 г. - Кубок Пермского края по волейболу. 21-23 ноября 2014г. - Первенство Пермского края  по баскетболу среди юношей 2001 г.р. 29  ноября 2014 г. - Открытые краевые соревнования по рукопашному бою «Кубок генерала Зайцева» среди юношей 14-15, 16-17 лет. 13 декабря 2014 г. - Открытый краевой турнир по смешанному боевому единоборству ММА. 19 декабря 2014г. - Открытое Первенство СДЮСШОР «Старт» по плаванию.', 'Система отопления здания предусмотрена двухтрубная с горизонтальными ветками для разных групп помещений, радиаторы биметаллические, оборудованные терморегуляторами. Установление  нормируемой температуры внутренних помещений достигается также с помощью воздушной системы отопления, совмещенной с приточной вентиляцией, путем использования тепла удаляемого  воздуха в теплообменниках для подогрева приточного воздуха. Предусмотрено и напольное водяное отопление обходных дорожек. Проектом водоподготовки бассейнов принят комбинированный метод обеззараживания: хлорирование и УФ-стерилизация, а на малой ванне дополнительно предусмотрено озонирование. 1&#41     Гидравлический лифт с креслом &#40 производство фирма «Новум», арт. 11 277, 15 219&#41 . Предназначен для облегчения входа и выхода из бассейна людям с нарушениями опорно-двигательного аппарата. Гидравлический цилиндр стула с подъемником присоединяется к стандартной водопроводной линии, давление в которой находится в пределах 3,2 – 4,2 кг/мм2. При таком давлении воды в линии грузоподъемность стула равняется приблизительно 135 кг.  2&#41     В плавательных бассейнах МБУ «СОК» используется тройная система очистки и водоподготовки: ультрафиолетовая обработка, хлорирование и озонирование с использованием моноблока для обработки воды для плавательных бассейнов UVAZONE 300 – 1500. Использование обработки озоном в сочетании с хлорированием позволяет достичь более низкого уровня связанного хлора, чем просто хлорирование. Это, в свою очередь, имеет огромное значение для комфорта и безопасности посетителей и персонала бассейнов, снижая раздражение глаз, кожи, повреждение волос и уничтожая специфический запах бассейна. ', 'Physical and sports centre is designed for educational, training and health activities as well as competitions.', 'The heating system of a building is two-pipe with horizontal branches for different groups of rooms and bimetal radiators equipped with thermostatic controllers. The rated temperature of internal rooms is also achieved by the air heating system, combined with forced ventilation, using heat of the exhaust air in the heat exchangers to heat the inlet air. Floor water heating of pool decks is also provided. The pool water treatment project adopted a combined method of disinfection: chlorination and UV sterilization. Ozonation is additionally provided for a small bath.', 'Чусовской район', 'Пермский край', NULL, 'г. Чусовой', 'Chusovoy', 'Пермский край, г. Чусовой, ул. Мира, 19', '19 Mira str., Chusovoy, Perm Territory', 57658101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-15', '2012-09-28', 415774274, '08:30-23:00', '12:00-18:00', '12:00-18:00', NULL, 'sok.chusovoy@mail.ru', 'многофункциональный спортивный комплекс', '8 (34 256) 6 06 71', 'sok-chus.ru', 'армспорт (армрестлинг), бадминтон, баскетбол, бокс, волейбол, гандбол, мини-футбол, настольный теннис, пауэрлифтинг, плавание, рукопашный бой, самбо, спортивная гимнастика, теннис, тяжёлая атлетика, футбол, черлидинг', 57.8004, 58.2698, 11);
