INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1061, 'Школьный стадион № 10', 'School stadium No.10', true, 'Школьный стадион включает в себя: поле для мини-футбола,  беговые дорожки, универсальную спортивную площадку, гимнастический городок, площадку для прыжков в длину.', 'Поле для мини-футбола с искусственным покрытием, беговые дорожки и универсальная спортивная площадка с наливным покрытием.', 'The school stadium includes a mini-football field, run tracks, a multi-purpose sports ground, a gymnastic ground, a section for long jump.', 'A futsal field is with artificial turf and running tracks and multi-purpose sports ground are with poured surfaces.', 'Лискинский район', 'Воронежская область', NULL, 'г. Лиски', 'Liski', 'Воронежская область, г. Лиски, ул. Трудовые резервы, 60/2', 'Voronezh region, Lisky, Trudovye Rezervy str., 60/2 ', 20621101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-04-01', '2012-06-25', 19244300, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'игровые виды спорта (разное)', 39.4816, 50.9933, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (683, 'Спортивное ядро с трибуной на 3000 мест в парке', 'Field', true, 'Спортивное ядро эксплуатируется ежедневно с 8-00 до 20-00, как для проведения учебно-тренировочного, так и для соревновательного процессов согласно ежегодному календарному плану спортивно-массовых мероприятий МБОУ ДОД ДЮСШ, района и края. Кроме того, жители с.Курсавка используют возможность заниматься спортивным бегом и оздоровительными упражнениями на спортивном ядре. Спартакиада по ОФП среди молодежи, соревнования по мини-футболу, легкоатлетический кросс «Олимпийская звездочка», спортивные соревнования школьников «Президентские состязания», соревнования «Папа, мама, я – спортивная семья!», соревнования по мини-футболу, футболу,  легкоатлетический кросс «Золотая осень».', 'значимых технологических решений при строительстве не применялось', 'Sports core is used daily from 08-00 to 20-00 both for holding of educational, training and competition processes in accordance with the annual calendar schedule of mass sports events of the Children and Youth Sports School Municipal Budgetary General Educational Establishment of Additional Education for Children, district and territory. Additionally, residents of the village of Kursavka use a possibility to go in for running and regenerative exercises in the sports core.', NULL, 'Андроповский район', 'Ставропольский край', NULL, 'с. Курсавка', 'Kursavka', 'Ставропольский край, с. Курсавка, ул. Спортивная, 8', '8 Sportivnaya str., Kursavka, Andropovsky region, Stavropol Territory', 7632410, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-11-01', '2009-12-30', 34100000, '8-00 до 20-00', '8-00 до 20-00', '8-00 до 20-00', NULL, 'kskskcp@rambler.ru', 'стадион', NULL, NULL, 'лёгкая атлетика, футбол', 42.4973, 44.46, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (688, 'Стадион «Сельмаш»', '«Selmash» Stadium', true, 'Спорткомплекс расположен в жилом микрорайоне, помимо проведения учебно-тренировочных занятий спортшколы по футболу, соревнований, турниров, в свободное время площадки предоставляются для занятий спортом жителям микрорайона. На базе комплекса проводят занятия физической культурой близлежащие общеобразовательные школы.  На стадионе проводятся: Первенство России, Первенство края, Первенство ЮФО и СКФО, международный фестиваль «Локобол – РЖД»', 'Объект введен в эксплуатацию в 1986 году, планомерно проводятся ремонтные работы, в 2009 году проведена реконструкция: баскетбольной и волейбольной площадок, произведено устройство искусственного покрытия мини-футбольного поля, в 2010 году произведено устройство искусственного покрытия футбольного поле с искусственным покрытием размером 104х69.', 'The sports complex is located in the residential neighbourhood. Besides educational and training activities held by football sports school, competitions and tournaments the fields are allowed for going in for sports for neighbourhood residents in vacant hours. Neighbouring general education schools give physical training classes on the base of the complex.The object was put into service in 1986. Repair works are performed in accordance with the schedule. In 2009 reconstruction of volleyball and basketball fields was made, mini-football field was covered with artificial turf.  In 2010 the football field was covered with artificial turf with dimensions of 104х69.', NULL, 'Город Пятигорс