INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (711, 'Физкультурно-оздоровительный компклекс', 'Physical training and recreation center', true, 'Ввод физкультурно-оздоровительного комплекса в эксплуатацию позволит: - привлечь дополнительно к регулярным занятиям физической культурой и спортом не менее тысячи жителей города Зеленокумска, - значительно снизить социальную напряженность по обеспечению спортивными сооружениями города Зеленокумска, - обеспечить детско-юношеские спортивные школы современным спортивным сооружением для проведения учебно-тренировочной работы, - обеспечить спортивными сооружениями людей с ограниченными возможностями, - подготавливать спортивные команды города к ответственным краевым и Российским соревнованиям, - проводить на территории города массовые городские и районные спортивные мероприятия.', 'При строительстве «Физкультурно-оздоровительного комплекса в г. Зеленокумске Ставропольского края», применяются современные строительные технологии.', 'Putting of the sports and recreation complex into service will allow for:- involving not less than a thousand of Zelenokumsk city residents in going in for sports and physical training on a regular basis - reducing significantly social tension in regard to availability of sports facilities in the city of Zelenokumsk - providing children and youth sports schools with a modern sports facility for educational and training activities - providing people with reduced capabilities with sports facilities - preparing sports teams of the city for important territory and all-Russian competitions - delivering municipal and district mass sports events within the territory of the city.', 'Modern construction technologies are used in the construction of the &quot Physical Training and Recreation Center in Zelenokumsk, Stavropol Region&quot .', 'Советский район', 'Ставропольский край', NULL, 'г. Зеленокумск', 'Zelenokumsk', 'Ставропольский край, г. Зеленокумск, ул. Кирова и ул. Кочубея', 'Kirov str., Kochubey str., Zelenokumsk, Sovetsky region, Stavropol Territory, 357910', 7650101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-01-15', '2014-12-30', 176300000, 'с 9.00 до 21.00', 'с 9.00 до 21.00', 'с 9.00 до 21.00', NULL, 'kskskcp@rambler.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, мини-футбол, плавание, фитнес-аэробика', 43.8681, 44.4123, 2);
