INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (745, 'Центр развития хоккея «Амур»', 'Ice Hockey Center «Amur»', true, 'Созданный спортивный комплекс является многофункциональным и может стать хорошей площадкой не только для хоккейных матчей, но и для соревнований по фигурному катанию, конькобежному спорту, керлингу и даже футболу – поверх льда можно уложить искусственный травяной газон. Одновременно здесь могут заниматься до 250 человек. возведение крытого ледового стадиона создаст благоприятные условия для развития хоккея с мячом в Дальневосточном регионе. В спортивном комплексе можно проводить международные товарищеские встречи, чемпионаты России, Дальнего Востока, заниматься фигурным катанием и конькобежным спортом.  Пропускная способность 60 человек в смену - хоккей с шайбой, фигурное катание, 120 человек в смену - свободное массовое катание на коньках. В учреждении, вместе с филиалами,  занимается 774 учащихся в 51 группе. Учебно-тренировочный процесс в учреждении осуществляют 22 тренера-преподавателя. Штатная численность краевого центра хоккея «Амур» составляет 114 единицы. ', 'Спортивный комплекс «Амур-Энергия» был  сдан в эксплуатацию в декабре 2008 года, как учебно-тренировочная база краевого государственного учреждения дополнительного образования детей.Участок под спортивную площадку находится в Индустриальном районе г. Хабаровска, в районе  ул. Прогрессивной - П.Морозова. Размещение спортивной площадки определено с учетом существующей планировочной ситуации. Спортивная площадка представляет собой здание спортивного комплекса, включающее в себя два крытых катка с искусственным льдом,  административно-бытовые и хозяйственные помещения. Архитектурно-планировочная структура проектируемого здания принята   с учетом противопожарных, санитарных, технологических норм. Здание строго ориентировано по сторонам света и расположено на оси находящихся рядом тренировочных футбольных полей &#40 север-юг&#41 . Также, в состав комплекса входит два футбольных поля с искусственным покрытием.  Спортивный комплекс предназначен для проведения  учебно-тренировочных занятий и соревнований по хоккею с шайбой, футболу, фигурному катанию на коньках, шор-треку, а также для свободного &#40 массового&#41  катания на коньках. Транспортно – пешеходный доступ к зданию осуществляется с магистрали общегородского значения – ул. им. П.Морозова,  что обеспечивает свободный въезд и выезд с территории комплекса. Предусмотрена автопарковка для посетителей и обслуживающего персонала спортивного комплекса  на 400 автомобильных мест. Для обеспечения свободы передвижения маломобильным группам  населения предусмотрено: - на входе в здание устройство пандуса  - на путях передвижения инвалидов-колясочников устройство проходов  шириной, допускающей проезд инвалидной коляски. Спортивный комплекс включает в себя два функционально-взаимосвязанных блока: Блок №1 - крытый каток с искусственным льдом, размерами ледовой арены 60х30 м и трибунами на 398 посадочных мест.  Блок-№2 - крытый каток с искусственным льдом, размерами ледовой арены 60х30 м.  Связующей вставкой между катками является блок административно-бытовых помещений, расположенных на двух этажах. Двухэтажная часть здания: бескаркасная с продольными и поперечными несущими стенами. Наружные стены из красного керамического кирпича. Стены внутренние из кирпича толщиной 380мм. Полы -линолеум, керамогранит, бетон. Крыша совмещенная невентилируемая, с внутренним водостоком. Утеплитель в покрытии - плиты полистирольные вспененные экструзионные «Пеноплекс».   ', 'Created sports complex is multifunctional and can be a good platform not only for hockey matches, but also for figure skating, speed skating, curling, and even football - artificial turf can be laid over ice. At the same time there may be engaged up to 250 people. the construction of an indoor ice stadium will create favorable conditions for the development of hockey with a ball in the Far East. The sports complex can host international friendly matches, championships of Russia, the Far East, figure skating and speed skating.', 'The capacity of 60 people per shift - ice hockey and figure skating, 120 people per shift - free mass ice skating. 774 students in 51 groups attend a facility together with branches. The training process in the facility is carried out by 22 coaches. The number of staff of the Regional Hockey Center &quot Amur&quot  is 114 people.', 'Город Хабаровск', 'Хабаровский край', NULL, 'г. Хабаровск', 'Khabarovsk', 'Хабаровский край, г. Хабаровск, ул. П. Л. Морозова, 30', '30 P.L.Morozov str., Khabarovsk, 680000', 8701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-15', '2013-12-01', 835800000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', '8-4212-42-87-80', NULL, 'фигурное катание, хоккей', 135.0877, 48.3883, 2);
