INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1742, 'Пришкольный стадион школы № 13', 'Interschool stadiumthe School No.13', true, 'Межшкольный стадион школы №13 важен для жителей близлежащих домов, так как основные спортивные объекты удалены.', 'Использовались типовые решения и современные технологии.', 'Inter-school stadium of the school No. 13 is of great importance for residents of neighbouring buildings, because the main sports objects are far away from them.', 'Standard solutions and modern technologies were used.', 'г.о. город Железногорск', 'Курская область', NULL, 'г. Железногорск', 'Zheleznogorsk', 'Курская область, г. Железногорск, мкр. 14, ул. Энтузиастов, 6', '6 Enthusiasts str., microdistrict № 14, Zheleznogorsk, Kursk region', 38705000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-07-28', '2010-10-15', 30657609, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'лёгкая атлетика, футбол', 35.3581, 52.3529, 34);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (842, 'Многофункциональный зал', 'Multi-purpose sports hall Дикопольцева, 35/5', true, 'Предназначен для проведения учебных занятий физической культурой и спортом, соревнований, занятий в секциях как школьников, так и жителей микрорайона.', 'Здание одноэтажное, без подвала, прямоугольное с габаритами в осях 54x18м.  В составе многофункционального зала  имеется набор следующих помещений:  1&#41  комплекс раздевальных, включающий в себя раздевальные помещения для девочек и мальчиков с - душевыми кабинами на 8 сеток каждая и санитарные узлы на 2 кабинки каждый,  2&#41  помещения для преподавательского и тренерского состава,  3&#41  технические помещения, включающие в себя водомерный узел, приточную вентиляционную камеру, вытяжную вентиляционную камеру, электрощитовую, помещение МОП.', 'It is designed for giving of physical training classes and going in for sports, competitions, activities of sports sections for both school-children and neighbourhood residents.', 'A one-story rectangular building with no basement with dimensions of 54x18m along the axes.A multi-purpose sports hall contains the following rooms:1) Set of changing rooms, including changing rooms for girls and boys with shower cabins with 8 shower heads each and restrooms with 2 cabins each 2) Rooms for teachers and coaches 3) Technical rooms, including a water meter unit, inlet ventilation chamber, exhaust ventilation chamber, switch room, and common areas.', 'Комсомольский район', 'Хабаровский край', NULL, 'г. Комсомольск-на-Амуре', 'Komsomolsk-on-Amur', 'Ха