INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (917, 'Спортивно-зрелищный комплекс «Звездный» на 7000 зрительских мест', 'Sports and Entertainment Center «Zvyozdny»', true, 'Спортивный центр предназначен для учебной работы с учащимися образовательных учреждений различного уровня, а также для физкультурно-оздоровительных занятий населения. База высшего учебного заведения  спортивных база или центр подготовки спортсменов-олимпийцев Даты крупных соревнований и значимых событий: Чемпионат России по гандболу среди мужских и женских команд Кубок ЕГФ по гандболу 2014, 2015. Первенство России по дзюдо 2013. Эстафета олимпийского огня. Мировая лига по водному поло среди мужских и жен команд – 2015 г. Кубок России по синхронному плаванию – 2015г Чемпионат России по водному поло.  ', 'Проектом предусмотрены большепролетные несущие конструкции ферм из клееной древесины, что существенно снижает нагрузки на опоры и фундамент, данное решение помогло реализовать оригинальное архитектурно-дизайнерские решения. Для увеличения прочности бетона, увеличения его морозостойкости и устойчивости к агрессивным средам, применяется материал пенетрон. Этот материал способствует увеличению гидрофобных свойств бетона, защищая его от сырости, а также способности бетона задерживать радиацию.  С целью экономии электроэнергии в помещении предусмотрена установка датчиков движения.', 'The sports centre is designed for educational work with students of educational institutions of different levels as well as for physical training and recreational activities of the local people.', 'The project provides large-span supporting truss structures made of laminated wood that significantly reduce the load on the bearings and foundation. This solution helped implement the original architectural and design solutions. To increase the strength of concrete, its resistance to frost and aggressive media, the Penetron material is used. This material increases the hydrophobic properties of concrete, protecting it from moisture, and the ability of concrete to delay radiation.To save electricity, motion sensors are installed inside.', 'Город Астрахань', 'Астраханская область', 'регионально-значимый', 'г. Астрахань', 'Astrakhan', 'Астраханская область, г. Астрахань, ул. Н. Островского, 147', '147 N.Ostrovsky str., Astrakhan', 12701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-12-30', '2013-07-22', 2454070000, NULL, NULL, NULL, NULL, 'mail@starcomplex.ru', 'многофункциональный спортивный комплекс', '(8512) 495500', NULL, 'водное поло, гандбол, каратэ, мини-футбол, настольный теннис, плавание, прыжки в воду, синхронное плавание, фитнес-аэробика, художественная гимнастика', 48.067, 46.3403, 14);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (919, 'Физкультурно-оздоровительный комплекс', 'Sports complex', true, 'МБУ «Котельниковский ФОК»  - учреждение, социальную значимость которого можно рассматривать в масштабе Котельниковского муниципального района. Учреждение эффективно используется как объект спорта для удовлетворения потребностей профессионального и массового спорта в физкультурно-оздоровительных, спортивных и спортивно-зрелищных услугах. Даты крупных соревнований и значимых событий: 26-28.01.2013 г. Областные зональные соревнования по баскетболу  «КЭС-БАСКЕТ»   05-07.04.2013 г. Чемпионат Волгоградской области по волейболу  среди мужских и женских команд по 2-ой группе  16-19.02.2013 г. Финал Школьной баскетбольной лиги «КЭС-БАСКЕТ»  20-24.05.2013 г. Финальные соревнования «Президентские спортивные игры в зачет 24-ой Областной спартакиады учащихся Волгоградской области  11-13.04.2014 г. Финал Чемпионата Волгоградской области по волейболу среди мужских команд по 2-ой группе  19.23.05.2014 г. Финальные соревнования «Президентские спортивные игры в зачет 25-ой Областной спартакиады учащихся Волгоградской области. ', 'При строительстве объекта были внедрены в работу и функционирование учреждения новейшие технологии  в инженерные системы жизнеобеспечения здания, например: система вентиляционного оборудования, пожарная и охранная сигнализация, наружное освещение, система видеонаблюдения, насосное оборудование и система очистки воды в плавательном бассейне, современное напольное покрытие спортивного зала, применение полипропиленовых труб в системе водоснабжения, водоочистки, канализации, отопления.', NULL, NULL, 'Котельниковский район', 'Волгоградская область', NULL, 'г. Котельниково', 'Kotelnikovo', 'Волгоградская область, г. Котельниково, ул. Родина, 27', 'Volgograd region, Kotelnikovo, 27 Rodina str.', 18624101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-01', '2010-10-07', 96000000, '06:00-22:00', '06:00-22:00', '06:00-22:00', NULL, 'fok404354@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'армспорт (армрестлинг), баскетбол, волейбол, гиревой спорт, мини-футбол, настольный теннис, плавание, футбол', 43.1474, 47.6309, 15);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (922, 'ФОК «Тотемский» ', 'Totma Physical Training and Recreation Center', true, 'Тотемский физкультурно-оздоровительный комплекс имеет большую социальную значимость для города Тотьма и Тотемского района. С его пуском значительно выросла численность жителей, регулярно занимающихся физической культурой и спортом. В дневное время при ФОКе работают группы здоровья для лиц пожилого возраста.  Особой популярностью у населения пользуются бассейн и тренажерный зал. В выходные дни для детей в бассейне работает аквапарк. На объекте проходят мероприятия годовой Спартакиады школьников и Спартакиады района среди поселений.  Услугами ФОКа пользуются работники учреждений и организаций района и города. Единственный спортивный объект г. Тотьма. Пользуется огромным спросом у населения. на базе ФОКа проводятся практически все районный спортивно-массовые мероприятия. Дата крупных соревнований и значимых событий: 10-12 января - Рождественская гонка &#40 лыжные гонки&#41 . 22-23 ноября - зональный этап Спартакиады ветеранов. 16 ноября 2014 г. – зональный этап I Спартакиады ветеранов &#40 пенсионеров&#41  Вологодской области. 23 февраля &#40 ежегодно&#41  – областной турнир по волейболу «Северные зори». 9 мая &#40 ежегодно&#41  – массовый заплыв в честь Дня Победы. 12 июня &#40 ежегодно&#41  – массовый забег «Тотемская верста». ', 'Кровля под бассейном выполнена по  шведской технологии.', 'The only sports object in the city of Totma. It is in massive demand among the local residents. Practically all the district mass sports activities are held on the base of the Sports and Entertaining Complex.', 'The swimming pool roofing was made according to the Swedish company technology.', 'Тотемский район', 'Вологодская область', NULL, 'дер. Варницы', 'hamlet Varnitsy', 'Вологодская область, дер. Варницы, ул. Совхозная, 14', '14 Sovkhoznaya str., Varnitsy', 19646000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2002-01-14', '2008-12-28', 19637900, '08:00-22:00', NULL, NULL, NULL, 'SmirnovaKV@gov35.ru', 'многофункциональный спортивный комплекс', '8(81739) 2-11-99', 'www.fok­totma.ru', 'волейбол, каратэ, настольный теннис, плавание, футбол', 42.754, 59.9832, 16);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (933, 'Центр здоровья и детского спорта Ледовый комплекс', 'Health and Children’s Sports Center', true, 'Ледовый комплекс предназначен для проведения тренировочных занятий по хоккею с шайбой и фигурному катанию на коньках и является Центром проведения соревнований районного, областного, всероссийского масштаба, различных региональных и международных турниров. В год проводится множество спортивных мероприятий, с помощью которых решаются следующие задачи: пропаганда здорового образа жизни, улучшение физкультурно-спортивной работы с занимающимися во внеучебное время, отвлечение детей от негативных явлений современной жизни, выявление талантливых спортсменов, определение сильнейших команд Вологодской области в категориях: ветераны, мужская, юниорская и юношеская, формирование сборных команд Вологодской области для участия во Всероссийских и региональных соревнованиях. Социальная значимость объекта заключается в: - обеспечение здорового образа жизни занимающихся и молодежи   -формирование знания о физической культуре и спорте  -самоопределение личности, создание условий для самореализации и.т.д. - предоставление услуг для организации учебного процесса  -проведение федеральных, областных муниципальных соревнований.  Даты крупных соревнований и значимых событий: 02-04 января - региональный &quot Рождественский турнир&quot  по хоккею с шайбой среди юношей 2006г.р. 05-08 января - региональный &quot Рождественский турнир&quot  по хоккею с шайбой среди юношей 2003 г.р. 10-12 января - региональный &quot Рождественский турнир&quot  по хоккею с шайбой среди юношей 2004 г.р. Февраль - областные зональные соревнования юных хоккеистов. &quot Золотая шайба&quot , им. А.В. Тарасова: 11 февраля - Спартакиада работников органов государственной власти области и местного самоуправления. 14-21 марта - Всероссийский турнир клуба &quot Золотая шайба&quot  им. А.В. Тарасова, юноши 2001-2002 г.р., СЗФ и ЦФО. 18-20 апреля - финал чемпионата области среди мужских команд 1 группы. Апрель - финальный турнир первенства России по хоккею среди любительских команд II лиги, СЗФО, сезона 2013-2014 гг., &quot Кубок Чемпионов&quot . 15-16 мая - Спартакиада пенсионеров области. Декабрь - открытый турнир Межрегионального отделения по хоккею Кубок &quot Золотое кольцо&quot , среди мужских команд. Всероссийские соревнования юных хоккеистов клуба «Золотая шайба» имени А.В. Тарасова среди команд Северо-Западного и Центрального федеральных округов, юноши 2002-2003 гг.р. в период с «16» марта 2015г. по «21» марта 2015г', 'Быстровозводимый каркас, металлоконструкция, сэндвичпанели. Здание трансформаторной подстанции общей площадью 52,1 кв.м., оборудованной 2 трансформаторами. Воздушная линия ВЛ – 10 кВ и кабельная линия КВ – 10 кВ, протяженностью 1348 м. Канализационная сеть, протяженностью 64,3м. Электрическая сеть, протяженностью 152,6м. Ливневая канализационная сеть, протяженностью 206,7м. Тепловая сеть, протяженностью 30,3м. Водопроводная сеть, протяженностью 35,1м. Искусственный лёд, который создаётся при помощи современного холодильного оборудования  Имеется вентиляция ', 'The ice complex is designed for trainings in hockey and figure-skating  it is the Centre of district, regional and all-Russian sports competitions, different regional and international tournaments. A numerous sports events are held every year with the help of which the following tasks are fulfilled: healthy lifestyle promotion, improvement of physical and sporting work with persons attending the complex during extracurricular time, keeping of children from negative phenomena of modern life, finding out of talented sportsmen, defining of the strongest teams of the Vologda Region in the categories: veteran, men and junior teams, forming of picked teams of the Vologda Region for participation in all-Russian and regional competitions.', 'Pre-fabricated frame, steel structure, sandwich panels. A transformer substation with a total area of ??52.1 m2 is equipped with two transformers.Overhead line - 10 kV and cable line - 10 kV, 1,348 m long.Sewerage network is 64.3 m long.Electric network is 152.6 m long.Storm sewer system is 206.7 m long.Heating network is 30.3 m longWater supply network is 35.1 m long.', 'Вологодский район', 'Вологодская область', NULL, 'пос. Майский', 'Maysky', 'Вологодская область, пос. Майский, 20, корп. 2', '20, build. 2, Maysky (in 11 km from Vologda), Vologda area, Vologda region, 160508', 19620000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-09-01', '2011-12-30', 162364000, '07.00-24.00', '07.00-24.00', '07.00-24.00', NULL, 'moudovologda@mail.ru', 'арена ледовая', '70-05-02', 'www.олимп35.рф', 'фигурное катание, хоккей', 39.7436, 59.2578, 17);
