INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1003, 'ДЮСШ по футболу', 'Football field', true, 'Ввод в эксплуатацию этого спортивного объекта позволил проводить не только учебно-тренировочные занятия в межсезонный период, но и проводить уроки физкультуры со школьниками БСОШ №2 и лицея на открытом воздухе в ноябре и марте. Также этот спортивный объект является популярным местом для занятий мини-футболом взрослого населения города и района, что несет особую роль в пропаганде здорового образа жизни среди подростков и детей. Мини-футбольное поле позволило увеличить число занимающегося населения района физической культурой в целом на 500 человек.', NULL, 'Putting of this sports object into service allowed for holding not only out-off-season trainings, but also physical training classes in November and March in the open air with students of the BSOSh No. 2 and lyceum. This sports object is a popular place for mini-football activities among adult residents of the city and district that plays an important role in healthy lifestyle promotion among teenagers and children. Mini-football field enabled to increase a number of district residents involved in going in for sports by 500 persons in total.', NULL, 'Богучарский район', 'Воронежская область', NULL, 'г. Богучар', 'Boguchar', 'Воронежская область, г. Богучар, ул. Дзержинского, 21/а', 'Boguchar, Dzerzhinsky str., 21/a', 20605101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-06-01', '2012-09-28', 20421000, NULL, NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 40.5551, 49.933, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1010, 'Дворец спорта «ХАВА»', 'Physical training and recreation center', true, 'Универсальный игровой зал предназначен для проведения соревнований и тренировочных занятий по различным видам спорта.', 'Трёхэтажное здание, общая площадь - 5110,5 кв.м Основное здание - быстровозводимые металлоконструкции с покрытием сэндвич-панелями. Бассейн - безхлорная система очистки воды. Пропускная способность оздоровительного оздоровительного бассейна - 34 чел. Пропускная способность игрового зала - 100 чел. В здании размещаются: - 3 спортивных зала: универсальный игровой зал с 4-мя раздевалками - 1118,22 кв.м &#40 в зале установлены: электронное табло, пульт комментатора, трибуны для зрителей на 150 мест&#41 . Покрытие пола выполнено из паркетной доски &quot клен канадский&quot , что соответствует современным международным стандартам. Зал фитнеса и сухого плавания - 114,03 кв.м Зал силовой подготовки - 70,16 кв.м - Бассейн на 4 дорожки, имеет размеры 25х11,0 м с глубиной от 1,2 до 1,8, общий объём бассейна - 400 куб.м, площадь зеркала воды - 275 кв.м.   - детская игровая комната &#40 детский тренажёр, коврики, пазлы, сухой бассейн, лабиринт &quot Движок&quot &#41   - гидромассажный кабинет и сауна &#40 общий массаж, гидромассаж &#40 ПМД&#41 , кедровая бочка&#41   - кафе &#40 48 посадочных мест&#41   - 10 комнат для отдыха спортсменов на время соревнований на 40 мест. Кроме этого на первом этаже разместились: административный блок, медико-восстановительный блок, технические помещения, санузлы, тренерские. На территории ФОКА оборудована открытая стоянка легковых автомобилей на 50 машино-мест.', 'The multi-purpose sports hall is designed for holding of competitions and trainings in different sports.', 'Main building - prefabricated metal structures coated with sandwich panels. Swimming pool - non-chlorine water treatment system. The capacity of a recreational swimming pool is 48 people. The capacity of a sports hall is 67 people.', 'Верхнехавский район', 'Воронежская область', NULL, 'с. Верхняя Хава', 'Verkhnyaya Khava', 'Воронежская область, с. Верхняя Хава, ул. Ленина, 12/а', 'Voronezh region, Verkhnyaya Khava, 12/а Lenin str.', 20611424, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-04-01', '2012-10-22', 279736809, '09:00-21:00', '09:00-21:00', '09:00-21:00', NULL, NULL, 'многофункциональный спортивный комплекс', '84734373124', NULL, 'игровые виды спорта (разное), плавание', 39.9297, 51.8444, 2);
