INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1099, 'СДЮСШОР «Юность»', 'Artificial turf football field «Yunost»', true, 'Единственное футбольное поле в городе с искусственным покрытием последнего поколения. Даты крупных соревнований и значимых событий: Ежегодно, с мая по октябрь первенства, чемпионаты области по футболу.', 'Отвод  воды путём дренажной системы, проложенной под синтетическим покрытием, оставляющее поле практически сухим даже при обильных осадках в виде дождя.', 'Football field with artificial turf.', NULL, 'Город Торжок', 'Тверская область', NULL, 'г. Торжок', 'Torzhok', 'Тверская область, г. Торжок, ул. Энгельса, 4', '4 Engels str., Torzhok, Tver region', 28750000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-07-04', '2011-11-28', 20421000, NULL, NULL, NULL, NULL, 'torzhok.sportschool@yandex.ru', 'стадион', '8(48251)9-17-29', 'http://sdusshor-unost.ru/ ', 'футбол', 34.9745, 57.0489, 20);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1101, 'ДЮСШ «Химик»', 'Sports School for Children and Young People «Khimik»', true, 'На объекте регулярно проходят соревнования и мероприятия с большим количеством участников и зрителей, ежедневно функционируют детские секции, прививающие у занимающихся потребность к систематическим занятиям физкультурой и спортом, выполняя главную задачу – ведение  здорового образа жизни.   Даты крупных соревнований и значимых событий: Первенство России 3 дивизиона зона «Черноземье»  с апреля по ноябрь каждого года.', 'Подогрев футбольного поля позволяет проводить мероприятия круглогодично.', 'Football field with artificial turf.', NULL, 'Город Новомосковск', 'Тульская область', NULL, 'г. Новомосковск', 'Novomoskovsk', 'Тульская область, г. Новомосковск, ул. Садовского, 52', '52 Sadovsky str., Novomoskovsk, Tula region', 70724000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-07-01', '2014-03-01', 27621000, '09:00-20:00', NULL, NULL, NULL, 'skhimik@bk.ru', 'футбольное поле ', '(48762) 5-66-64, 5-41-33', 'fchimik.ru', 'футбол', 38.3077, 54.0152, 21);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1108, 'Велотрек', 'Renovationthe cycle track', true, 'Спортивные здание и сооружения. Помещения для персонала и комнаты для медицинского обслуживания спортсменов.', 'Высота проезда после реконструкции составит 4,6 м. с шириной 3,6 м.  Рабочим проектом предусмотрено восстановление и строительство подъездных автодорог и тротуаров после окончания строительства.  Проектом предусмотрена парковка на 15 легковых автомашин с северной стороны западной трибуны и площадки для контейнеров с мусором, которая размещается с южной стороны.', 'Sports buildings and constructions. Premises for personnel and rooms for medical services for sportsmen.', 'Passage height after the renovation will be 4.6 m with a width of 3.6 m.The working draft provides the restoration and construction of access roads and pavements after the construction.The project provides a parking for 15 cars on the north side of the western stands and an area for trash skips that is located on the south side. ', 'Город Тула', 'Тульская область', 'регионально-значимый', 'г. Тула', 'Tula', 'Тульская область, г. Тула, пр-т Ленина, 87', '87 Lenin avenue, Tula, Tula region, 300012', 70701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2012-12-29', 147825000, NULL, NULL, NULL, NULL, NULL, 'велотрек', NULL, NULL, 'велоспорт-трек', 37.6037, 54.1745, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1122, 'ФОК «Волна»', 'Physical training and recreation center', true, 'В ефремовском ФОКе разместились универсальный спортивный зал размером 42 на 24 м, тренажерный зал, раздевалки, душевые, административно-бытовые помещения. На базе нового спортивного объекта будут проводить тренировки и соревнования муниципального и областного уровня по баскетболу, волейболу, мини-футболу и другим видам спорта. За смену спортивный объект сможет принять 81 посетителя, а в спортзале одновременно сможет заниматься до 50 человек.', 'Крытый многовидовой соревновательно -тренировочный бассейн 25 метров. Каркас - легкие металлические конструкции, стальные рамы из двутавров переменного сечения, пролет 24 м, с шагом 6 м. Стены, кровля – 3-х слойные панели. Ванна бассейна – металлическая, размер в плане 25 х 11 м, глубина 1,4 – 1,8 м. Система водоподготовки – технология предусматривает коагуляцию дисперсных примесей, фильтрование через зернистую загрузку, обеззараживание для обеспечения эпидемиологической безопасности, а также нагрев воды до нормального показателя.', 'Covered multi-purpose swimming pool of 25 meters in length for competitions and trainings.', 'Frame - light metal structures, steel frames made of variable cross-section I-beams, a span is 24 m with a pitch of 6 m.Walls, roof - sandwich panels.Pool bath - metal, size in plan is 25 x 11 m, depth is 1.4 - 1.8 mWater treatment system – technology provides the coagulation of dispersive particles, filtration through granular beds, disinfection for epidemiological security, and water heating to the normal rate.', 'Город Ефремов', 'Тульская область', NULL, 'г. Ефремов', 'Efremov', 'Тульская область, г. Ефремов, ул. Тульское шоссе, 6', '6 Tulskoye shosse str., Efremov, Tula region', 70714000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-08-07', '2009-08-29', 58663504, NULL, NULL, NULL, NULL, 'efremov.volna@yandex.ru', 'многофункциональный спортивный комплекс', '8(48741)5-05-70', NULL, 'аквааэробика, акватлон (подводная борьба), плавание', 38.0881, 53.149, 22);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1142, 'Ледовый дворец «Россошь»', 'Ice Sports Palace «Rossosh»', true, 'Пропаганда здорового образа жизни в различных слоях населения города и района.', 'Спортивно-оздоровительный комплекс представляет собой ангар из легких металлоконструкций канадской компании «HONCO».', 'Healthy lifestyle promotion among different social groups of the city and district residents.', 'The sports and recreation center is a hangar made of light metal structures of the Canadian company «HONCO».', 'Россошанский район', 'Воронежская область', NULL, 'г. Россошь', 'Rossosh', 'Воронежская область, г. Россошь, ул. Белинского, 18', 'Voronezh region, Rossosh, Belinsky str., 18', 20647101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-05-20', '2008-10-01', 95097000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 39.5719, 50.1952, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1433, 'ДЮСШ  «Квант» на футбольном стадионе «Труд»', 'Football Stadium «Kvant»', true, 'Стадион «Труд» имеет большое значение в спортивной жизни города. На нем проходят тренировки отделения футбола ДЮСШ «КВАНТ», домашние матчи ФК «КВАНТ», соревнования по футболу регионального значения, спартакиада школьников города, спортивные праздники для жителей города. Даты крупных соревнований и значимых событий: Домашние матчи ФК КВАНТ в Первенстве России по футболу 3 лига группа «А» &#40 зона Московской области&#41 , домашние матчи футбольной команды города в зачет летней областной спартакиады среди муниципальных образований Калужской области, спартакиада среди школ города в 2012 г., «День молодежи» -27.06.2012г., «День физкультурника» - 11.08.2012г. ,  турнир по футболу посвященный «Дню Города» - 28.07.2012г.', 'Строительство велось с использованием типовых технологий.', '“Trud” Stadium is of great importance in sports life of the city. Trainings of football section of “KVANT” Children and Youth Sports School, home matches of «KVANT” Football Club, football competitions of regional level, Spartakiad games among city school children, sports holidays for city residents are held here.', 'During the construction standard technological solutions were used.', 'Город Обнинск', 'Калужская область', NULL, 'г. Обнинск', 'Obninsk', 'Калужская область, г. Обнинск, ул. Шацкого, 1', '1 Shatsky str., Obninsk, Kaluga region', 29715000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-08-23', '2011-11-30', 22871000, NULL, NULL, NULL, NULL, 'sport@admobninsk.ru', 'стадион', NULL, NULL, 'лёгкая атлетика, футбол', 36.6036, 55.0945, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1180, 'СДЮШОР № 5 по футболу', 'Football field «Pioner»', true, 'Цель реализации проекта является обеспечение жителей города физкультурно-оздоровительным учреждением для проведения учебно-тренировочных, секционных и индивидуальных занятий, повышение массовости занятий футболом. Реализация данного проекта приведет к значительному увеличению числа детей и подростков, занимающихся массовым футболом, обеспечит более качественную подготовку футболистов для юношеских  и молодежных команд, а в дальнейшем и команды  мастеров «Балтика», внесет значительный вклад в развитие футбола в Калининграде, укрепление здоровья подрастающего поколения.', 'В основании поля выполнен дренаж, отведение  поверхностных вод предусмотрено  в водосборный лоток со сбросом в ливневую канализацию. Основание поля выполнено из 2-х слоев гранитного щебня фракции 20-40 и 0-16мм.  Вдоль длинной стороны футбольного поля  предусматривается  устройство  типовой 4-х рядной сборно-разборной трибуны на 266 мест, гостевой автостоянки на 56 мест и 7 мест для маломобильного  населения, хозяйственной площадки и зоны отдыха.  Игровое поле огораживается по периметру забором. Проектом предусматривается наружное освещение футбольного поля, высадка зеленых насаждений в виде деревьев и кустарников. Вокруг футбольного поля предусмотрен тротуар с возможностью  проезда  автомобилей  скорой медицинской помощи и пожарных машин.', 'The purpose of project implementation is to provide the city residents with a fitness and health recreation facility for educational, training, section and individual sports activities, increase of participation scale in going in for football. Implementation of this project will result in significant growth of a number of children and teenagers going in for football  ensure more qualitative training condition of football players of junior and youth teams and “Baltika” professional team in the future  significantly contribute to development of football in Kaliningrad, strengthening of the rising generation health.', 'At the base of a field a drainage system is made. Surface water is drained to a water collecting tray and then discharged into storm sewers. The base of the field is made of 2 layers of crushed granite with fraction of 20-40 and 0-16mm.Standard four-row dismountable stands for 266 seats, visitor parking for 56 seats and 7 seats for people with limited mobility, and service and recreation areas are provided along the long side of the football field. The field is fenced along the perimeter. The project provides outdoor lighting of the football field and planting of trees and shrubs. A sidewalk with the ability to access for ambulances and fire engines is provided around the football field.', 'Город Калининград', 'Калининградская область', NULL, 'г. Калининград', 'Kaliningrad', 'Калининградская область, г. Калининград, пр-т Мира, 134', '134 Prospekt Mira str., Kaliningrad, Kaliningrad region', 27701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-01', '2014-01-01', 29640000, NULL, NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 20.4536, 54.7244, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1192, 'Школа олимпийского резерва по спортивной гимнастике Ларисы Латыниной', 'Sports School Olympic Reserve Larisa Latynina', true, 'Основная задача учреждения –вовлечение максимально возможного числа детей в систематические занятия спортом, формирование у детей потребности в здоровом образе жизни.  Школа  имеет в своем составе 4 отделения, предметом деятельности, которых является: спортивная гимнастика &#40 девочки, мальчики&#41 , танцевальный спорт, развивающая гимнастика для детей 3-4 лет и подразделение для ведущих спортсменов Калужской области с круглосуточным пребыванием детей.  В настоящее время в ГБОУ «СДЮШОР Л.Латыниной» занимается более 1200 человек в 80 группах. На сентябрь 2012 года на обучение в школу запись составляет 423 ребенка. Подразделение для ведущих спортсменов Калужской области с круглосуточным пребыванием детей &#40 по типу интерната&#41  сегодня насчитывает 84 спортсмена: представители спортивной и художественной гимнастики, волейбола, футбола, тяжелой атлетики, плавания, для которых  разработан режим образовательного и учебно-тренировочного процесса с использованием современных методических разработок,  предусматривающий двухразовые тренировки и индивидуальное обучение в общеобразовательных школах города.\ Даты крупных соревнований и значимых событий: - декабрь 2009 г. – торжественные мероприятия, посвященные 75-летнему юбилею Ларисы Латыниной - май 2010 г. - Российский турнир по танцевальному спорту «Мелодии весны», посвящённый 65-летию Победы в Великой Отечественной Войне &#40 участники 150 пар из 15 клубов России&#41  - май 2010 г. - Фестиваль спортивной гимнастики для детей детских домов Калужской области &#40 10 детских домов, около 500 детей&#41   - март 2011 г. Благотворительный марафон «Дети - детям!», в помощь детской областной больнице, итогом, которого стало приобретение медицинских инструментов для проведения операций &#40 пр-ва Германии&#41  и другая помощь на сумму 680 тысяч рублей  - март 2011 г. – Первенство России ЦФО по спортивной гимнастике  - ноябрь-декабрь  &#40 ежегодно&#41  - Всероссийские соревнования  «Кубок Губернатора Калужской области по спортивной гимнастике», собирающие  спортсменов из разных городов России  - декабрь &#40 ежегодно&#41   –Международный турнир Ларисы Латыниной по спортивной гимнастике «Золотая Чайка», в соревнованиях принимают участие сильнейшие гимнасты зарубежных стран. ', 'В 2007 г. завершена реконструкция существующего объекта, в ходе которой достроена часть спорткомплекса, включающая в себя гостиничный комплекс, холл, гардероб, двухуровневые трибуны, раздевалки для девочек и мальчиков.  Двухуровневые трибуны для зрителей общей численностью 444 посадочных места и ВИП-трибуны на 80 посадочных мет, позволяют наблюдать за спортсменами во всех уголках зала. К услугам зрителей – гардероб на 500 человек, туалеты, бар, просторный холл. Спорткомплекс оборудован современными системами видеонаблюдения, освещения, отопления, вентиляции и кондиционирования воздуха, а также профессиональным звуковым оборудованием. Озвучение зала выполняется тремя группами акустических систем по периметру зала &#40 20 колонок&#41 .  Спортивное оборудование, используемое в работе, представляет собой последние, современные разработки немецкой компании «ШПИТ» по производству гимнастического оборудования, и имеет сертификат соответствия международной федерацией спортивной гимнастки &#40 ФИЖ&#41 .   Спортивный зал школы оборудован современными поролоновыми ямами для отработки сложнейших гимнастических упражнений.', 'The main task of the institution is to involve as many children as possible in going in for sports on a systematic basis, development of demand for health lifestyle among children.The school includes 4 branches which have subjects of activities as follows: artistic gymnastics (girls, boys), dancing sports, developing gymnastics for children of 3-4 years old and a subdivision for leading sportsmen of Kaluga Region with round-the-clock staying of children.Currently more than 1,200 persons in 80 groups attend the “Children''s and Youth Sports School of Olympic Reserve by L.Latynina“ State Budget Educational Institution. As of September 2012 423 children are registered for studying in the school. The subdivision for leading sportsmen of Kaluga Region with round-the-clock staying of children (by kind of a boarding school) amounts today up to 84 sportsmen: representatives of artistic and rhythmic-sportive gymnastics, volleyball, football, weight-lifting, swimming for whom a regime of educational and training  process is developed with account of modern study manuals. This regime provides two-times trainings and individual education in general education schools of the city.', 'In 2007, the renovation of the existing facility was completed. During the renovation, the construction of a part of the sports center was finished, including a hotel, hall, cloakroom, two-level stands, and changing rooms for girls and boys.Two-level stands for a total of 444 seats and VIP stands for 80 seats allow the observation of athletes all over the room. A cloakroom for 500 people, restrooms, bar, and spacious hall are available for spectators. The sports center is equipped with modern video surveillance, lighting, heating, ventilation and air conditioning systems, as well as professional audio equipment. A sound system of the sports hall consists of three groups of loud speakers along the perimeter of the sports hall (20 speakers).The sports equipment used in the work is advanced modern gymnastic equipment of the German company &quot SPIT&quot , and has a certificate of compliance with the International Federation of Gymnastics (IFG).The school’s sports hall is equipped with modern porolone pits for practicing complex gymnastic exercises.', 'Город Обнинск', 'Калужская область', NULL, 'г. Обнинск', 'Obninsk', 'Калужская область, г. Обнинск, пр. Самсоновский, 8/а', 'Obninsk, Kaluga region', 29715000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2004-01-01', '2007-12-27', 52400000, '09:00-20:00', NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'спортивная гимнастика, танцевальный спорт', 36.5865, 55.1034, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1276, 'Плавательный бассейн «Дельфин»', '«Delfin» Swimming Pool', true, 'В плавательном бассейне проводятся уроки физической культуры школьников города, занятия спортивно-оздоровительных групп, соревнования КФК.', 'Установка вертикальных насосов, подсветка ванн,ввод в процесс обеззараживания воды ионизаторов и бактерицидных ламп.', 'Physical training classes, trainings of sports and recreational groups, competitions of CFC are held in the swimming pool.', 'Installation of vertical pumps, bath lighting, and introduction of ionizers and germicidal lamps into a water disinfection process.', 'Миасский г.о.', 'Челябинская область', NULL, 'г. Миасс', 'Miass', 'Челябинская область, г. Миасс, ул. Нахимова, 24', '24 Nakhimov str., Miass, Chelyabinsk region, 456313', 75742000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '1998-01-19', '2007-12-28', 41312452, NULL, NULL, NULL, NULL, NULL, 'бассейн', NULL, NULL, 'плавание', 60.1412, 55.0881, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1286, 'Физкультурно-оздоровительный комплекс', 'Physical training and recreation center', true, 'Спортивный комплекс используется не только для занятий населения физкультурой и спортом, но и для проведения районных, областных мероприятий и соревнований. Его посещают воспитанники детских садов, общеобразовательных учреждений, ветераны труд, люди с ограниченными возможностями. Даты крупных соревнований и значимых событий: 27.09.14г. Открытый областной турнир по дзюдо памяти воинам котельничанам  12-13.12.14 V традиционный турнир по баскетболу среди мужских команд  5-6.03.15 Первенство области по мини-футболу среди мальчиков  28.03.15 Первенство области по мини-футболу среди любительских команд  24.04.15 Спартакиада пенсионеров России Кировской области, посвященная 70-й годовщине Победы в Великой Отечественной войне 1941-45 гг, муниципальный этап.', 'Посещаемость ФОКа составляет 99,8&#37  от пропускной способности сооружения. В день комплекс посещает 432 человека. Площадь универсального зала составляет 1056 м2 с металлическим каркасом и легкими ограждающими конструкциями. Технологические решения в соответствии с типовым проектом.', 'The sports complex is used not only for local people to go in for physical exercising, but also for holding of district and regional activities and competitions. Pupils of nursery schools and general education institutions, veterans of work, people with reduced capabilities attend it.', 'The PTRC attendance is 99.8% of the facility capacity. Each day 432 people visit the center. An area of a multi-purpose sports hall is 1056 m2 with a metal frame and light enclosing structures.', 'Котельничский район', 'Кировская область', NULL, 'г. Котельнич', 'Kotelnich', 'Кировская область, г. Котельнич, ул. Советская, 170', '170 Sovetskaya str., Kotelnich, Kirov region', 33710000, NULL, 'строительство', '2009-08-03', '2009-12-25', 67100000, '08:00-21:00', NULL, NULL, NULL, 'suadmkot@yandex.ru', 'многофункциональный спортивный комплекс', '(83342) 4-77-31', NULL, 'баскетбол, бодибилдинг, волейбол, дзюдо, каратэ, футбол', 48.3239, 58.3048, 23);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1434, 'Универсальный спортивно-зрелищный комплекс «Богатыри»', 'Universal sports and entertainment  complex «Bogatyri»', true, 'Столь долгожданный для Кирова Дворец единоборств спроектирован для проведения тренировок и крупных соревнований по греко-римской, женской и мужской вольной борьбе, дзюдо, боксу, тхэквондо, рукопашному бою, карате, ушу, айкидо, самбо и кикбоксингу.  Планируется, что в спорткомплексе будут проводиться тренировочные сборы и соревнования международного и российского уровня по всем видам единоборств. В ближайших планах – создание на базе Дворца постоянно действующей тренировочной базы по всем видам единоборств и центра обмена опытом спортивных специалистов.   Дворец единоборств в перспективе будет предлагать и другие услуги: тренажерный зал, массажный центр, СПА комплекс, кафе здорового питания, платные занятия по единоборствам для кировчан. Помимо этого, соревнования, проводимые в  спорткомплексе, могут посещать инвалиды.   Дворец единоборств становится  настоящим центром спорта и дружеского общения, способствует продвижению различных видов спортивных состязаний, популяризации здорового образа жизни, активного отдыха, что особенно важно для молодежи. Даты крупных соревнований и значимых событий: 24-25 ноября 2012 – Первенство России по греко-римской борьбе &#40 профсоюзы&#41   21-24 декабря 2012 – Финальный Турнир «РАПИД Гран-При России 2012 года» - Кубка России по быстрым шахматам  15-23 марта 2013 – Первенство Вооружённых Сил России по боксу среди юниоров  27-28 апреля 2013 – Открытое первенство России по киокусинкай  25-27октября 2013 – Международный турнир «Звёздочки дзюдо»  и т.д. 11 октября 2014 – Всероссийский фестиваль по пауэрлифтингу «Жим-марафон» 25 октября 2014 – Кубок Росси по Киокусинкай Като 8-9 ноября 2014 – Всероссийский турнир по греко-римской борьбе Дьяконова А.П. 15-16 ноября 2014 - Всероссийский турнир памяти Сабакинских по самбо 23 ноября 2014 - Всероссийский турнир по киокусинкай каратэ 7 декабря 2014 - Чемпионат и первенство по танцам Приволжского федерального округа 22-25 января 2015 - Первенство Приволжского федерального округа по дзюдо среди юношей и девушек до 18 лет 1-10 марта 2015 -  Первенство Приволжского федерального округа по боксу среди юношей  21-23 марта 2015  - Чемпионат и первенство  Приволжского федерального округа по тхэквондо GTF 3-4 апреля 2015 - Чемпионат  Приволжского федерального округа по ММА 11 апреля 2015 - Чемпионат и первенство  Приволжского федерального округа по киокусинкай каратэ 29-30 апреля 2015 - Всероссийский турнир по греко-римской борьбе памяти С.М. Кирова ', 'В связи с изменением функционального назначения здания, произведена реконструкция, обновлен внешний облик сооружения. Сейчас здание запроектировано для работы в двух-трех режимах: для проведения крупных соревнований, тренировки спортсменов, массовых учебно-спортивных мероприятий и позиционирует себя как Дворец Единоборств. Объемно-планировочное решение здания обеспечивают взаимосвязь помещений, удобную для всех спортивных мероприятий, тренерского состава, судей, зрителей и прессы. Строительный объем 51 820 м3, полезная площадь здания 9 025,71 м2.  1.    Применение современной системы водоочистки  в бассейне сауны, обеспечивающую механическую, химическую, бактериологическую очистку воды в период эксплуатации бассейна  2.    Применение современных кондиционеров на приточных системах вентиляции, позволяющих обеспечивать оптимальный, комфортный режим тренировок в летний период эксплуатации спорткомплекса  3.    Применение пяти автономных систем теплоснабжения, связанных с микропроцессором системы управления, позволяющих автоматически вести регулировку параметров теплоснабжения спортивных помещений комплекса при работе каждой из них автономно или вместе. ', 'The long-awaited for Kirov Wrestling Palace is designed for training _ major competitions in Greco-Roman, women''s and men''s freestyle wrestling, judo, boxing, taekwondo, fighting, karate, martial arts, aikido, sambo and kickboxing.&lt br&gt  It is planned that in the sports complex will be held training camps of _ international and  all-Russian level for all types of martial arts. In the near future it is planned to create  a base for permanent trainingя of all types of martial arts and sports exchange desk of specialists. &lt br&gt Wrestling Palace in the future will offer other services: gym, massage center, spa complex, healthy eating cafe, paid lessons in martial arts for people who live in the city. In addition, the competitions held in the sports complex, &lt br&gt can attend&lt br&gt people with disabilities.&lt br&gt Wrestling Palace was a center of sports and companionship, promotes different kinds of sports, promotes healthy lifestyles, recreation, which is particularly important for young people. &lt br&gt &lt br&gt Dates of major events and significant events: &lt br&gt 24-25 November 2012 - Russian Championship in Greco-Roman wrestling (trade unions), &lt br&gt 21-24 December 2012 - Final Tournament &quot Rapid Grand Prix of Russia 2012&quot  - the Cup of Russia in rapid chess, &lt br&gt 15-23 March 2013 - Championship of the Russian Armed Forces Boxing Championships, 2&lt br&gt 7-28 April 2013 - Open Championship of Russia kyokushin, &lt br&gt 25-27 October 2013 - International Tournament &quot Zvezdochki of judo&quot , etc.', 'Due to changes in the functional purpose of a building, it was reconstructed and its exterior was renovated.Now the building is designed to work in two-three modes: for major competitions, athletes training, mass training and sports activities, and positions itself as the Palace of Combat Sports. The space and layout design of the building provides interconnection of rooms, suitable for all sports activities, coaches, referees, spectators, and media.The building volume is 51,820 m3, useful area is 9,025.71 m2.', 'Город Киров', 'Кировская область', NULL, 'г. Киров', 'Kirov', 'Кировская область, г. Киров, ул. Мира, 46 ', '46 Mira str., Kirov, Kirov region', 33701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '1991-01-14', '2012-12-25', 323946300, '8.00 - 20.00', '8.00 - 20.00', '8.00 - 20.00', NULL, 'Bogatyri2012@yandex.ru', 'многофункциональный спортивный комплекс', '(8332) 53-10-06', NULL, 'бокс, вольная борьба, гиревой спорт, греко-римская борьба, дзюдо, каратэ, кикбоксинг, рукопашный бой, самбо, танцевальный спорт, тхэквондо, ушу, шахматы, шашки', 49.6094, 58.6114, 25);
