INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1366, 'Ледовый дворец «Олимп-Арена»', 'Ice Sports Palace «Olimp-Arena»', true, 'Данное спортивное сооружение является единственным в Кировской области, ледовая арена которого вмещает 1 500 посетителей при проведении спортивных соревнований, а при проведении концертных мероприятий вместимость увеличивается до 2 000 зрителей.  Ледовый дворец «Олимп – Арена» находится в парковой зоне города. Площадь земельного участка 41726 м2. Вокруг дворца газоны на разных уровнях, клумбы сквер с детской площадкой, автостоянка на 200 машин, vip автостоянка и спецмашин. Асфальтовые дорожки протяженностью  1200 м. используются для катания  на лыжероллерах, роликовых конькобежных коньках, обычных роликовых коньках которые есть в прокате. Прокат дворца может предоставить 200 пар коньков разного размера, как для игры в хоккей, так и фигурные. Ледовый дворец «Олимп – Арена» имеет возможность организовать и провести соревнования по всем игровым видам спорта, настольному теннису, большому теннису, борьбе, боксу, художественной и спортивной гимнастике и т.д. Единовременно «Олимп – Арена»  может предоставить раздевалки для 10 команд. Во дворце имеется концертное оборудование со всей концертной технологией, включая световое оборудование, звуковое, оборудование для спецэффектов, оборудование для лазерного шоу, сборно-разборную сцену, что удовлетворит любой технический райдер и позволяет провести концерты, развлекательные шоу любого уровня. Даты крупных соревнований и значимых событий: 20 ноября 2010 года – праздничное шоу открытия Ледового дворца «Олимп – Арена». Присутствовали: министр спорта В.Л. Мутко, президент ФХР В.А. Третьяк, губернатор Кировской области Н.Ю. Белых, Олимпийские чемпионы А.Н. Мальцев, В.С. Мышкин, В.С. Давыдов. Участвовало более 100 чел. артистов и спортсменов. 5-6 марта 2011 года детский турнир по хоккею с шайбой «Кубок Третьяка» 25 августа 2012 года – презентация Кубка Гагарина КХЛ с участием А.Н. Мальцева, В.С. Давыдова. Матч ветеранов «Динамо» г. Москва – «Олимпия» г. Кирово-Чепецк. 18 ноября 2012 года – матч звезд ночной хоккейной лиги RLHL &#40 звезды советского хоккея&#41  – ветераны команды «Олимпия» г. Кирово-Чепецк. 17 апреля 2013 года – Кубок Поколения Молодежной хоккейной лиги матч сборных команд МХЛ. Сборная команда «Запад» - «Восток». 14-17 октября 2013 года – Кубок России по  мини-хоккею с мячом среди команд высшей лиги сезона 2013-2014 гг. 4 ноября 2013 года – шоу – праздник 45-летия детской хоккейной школе г. Кирово-Чепецка. С 7 по 12 марта 2014 г. – финальный турнир Приволжского федерального округа «Золотая шайба» им. А.В. Тарасова.    15 марта 2014 г. Турнир среди команд органов безопасности и правопорядка на призы Уполномоченного по правам человека в Кировской области. 30 марта 2014 г. Открытый Чемпионат и Первенство по карате киокушинкай Кировской области. 26  апреля 2014 г. Товарищеский матч по хоккею между ветеранами команды «Олимпия» и ветеранами сборной СССР и «Динамо» г. Москва, посвященный 65-летию ветерана спорта, Олимпийского чемпиона А.Н. Мальцева. С 14 по 16 мая 2014 г. Кубок по фигурному катанию на коньках «Майские звездочки 2014». С 7 по 10 октября 2014 г. Первенство России по мини-хоккею среди юношей 14-15 лет. 8 октября 2014 г. V Зональный турнир по настольным играм среди людей с ограниченными возможностями. С 14 по 17 октября 2014 г. Кубок России 2014 г. по мини-хоккею с мячом среди команд Высшей лиги. С 20.02.2015 по 22.02.2015  г. «Региональный турнир по хоккею с шайбой памяти Максима Копосова» среди команд 2004 г.р. С 10.02.2015 по 11.02.215 г.  «II этап всероссийских соревнований юных хоккеистов «Золотая шайба» имени А.В. Тарасова &#40 2004-2005 г.р.&#41  С 17.02.2015 по 18.02.2015  г. «II этап всероссийских соревнований юных хоккеистов «Золотая шайба» имени А.В. Тарасова &#40 2002-2003 г.р.&#41  С 24.02.2015 по 25.02.2015 г. «II этап всероссийских соревнований юных хоккеистов «Золотая шайба» имени А.В. Тарасова &#40 2000-2001 г.р.&#41  01.03.2015 г. «Открытый турнир города Кирово-Чепецка по Каратэ Кекусинкай &#40 кумитэ&#41  памяти дважды Героя Советского Союза, маршала И.С.Конева С 24.03.2015 по 25.03.2015 г.  соревнования юных хоккеистов «Надежда» &#40 2002-2003 г.р.&#41  С 26.03.2015 по 27.03.2015  г. соревнования юных хоккеистов «Надежда» &#40 2004-2005 г.р.&#41  С 25.04.2015 по 29.05.2015 г. Всероссийский финал Юниорской хоккейной лиги «Первенство России среди юниоров до 18 лет Юниорской хоккейной лиги».', 'Это полногабаритное многофункциональное спортивное сооружение площадью 12730 м2. Сооружение решено в двухэтажном исполнении с цокольным этажом. Цокольный этаж – тренажерный зал бодифитнеса, бильярдный зал, сауна, фитнес зал, четыре больших комфортабельных раздевалки с душами и санузлами, отдельно тренерские раздевалки. Первый этаж – полногабаритный игровой зал, тренажерный зал бодибилдинга &#40 для зала специальные  раздевалки с душами, санузлами&#41 , блок комфортабельных раздевалок для спортсменов с сушилками, мастерскими для заточки коньков, душевыми комнатами, санузлами отдельными раздевалками для тренеров, буфет-бар на 600 блюд для персонала и тренеров, турбо-солярий, пресс-центр, сектор проката спортинвентаря. Второй этаж -  фойе второго этажа позволяет зрителям обойти зал ледовой арены по кругу. В фойе расположены гардеробы, магазины, аппараты быстрого питания. На втором этаже находится кафе на 60 посадочных мест. Ледовая арена может принять в режиме спортивных соревнований 2000 человек, в концертном режиме более 3000 зрителей. Ледовый дворец «Олимп – Арена» находится в парковой зоне города.