INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1553, 'Физкультурно-оздоровительный комплекс', 'Physical training and recreation center', true, 'Со строительством спорткомплекса число занимающихся увеличилось до 23,5&#37 . Залы спортивного комплекса используют учащимися Санчурской средней школы, профессионального училища №46, медицинского училища, а также спортсменами ДЮСШ и клуба по месту жительства. Строительство ФОКа разрешила проблему по организации занятий физкультурой и спортом населения пгт Санчурска и, особенно среди молодежи и подростков. Спортивный комплекс используется не только для занятий населения физкультурой и спортом, но и для проведения районных, областных мероприятий и соревнований. Объект спорта служит центром физкультурно-спортивной работы с населением в районе и для организации межрайонных соревнований и региональных соревнований. Даты крупных соревнований и значимых событий: 1-3.05.2009-2013г. Межрайонный турнир по волейболу 16.04.2011г. Межрегиональный этап по мини-футболу 3-4.11.2013г. Сельские игры. -9.11.2014 г. - зональный этап по мини-футболу Всероссийского проекта среди образовательных учреждений «Мини-футбол  в школу»  22.02.2015 г. – межрегиональный турнир по мини-футболу среди ветеранов  29.03.2015 г. - районная спартакиада работающей молодежи.   ', 'Площадь спортивного зала составляет 1056 м2. Объект спорта служит центром физкультурно-спортивной работы с населением в районе и для организации межрайонных соревнований и региональных соревнований. В проекте применялись металлические арки на перекрытии, использовались сэндвич- панели в кровле.применение для окон профиля «профлекс». фундаменты - ж/б сборный, ж/б столбчатый монолит. стены - термопанели по металлическому каркасу. кровля - профнастил. окна - из алюминиевых профилей с 2х камерными стеклопакетами. ', 'Upon completion of the sports centre a number of persons going in for sports has increased up to 23.5%. The sports complex halls are used by students of Sanchursk secondary school, vocational school No. 46, medical secondary school as well as by sportsmen of the Children and Youth Sports School and the club at the place of residence.Construction of sports and recreation complex has solved a problem of organization of physical exercising and going in for sports by local residents of the city of Sanchursk, especially among teenagers and young people.The sports complex is used not only for local residents to exercise and go in for sports, but also for holding of district and regional events and competitions.', 'The sports hall area is 1,056 m2.', 'Санчурский район', 'Кировская область', NULL, 'пгт Санчурск', 'Sanchursk', 'Кировская область, пгт Санчурск, ул. Пионерская, 41', '41 Pionerskaya str., Sanchursk, Sanchursk municipal district, Kirov region', 33633151, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-08-23', '2009-12-19', 51766200, '10:00-20:00', NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', '(83357) 2-20-89', NULL, 'баскетбол, бокс, волейбол, настольный теннис, футбол, шахматы, шашки', 47.2507, 56.9356, 27);
