INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1795, 'Спортивный зал школы № 55', 'Sports hall the No.55', true, 'Спортзал предназначен для проведения учебно-тренировочного процесса,  тренировочных мероприятий и соревнований по легкой атлетике, волейболу, баскетболу, футболу, спортивным единоборствам, городского, областного и всероссийского уровня. В легкоатлетическом манеже располагается СДЮСШОР №5 м численностью детей 800 чел., с которыми занимаются 20 тренеров - преподавателей, также проводятся уроки физкультуры сш № 55, занимаются студенты филиала Орловской академии госслужбы, ЛГПУ, занимаются воспитанники ДЮСШ № 12 по футболу. С 20 до 23 часов оказываются услуги для проведения занятий по мини-футболу. Секция легкой атлетики насчитывает 74 группы. Занятия проводятся ежедневно с 9:00 до 11.00 и с 15:00 до 20.00. ', 'Размер манежа 120x40, двухэтажное административное здание, пропускная способность 250 человек для проведения учебно-тренировочных занятий продолжительностью 1 час 30 минут. Раздевалки &#40 2 для мальчиков и 2 для девочек&#41  оборудованы душевыми и туалетами, шкафчиками для одежды в количестве 40 штук в каждой раздевалке. На манеже имеется площадка, огражденная сеткой, размером 20x40 для мини-футбола, сектор для толкания ядра и метания диска, сектор для прыжков в высоту, с шестом, прыжковая яма для длины, 6 беговых дорожек по кругу размером в 200 метров. Манеж оборудован электроникой. Создание условий, ориентирующих граждан на здоровый образ жизни, занятия физической культурой и спортом, развитие спортивной инфраструктуры, повышение конкурентоспособности российского спорта.', 'The purpose of the sports centre construction was creation of conditions focusing citizens on healthy lifestyle, going in for physical exercises and sports, development of sports, competitive growth of Russian sports.', 'A sports hall with an area of 3,914 m2 is designed for one-time stay of 104 visitors. To hold competitions, there are dismountable stands for 360 people.', 'Город Липецк', 'Липецкая область', NULL, 'г. Липецк', 'Lipetsk', 'Липецкая область, г. Липецк, ул. Космонавтов', 'Kosmonavtov str., Sovetsky district, Lipetsk, Lipetsk region', 42701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-06-01', '2012-10-26', 161400000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'баскетбол, волейбол, лёгкая атлетика, футбол', 39.5614, 52.6162, 2);
