INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1823, 'Спортивный комплекс с универсальным залом и плавательным бассейном «Молодежный»', 'Sports center', true, 'Благодаря строительству спортивного комплекса будут созданы условия для развития игровых видов спорта и нового ранее не культивируемого в Валдайском муниципальном районе вида спорта - плавания. Даты крупных соревнований и значимых событий: 27.09. 2014 г.  прошёл третий ежегодный Открытый чемпионат Валдайского городского поселения по пауэрлифтингу &#40 жиму лёжа&#41  среди мужчин и женщин, посвящённый памяти кавалера ордена Мужества Алексея Аушева. 13.09.2014 г. – Сухая тренировка хоккейных команд  ХК«ЛЕДОКОЛ» г. Валдай и ХК «МЕТАЛЛУРГ» г. Боровичи и  купание в бассейне. 19.08.2014 г. – Сухая тренировка хоккейных команд  ХК «ЛЕДОКОЛ» г. Валдай и ХК «ШТОРМ» г. Мурманск &#40  школа олимпийского резерва&#41 и купание в бассейне. Спартакиада муниципальных служащих Валдайского муниципального района 4 октября 2014 год. Открытый Чемпионат Валдайского городского поселения по пауэрлифтингу &#40 классическому жиму лежа&#41 , 27 сентября 2014 года. Открытый Чемпионат Валдайского городского поселения по волейболу, с 1 по 30 ноября 2014 года. Личное Первенство и Чемпионат Новгородской области по настольному теннису среди лиц с ограниченными возможностями здоровья и инвалидов, 11 октября 2014 года Первенство Новгородской области по мини футболу среди юношей 2000-2001  г. р. и 2002 - 2003 г. р. &#40 детская футбольная лига&#41 . Открытый Чемпионат Валдайского городского поселения по мини-футболу. Турнир по волейболу, посвященный Дню защитника Отечества II этапа Спартакиады населения Валдайского муниципального района – Настольный теннис. Первенство области по настольному теннису среди юношей и девушек 1997 г.р. и моложе. Объект позволяет увеличить количество занимающихся физической культурой и спортом проводимых спортивно-массовых и физкультурно-оздоровительных мероприятий. Обеспечивает дополнительное количество рабочих мест. ', 'Общая площадь составит 5597,3 квадратных метров. Объект будет иметь прямоугольную форму с размерами 60,0 х 57,8м. Пропускная способность 204 человека в смену. Будет создано 45 рабочих мест. Здание 2-х этажное, каркасное на основе металлокаркаса. Ограждающие конструкции – из стальных рифленых оцинкованных секций «HONCO». Перегородки - гипсокартонные &#40 ГКЛО&#41  по металлокаркасу. Потолки- подвесные «Armstrong». Окна - металлопластиковые. Двери – металлопластиковые. Ванны плавательных бассейнов – монолитные ж/б. Полы - в залах для аэробики и игры в настольный теннис применяется спортивное покрытие «Taraflex Surfase». Арена - плашки из канадского клена.', 'Conditions for development of playing sports and swimming as well as a new sport not cultivated earlier in Valday municipal district will be created owing to construction of the sports complex.', 'Total area will be 5,597.3 m2.The facility will have a rectangular shape with dimensions of 60.0 x 57.8 m. The capacity is 204 people per shift. 45 jobs will be created. The facility is a 2-storey frame building constructed on the base of a metal frame.Enclosing structures – made of corrugated galvanized steel sections &quot HONCO&quot .Partitions - plasterboard on metal frames.Dropped ceiling &quot Armstrong&quot .Windows – made of metal.Doors – made of metal.Swimming pool baths - solid-cast reinforced concrete.Flooring - sports surface &quot Taraflex Surface&quot  is used in aerobics and table tennis rooms.Arena - plates made ??of Canadian maple.', 'Валдайский район', 'Новгородская область', NULL, 'г. Валдай', 'Valday', 'Новгородская область, г. Валдай, ул. Молодежная, 18', 'Intersection of Molodezhnaya str. and Lenin str., Valday', 49608101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-01-17', '2014-05-30', 380207000, NULL, NULL, NULL, NULL, 'agdvorcov@mail.ru', 'многофункциональный спортивный комплекс', '8 (816-66) 2-92-06', 'www.53sport.ru', 'бадминтон, баскетбол, волейбол, гандбол, мини-футбол, настольный теннис, пауэрлифтинг, плавание, теннис, черлидинг', 33.2389, 57.9852, 39);
