INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1963, 'Спортивный зал с плавательным бассейном', 'Sports hall with a swimming pool', true, 'Данный объект используется для проведения спортивно - массовых мероприятий – районного, областного, межрегионального уровней. Является учебно- тренировочной базой для проведения занятий и сборов.', 'При строительстве использовались типовые технологические решения.', 'This object is used for mass sports events of district, regional and interregional levels. It is an educational and training base for classes and camps.', 'During the construction standard technological solutions were used.', 'Песчанокопский район', 'Ростовская область', NULL, 'с. Песчанокопское', 'Peschanokopskoe', 'Ростовская область, с. Песчанокопское, ул. Азовская, 52', '52 Azovskaya str., Peschanokopsky, Peschanokopsky area, Rostov region, 347570', 60644455, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-09-17', '2010-04-06', 76830000, NULL, NULL, NULL, NULL, '01032013000@mail.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, гандбол, греко-римская борьба, лёгкая атлетика, мини-футбол, настольный теннис, плавание, спортивный туризм, футбол, художественная гимнастика', 41.0773, 46.1789, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (1971, 'Физкультурно-спортивный комплекс', 'Sports and physical training center', true, 'С каждым годом неуклонно возрастает роль учреждений дополнительного образования в обеспечении занятости детей и подростков, организации их  досуга, что, по сути, является значимыми мерами профилактики правонарушений, наркомании и других асоциальных проявлений среды несовершеннолетних.   Физкультурно-спортивный комплекс «Арена» образован и открыт в январе 2009 года. Он создан для реализации программ физического воспитания детей и организации физкультурно-спортивной работы по программам дополнительного образования детей. Организации и проведения досуга взрослого населения.  В оперативном управлении ГАОУ ДОД «ДЮСШ «АРЕНА» находится 2-х этажное здание физкультурно-спортивного комплекса общей площадью 4734,5 кв.м., в котором имеется: Универсальный спортивный зал 47х22 м., оборудованный передвижными телескопическими стойками, соревновательными щитами, кольцами для баскетбола  безрастяжечными стойками, сетками и судейской вышкой для волейбола. Все соревновательные площадки размещаются по центру арены. Судейско-информационный комплекс включает в себя два электронных текстовых табло и комплекты для судейства по соответствующим видам спорта. Четыре раздевалки при арене оборудованы как командные, где установлены шкафы для домашней одежды и кресла для отдыха и обсуждения игры в перерывах. В зале  проводятся учебно-тренировочные занятия и  соревнования местного, регионального и всероссийского уровней по баскетболу, волейболу, мини-футболу. Бассейн 25х13 м., оснащенный разделительными дорожками, стартовыми тумбочками, пластиковой мебелью. Раздевалки меблированы шкафами. Кабинеты медсестры и анализа воды совмещены, для чего, помимо смотровой кушетки, письменного стола, шкафа для хранения медицинских инструментов, установлен лабораторный стол. В бассейне проводятся учебно-тренировочные занятия и соревнования по плаванию, оздоровительные занятия аквааэробикой, сеансы оздоровительного плавания. Зал для занятий борьбой 24х18 м, площадью 360 кв. м, с пропускной способностью 40 человек. В зале проводятся учебно-тренировочные занятия и соревнования по самбо и дзюдо.   Зал аэробики 16х8 м, площадью 133,1 кв. м, с пропускной способностью 20 человек. Оборудован зеркалами, хореографическим станком. В зале проводятся физкультурно-оздоровительные занятия йогой, степ-аэробикой, оздоровительной гимнастикой, восточными танцами. Тренажерный зал 16х8 м, площадью 113,7 кв. м, с пропускной способностью 12 человек. Оборудован комплектом тренажеров, обеспечивающих как общее развитие мышечной системы, так и тренировку отдельных групп мышц. На 1 этаже комплекса расположен буфет на 26 посадочных мест, который предназначен для обслуживания спортсменов, персонала и посетителей.  Даты крупных соревнований и значимых событий: 2013 год 23 марта Открытое Первенство Рязанской области по дзюдо, посвященное памяти Героя России Игоря Филькина, 180 участников  25-27 октября Открытое Первенство Рязанской области по борьбе самбо, дзюдо и армейскому рукопашному бою, посвященное 82-ой годовщине образования Росрезерва, 1000 участников  4 декабря Областной спортивно-оздоровительный фестиваль среди лиц с ограниченными возможностями здоровья, 200 участников 2014 год 22 марта Открытое Первенство Рязанской области по дзюдо,  памяти Героя России Игоря Филькина, 170 участников ', 'Конструкция здания собрана из легких металлических конструкций. Наружные стены запроектированы из трехслойных «сэндвич» панелей с утеплителем из жестких минеральных плит на основе базальтового волокна. Технология водоподготовки для ванн большого и малого бассейнов, основана на современных средствах очистки, включая ультрафиолетовое обеззараживание, что позволяет уменьшить содержание хлора до уровня 0,15мг/л., что меньше, чем в сетях городского водопровода. Для обеззараживания воды в бассейне применяются новейшие технологии очистки и подготовки воды, что позволяет снизить содержание хлора до 0,1-0,3мг/л. Это существенно ниже, чем в городском водопроводе и безвредно для кожного покрова и глаз.', 'Every year has been steadily increasing role of supplementary education institutions in ensuring the engagement of children and young people, organizing their leisure time, which, in essence, is the significant measures of crime prevention, drug abuse and other anti-social manifestations of juvenile delinquency.яя&lt br&gt Sports complex &quot Arena&quot  was founded and opened in January 2009.яIt was created for the implementation of programs for the physical education of children and the organization of sports and athletic performance on programs of additional education of children.яOrganization and leisure of adult population.я&lt br&gt The operational management of Youth Sports School &quot Arena &quot  has a 2-storey building sports complex with total area of ??4,734.5 square meters, in which there are:&lt br&gt Universal sports gym 47x22 m. equipped with mobile telescopic columns, competitive shields, rings for basketball, racks, nets and a referee''s stand for volleyball.яAll competitive fields are placed in the center of the arena.яJudicial and information complex includes two electronic text boards and kits for judging the respective sports.Four dressing rooms at the arena equipped with check boxes for home wear and lounge chairs to rest and discuss during the game breaks. яIn the gym, the educational and training sessions and competitions of local, regional and nationwide levels in basketball, volleyball, mini-football are held.&lt br&gt 