INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2097, 'Крытый ледовый каток', 'Indoor artificial ice rink', true, 'Под крышей ледового комплекса разместилась современная хоккейная коробка с трибунами на 270 зрительских мест, тренажерным залом, двумя раздевалками, а также всеми необходимыми административно-хозяйственными помещениями. Ледовый каток предназначен для учебно-тренировочных занятий воспитанников местных СДЮШОРов и проведения соревнований городского и областного уровня. На базе объекта действуют спортивные секции. Здесь можно заниматься хоккеем, шорт-треком, фигурным катанием. Каток используется и для массовых катаний. Спортивный комплекс оснащен самым современным оборудованием: тренажёрами, многофункциональным табло, мощной аудиосистемой.', 'При проектировании и строительстве объекта применены типовые для данного вида спортивных сооружений технологические решения.', 'Under the roof of the ice complex there is a modern hockey box with stands for 270 seats, a gym, two dressing rooms, as well as all the necessary administrative and utility rooms. The ice rink is designed for training sessions of pupils of local Sports School and carrying out competitions of city and regional level. On the basis of facility are operating sports clubs. Here you can play hockey, short track, figure skating.&lt br&gt Skating rink and used for public skating. The sports complex is equipped with the most modern equipment, fitness equipment, multifunction displays, powerful audio system.', 'Engineering solutions standard for this kind of sports facilities were used in the design and construction of a facility. ', 'Сафоновский район', 'Смоленская область', NULL, 'г. Сафоново', 'Safonovo', 'Смоленская область, г. Сафоново', 'Safonovo, Safonovsky area, Smolensk region', 66641101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-01-17', '2010-12-31', 180400000, NULL, NULL, NULL, NULL, 'sport@admin.smolensk.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 33.24, 55.1068, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2062, 'ФОК «Заводской» с ледовой ареной', 'Sports and Fitness Centre «Zavodskoy» with ice arena', true, 'Физкультурно-оздоровительный комплекс «Заводской» с ледовой ареной — был построен в рамках федеральных программ «Развитие сети физкультурно-оздоровительных и спортивных сооружений в Саратовской области» за счет средств федерального и областного бюджетов. Это первая крытая площадка с искусственным льдом в Заводском районе. В состав комплекса вошли крытый каток с искусственным льдом, трибуны, фойе, раздевалки с местами для населения, тренерские и судейские комнаты, медицинский и методический кабинеты, другие подсобные помещения. На втором этаже здания есть хореографический класс, тренажерный зал, кафетерий, административные помещения, комнаты для персонала. Также к объекту подведены все коммуникации, построена котельная и трансформаторная подстанция. ', 'Были разработаны методы намораживания и структуирования ледового массива с задаваемыми физико-механическими свойствами для различных видов спорта: хоккея, фигурного катания и т.д. Подготовительный процесс начинается с охлаждения бетонной плиты. Причем очень важно, чтобы охлаждение шло постепенно, без скачкообразных изменений в температуре. Вторым важным этапом является подготовка воды. Она проходит специальную очистку - лишается минеральных примесей на 95 - 98&#37 . Не менее важным этапом в подготовке льда является техническая вентиляция и соблюдение режима влажности для поддержания необходимых параметров воздушной среды. Иначе лед попросту может поплыть. После подготовки плиты и воды начинается собственно заливка льда. Весь процесс заливки льда занимает около трех суток. Последние слои заливаются льдоуборочной машиной, с помощью которого специалисты также имеют возможность подновить лед в процессе соревнований. Машина срезает верхний слой и заливает лед. Таким образом, спортсмены получают в свое распоряжение высококачественный лед. Так же ФОК «Заводской» оборудован системой видеогол и электронным хоккейным табло. Для комментаторов также предусмотрена своя зона – радиоузел На втором этаже Ледовой Арены расположены залы хореографической и индивидуальной силовой подготовки. Здесь спортсмены готовят и отрабатывают элементы своих программ, а уже затем выходят на лед. Система так называемого замкнутого тренировочного цикла позволяет добиваться наилучших результатов ', 'Creation of conditions for carrying on of training process of sports schools, involving of population in active going in for sports.', 'Methods of freezing and ice structuring were developed with definable physical and mechanical properties for different sports: hockey, figure skating, etc.&lt br&gt &lt br&gt &lt br&gt &lt br&gt Preparatory process begins with the cooling of the concrete slab. Moreover, it is very important that the cooling proceeded gradually, without sudden changes in temperature.&lt br&gt &lt br&gt &lt br&gt The second important step is the preparation of water. It undergoes special treatment - deprived of mineral admixtures at 95 - 98%.&lt br&gt &lt br&gt &lt br&gt Equally important stage in the preparation of ice is a technical ventilation _ moisture adherence to maintain required air parameters. Otherwise, the ice simply may swim.&lt br&gt &lt br&gt &lt br&gt After preparation of the plate, the water starts filling the ice itself. The entire process of filling the ice takes about three days.&lt br&gt &lt br&gt Recent layers filled by ice machine with which experts are also able to refurbish an ice during the competition. The machine cuts the top layer and fills the ice.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt Thus, the athletes have at their disposal a high quality ice.&lt br&gt &lt br&gt &lt br&gt Also Sports and Fitness school &quot Zavodskoy&quot  is equipped with video and electronic goal hockey scoreboard. For commentators also there is their own zone - radio unit.&lt br&gt &lt br&gt &lt br&gt &lt br&gt On the second floor of the ice arena _ choreography and individual strength training gyms are located. Here athletes prepare and work out elements of their programs, and then to go on the ice. The system of so-called closed training cycle allows us to achieve the best results', 'Город Саратов', 'Саратовская область', NULL, 'г. Саратов', 'Saratov', 'Саратовская область, г. Саратов, ул. Ново-Астраханское шоссе (за стадионом Торпедо)', 'Novoastrakhanskaya str., Zavodskoy area, Saratov, Saratov region', 63701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-17', '2013-12-25', 178314000, '09:00-00:00', NULL, NULL, NULL, 'shkola-kristall@mail.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'фигурное катание, хоккей', 45.9238, 51.495, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2071, 'Стадион «Водник»', '«Vodnik» Stadiumthe', true, 'Ввод в эксплуатацию стадиона позволит увеличить численность подростков и молодежи систематически занимающихся физической культурой и спортом. Увеличить численность жителей Корсаковского городского округа систематически занимающегося физической культурой и спортом. Укрепить здоровье и продлить активное творческое долголетие населения городского округа. Увеличить численность людей с ограниченными возможностями здоровья и инвалидов, занимающихся физической культурой и спортом. Спортивная подготовка для всех категорий населения муниципального образования. Даты крупных соревнований и значимых событий: Чемпионат и первенство Сахалинской области по футболу. 06.09.2013 – торжественное открытие стадиона «Водник»  12.10.2013 – Спартакиада среди сельских общеобразовательных  учреждений Корсаковского городского округа  21-24.10.2013 – муниципальные этапы Всероссийских спортивных соревнований «Президентские спортивные игры» и «Президентские состязания»  24.12.2013 – 22.03.2014 – зимнее Первенство Корсаковского городского округа по мини-футболу  02-03.06.2014 – муниципальный этап турнира дворовых команд по футболу в рамках областного проекта «Спорт против подворотни» на приз «Кожаный мяч»  28.06.2014 – Всероссийский Олимпийский день  с июня 2014 по октябрь 2014 – Чемпионат Корсаковского городского округа по футболу на кубок мэра Корсаковского городского округа  с май 2014 по сентябрь 2014 Чемпионат и Первенство Сахалинской области по футболу. ', 'Информации о примененных новых