INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2314, 'Крытый детский спортивный каток с искусственным льдом', 'Indoor ice rink for children', true, 'Привлечение детей и подростков к систематическим занятиям физической культурой и спортом. Формирование здорового образа жизни, создание необходимых условий для активного отдыха детей и подростков и занятий физической культурой и спортом. В секции хоккея с шайбой  на безвозмездной основе осуществляют тренировочную деятельность 304 человека. Даты крупных соревнований и значимых событий: 1. Первенство Санкт-Петербурга по хоккею среди юношей 2001 г.р., 2002 г.р., 2003г.р. &#40 еженедельно по субботам и воскресеньям с 15.01.2014 г. по 30.03.2014г. и с 01.10.2014 г. по 31.12.2014 г.&#41 . 2.Кубок Федерация хоккея Санкт-Петербурга 2004 г.р., 2005 г.р. &#40 еженедельно по субботам и воскресеньям&#41 . 3. Кубок Санкт-Петербурга по хоккею среди команд 2001 г.р., 2002 г.р., 2003 г.р. &#40 еженедельно по субботам и воскресеньям с 05.04.2014 по 27.04.2014 г.&#41  4. IV детско-юношеский турнир по хоккею среди юношей 2002 г.р. на кубок Следственного Комитета РФ с 28.03.2014 г. по 30.04.2014 г. Ежегодный детско-юношеский турнир по хоккею памяти Олимпийского чемпиона Николая Дроздецкого - ноябрь 2014 г. 08.02.2015  Традиционный детско-юношеский турнир по хоккею памяти Заслуженного тренера Российской Федерации Владимира Александровича       Плуцинского 23-25.03.2015  V  открытый детско-юношеский турнир по хоккею на кубок Следственного комитета Российской Федерации Кубок Санкт-Петербурга по хоккею среди детей 1998-2004  гг.р. 10.05 2015