INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2363, 'Спортивный комплекс «Чароит» ', 'Sports Center Charoit ', true, NULL, 'Здание спорткомплекса двухэтажное. В плане «Т» образной формы. Спорткомплекс включает в себя спортивный зал и административно-бытовую часть. Спортивный зал размером 30.30 х 42.35 м. с трибуной на 500 мест. Высота этажа 3.3 м. высота спортивного зала 8.0 м. на 1-ом этаже расположены: вестибюль-фойе, гардероб, касса, пожарный пост, спортивный зал, снарядная, раздевальные, душевые, туалеты, сауна, администрация, помещение для персонала, кладовая уборочного инвентаря, узел ввода, венткамера, электрощитовая. На 2-ом этаже спорткомплекса расположены: борцовский зал, тренажерный зал, раздевальные, тренерские, душевые, туалеты, кабинет врача, методический кабинет, радиоузел и помещение для судей и прессы. Общая площадь 2458.57 м2. Спорткомплекс имеет стабильное электроснабжение, водоснабжение, теплоснабжение центральное от котельной ГУП ЖКХ, канализация в виде септика на 100 м3.  Спорткомплекс расположен рядом со стадионом с трибуной на 3000 мест, с беговой дорожкой на восемь дорожек из ригопола и стандартным футбольным полем. Авто и сельхо техники не имеется, кроме газонокосилки.  Спорткомплекс полностью оборудован мебелью и спортивным инвентарем для введения учебно-тренировочных занятий и проведения спортивно-массовых мероприятий.  Спорткомплекс каркасный. Сендвич панели. Стадион каркасный, обшит профлистом.', NULL, 'The sports center is a two-story T-shape building in plan. The sports center includes a sports hall and administrative part. The sports hall is 30.30 x 42.35 m in size with stands for 500 seats. Floor height is 3.3 m and sports hall height is 8.0 m. The 1st floor includes: entrance lobby, cloakroom, booking office, fire post, sports hall, sports equipment room, changing rooms, shower rooms, restrooms, sauna, administrative office, staff room, cleaning equipment storage room, input control center, ventilation chamber, and switch room. The second floor of the sports center includes:  wrestling room, weight room, changing rooms, coaching rooms, shower rooms, restrooms, doctor''s office, methodic department, radio station, and rooms for referees and media. The total area is ??2,458.57 m2. The sports center has stable electricity supply, water supply, and central heating supply from a boiler house of the State Unitary Enterprise Housing Maintenance and Utilities Board, as well as a sewer system in the form of a septic tank with capacity of 100 m3.The sports center is located next to a stadium with stands for 3,000 seats, running track for eight tracks with Regupol surface and standard football field. There is no automotive and agricultural equipment, except for a lawn mower.The sports center is fully equipped with furniture and sports equipment for holding training sessions and mass sports events.The sports center is framed. Sandwich panels. The stadium is framed and coated with corrugated steel sheet.', 'Олёкминский район', 'Республика Саха-Якутия', NULL, 'г. Олекминск', 'Olekminsk', 'Республика (Саха) Якутия, г. Олекминск, ул. Карла Миллера, 2/а', '2а Karl Miller str., Olekminsk, Republic of Sakha (Yakutia)', 98641453, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-01-01', '2010-08-15', 26000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, вольная борьба, дзюдо, лёгкая атлетика, самбо, фитнес-аэробика', 120.4686, 60.3726, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2377, 'Спортивный зал', 'Sports hall', true, 'Спортивный зал является собственностью общеобразовательной школы.  Проводятся все спортивные мероприятия школы и муниципального образования на уровне наслега, улуса, республики.  По инициативе спортивного инструктора наслега в будние дни работают секции по волейболу, баскетболу, легкой и тяжелой атлетике.', 'Сендвич панель, железная конструкция.', 'The sports hall is a property of the general education school.All sports events of the school and municipal education system on the level of a nasleg (agricultural community), ulus, and Republic are held here.On the initiative of the nasleg instructor volleyball, basketball, track-and-field athletics and weight-lifting sections function weekdays.', 'Sandwich panel, iron structure.', 'Усть-Алданский улус', 'Республика Саха-Якутия', NULL, 'с. Тулуна', 'Tuluna', 'Республика Саха (Якутия), с. Тулуна, ул. Аммосова, 4', '4 Ammosov str., Tuluna, Republic of Sakha (Yakutia)', 98652000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-05-01', '2009-12-15', 17500000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'баскетбол, волейбол, вольная борьба, лёгкая атлетика, спортивная гимнастика, теннис, футбол', 131.0848, 62.8053, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2382, 'Спортивный зал «Цементник»', 'Sports Hall «Tsementnik»', true, 'Спортивный зал «Цементник» расположен в центре рабочего поселка Мохсоголлох. Основная его задача - пропаганда и популяризация различных видов спорта, здорового образа жизни, привлечение широких слоев населения к систематическим занятиям физической культурой и спортом, а также укрепление дружеских связей среди близлежащих населенных пунктов.   В целях развития массового спорта среди молодежи и взрослого населения поселка в спортзале действуют различные секции. Расписание тренировок и занятий в секциях составлено с учетом интересов населения.    Ввиду переполненности местной школы и, соответственно, плотного графика занятий физкультурой в школьном спортзале, уроки физической культуры для учащихся Мохсоголлохской средней общеобразовательной школы частично проводятся в спортзале «Цементник».   В спортзале также проводятся ежегодные спартакиады среди предприятий, организаций и учреждений п. Мохсоголлох по различным видам спорта, другие спортивные мероприятия с привлечением детей начиная с дошкольного возраста и старше.   На территории спортзала имеется спортивная площадка с искусственным покрытием, на котором есть все необходимые разметки для игр в футбол, волейбол. В зимний период спортивная площадка заливается водой для массового катания на коньках.  •&nbsp &nbsp &nbsp &nbsp 5-6 декабря 2009г. V Спартакиада молодежи Хангаласского улуса •&nbsp &nbsp &nbsp &nbsp 2-3 октября 2010г. Республиканский турнир по мини-футболу на Кубок ФК «Цементник» •&nbsp &nbsp &nbsp &nbsp 17 октября 2010г. II Республиканский турнир по самбо среди юношей и девушек 1997 г.р. и младше. •&nbsp &nbsp &nbsp &nbsp 30-31 октября 2010г. Республиканский турнир по мини-футболу среди юношей 1995/96 гг. на Кубок ФК «Цементник» •&nbsp &nbsp &nbsp &nbsp 12 февраля 2011г. Мини-спартакиада между работниками ОАО ПО «Якутцемент» и депутатами Госсобрания «Ил Тумэн» •&nbsp &nbsp &nbsp &nbsp 26 февраля 2011 г. X Юбилейный традиционный турнир по дзюдо среди детей 2000г. и младше •&nbsp &nbsp &nbsp &nbsp 16-17 апреля 2011г. Республиканский турнир по мини-футболу на Кубок ФК «Цементник» среди мальчиков 2000-2001г.р.  •&nbsp &nbsp &nbsp &nbsp 17 мая 2011г. Республиканский турнир по мини-футболу на Кубок «Атланта» •&nbsp &nbsp &nbsp &nbsp 12 августа Республиканский турнир по мини-футболу, посвященный юбилею ОАО ПО «Якутцемент» •&nbsp &nbsp &nbsp &nbsp 29-30 октября 2011г. Республиканский турнир по мини-футболу на Кубок ФК «Цементник» •&nbsp &nbsp &nbsp &nbsp 13 апреля 2012г.Улусная Спартакиада допризывников общеобразовательных учреждений Хангаласского улуса •&nbsp &nbsp &nbsp &nbsp 14-15 апреля 2012г. Республиканский турнир по волейболу среди мужских команд на призы ООО УК «Востокцемент» •&nbsp &nbsp &nbsp &nbsp 19 мая 2012г. Республиканский турнир по мини-футболу на Кубок «Атланта»', NULL, '“Tsementnik” sports hall is situated in the centre of the industrial community of Mokhsogollokh. Its main task is promotion and popularization of different sports, healthy lifestyle, involvement of population in systematic physical exercising and going in for sports as well as strengthening of friendly relations among neighbouring populated localities. For the purpose of mass sports development among local youths and adult residents of the village different sections function in the sports hall. The time-table of training and classes of section has been developed with account of interests of population. Because of over-crowding of the local school and, consequently, the tight schedule of physical training classes in the school sports hall physical training classes for students of Mokhsogollokh secondary general education school are given partly in the “Tsementnik” sports hall. Besides, annual Spartakiad games among enterprises, organizations and establishments of the industrial community of Mokhsogollokh in different sports, other sports activities with involvement of children beginning from the preschool age and upwards are held in the sports hall. There is a sports ground with artificial turf with all necessary lines and marks for playing football and volleyball in the territory of the sports hall. In winter season the sports ground is flooded with water for mass skating.', NULL, 'Хангаласский улус', 'Республика Саха-Якутия', NULL, 'пос. Мохсоголлох', 'Mokhsogollokh', 'Республика Саха (Якутия), пос. Мохсоголлох, ул. Заводская, 11/а', '11/а Zavodskaya str., Mokhsogollokh, Republic of Sakha (Yakutia)', 98644157, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2009-12-04', 17500000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'баскетбол, волейбол, дзюдо, мини-футбол, хоккей', 128.9413, 61.3974, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2392, 'Республиканский спортивный комплекс', '«Yunost» Stadium', true, 'Предполагается создать условия для подготовки и передачи в команды мастеров и сборной России более 100 юных футболистов, из числа перспективных спортсменов республики, регионов Южного федерального округа. С введением в эксплуатацию футбольного поля с искусственным покрытием, город Элиста получит возможность проведения различных спортивных мероприятий регионального масштаба, создаст условия для организации учебно-тренировочных сборов футбольных команд. Организация в республике учебно-тренировочных сборов и соревнований Всероссийского и международного уровней, подготовка спортивного резерва спортивных сборных команд России к важнейшим стартам будущего, обеспечение реализации задач формирования здорового образа жизни и улучшение спортивной инфраструктуры на территории Республики Калмыкия.', 'Строительство велось с использованием типовых технологий.', 'It is assumed to create conditions for training and transferring to the team of masters and Russian national team more than 100 young football players from among promising sportsmen of the Republic, regions of the Southern Federal District. After putting of the football field with artificial turf into service the city of Elista will get a possibility to hold different sports activities of regional level, create conditions for organization of training camps of football teams.', NULL, 'г.о. город Элиста', 'Республика Калмыкия', NULL, 'г. Элиста', 'Elista', 'Республика Калмыкия, г. Элиста, мкр. 1', '1st microdistrict, Elista, Republic of Kalmykia', 85701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-09', '2015-12-09', 608462660, NULL, NULL, NULL, NULL, 'oksjs08@mail.ru', 'стадион', NULL, NULL, 'баскетбол, бокс, волейбол, мини-футбол, настольный теннис, плавание, стрельба из лука, футбол', 44.2575, 46.3165, 52);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2398, 'Дворец спорта', 'Sports palace', true, 'В 1973 году в г. Элиста был сдан в эксплуатацию Дворец Спорта с плавательным бассейном, являющийся основным спортивным сооружением, деятельность которого направлена на реализацию государственной политики, развитие массового спорта, улучшение здоровья населения республики. Это единственный объект спортивного назначения, имеющий плавательный бассейн и предназначенный для проведения соревнований республиканского и Всероссийского масштабов. С момента ввода данного объекта в строй капитальный ремонт здания не проводился. В связи с этим, в 2002 г. в рамках Федеральной адресной инвестиционной программы была начата реконструкция сооружения.', 'Строительство велось с использованием типовых технологий. ', 'In 1973 in the city of Elista the Palace of Sports with swimming pool which is the main sports facility was put into service. Its activity is directed to realization of the state policy, development of mass sports, improvement of health of the Republic population. It is the only object of sports designation that has a swimming pool and designed for competitions of republic and all-Russian levels. From the date of putting of this object into service the building failed to undergo a capital repair. Due to this fact in 2002 reconstruction of the facility was begun in the frames of Federal targeted investment programme.', NULL, 'г.о. город Элиста', 'Республика Калмыкия', NULL, 'г. Элиста', 'Elista', 'Республика Калмыкия, г. Элиста, ул. Буденного, 5', '5 Budenny str., Elista, Republic of Kalmykia', 85701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2002-01-01', '2013-08-26', 74567000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, мини-футбол, настольный теннис, плавание, художественная гимнастика', 44.2621, 46.3179, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2415, 'Крытый каток с искусственным льдом', 'Indoor artificial ice rink', true, 'Введение в эксплуатацию данного спортивного объекта дает региону: - возможность развития двух видов спорта – хоккей, фигурное катание &#40 работа секций, проведение соревнований регионального значения и с приглашением других регионов&#41 , - дополнительные площади для занятий другими видами спорта &#40 фитнесс, аэробика&#41 , - возможность проведения культурно-массовых мероприятий в закрытом помещении с одновременным скоплением 1500 зрителей. Даты крупных соревнований и значимых событий: 2012, 2013, 2014 гг. – чемпионаты области по хоккею с шайбой', 'Возведено согласно проекту.', 'Putting of this sports object into operation gives to the region:- a possibility of development of two sports, namely hockey and figure-skating (func tioning of sections, competitions of regional level and with invitation of other regions),- additional areas for other sports (fitness, aerobics),- a possibility to hold mass culture activities indoor with simultaneous presence of 1,500 spectators.', 'Built according to the project. ', 'Псковский район', 'Псковская область', NULL, 'дер. Борисовичи', 'Borisovichi', 'Псковская область, дер. Борисовичи', 'Borisovichi, Pskov area, Pskov region', 58649000, NULL, 'строительство', '2010-04-01', '2013-08-27', 274092000, '08:30-19:30', '09.00-21.00', '09.00-21.00', NULL, 'dss_pskov@mail.ru', 'арена ледовая', '(8112) 59-00-59', 'www.ledarenapskov.ru', 'фигурное катание, хоккей', 28.2706, 57.8215, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2461, 'Дворец спорта им. А. Алиева', 'Sports Palace named after A. Aliev', true, 'Дворец спорта предназначен для проведения спортивных мероприятий соответствующих современным требованиям и нормам, физкультурно-оздоровительных, культурно-зрелищных и общественных мероприятий, пропаганды здорового образа жизни.  Комплекс рассчитан на 5 тысяч зрителей, соответствует самым высоким требованиям международных спортивных федераций позволяет проводить спортивные соревнования международного уровня по всем видам единоборств, а также по отдельным игровым видам спорта. Даты крупных соревнований и значимых событий: Международный турнир по в/борьбе памяти А.Алиева &#40 ежегодно&#41  Международный турнир по боксу памяти М-С. И. Умаханова &#40 ежегодно&#41  ', 'Фасад здания  облицован современным материалом – композитные панели с утеплителем. Спортивный зал оснащен дополнительными трансформируемыми трибунами, что позволяет увеличить количество посадочных мест при проведении спортивных мероприятий. При Дворце спорта предусмотрена автономная котельная для обеспечения отопления и горячей водой независимо от централизованных сетей. Для кондиционирования воздуха установлены два чиллера.', 'The Palace of Sports is designed for holding of sports activities complying with modern requirements and standards, health and fitness, cultural and entertaining public events, health lifestyle promotion.  The complex is design for 5 thousand visitors. It complies with the highest requirements of international sports federations and enables to hold sports competitions of international level in all combat sports as well in certain playing sports.', 'The building facade is faced with modern materials - composite panels with insulation. A sports hall is equipped with additional transformable stands, which allows increasing the number of seats when holding sporting events. At the Sports Palace there is an independent boiler house to provide heating and hot water independently of centralized networks. Two chillers are installed for air conditioning.', 'г.о. город Каспийск', 'Республика Дагестан', NULL, 'г. Каспийск', 'Kaspiysk', 'Республика Дагестан, г. Каспийск, мкр. Центральный', 'The Republic of Dagestan, Kaspiysk city, neighborhood &quot Central&quot ', 82720000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-12-01', '2010-05-20', 1119174000, NULL, NULL, NULL, NULL, '676392@mail.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, вольная борьба, дзюдо, игровые виды спорта (разное), мини-футбол, самбо, спортивная борьба', 47.6367, 42.8916, 2);
