INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2503, 'Детский стадион', 'Children’s stadium', true, 'Созданный современный спортивный комплекс для детей позволит качественно улучшить процесс подготовки спортсменов и даст возможность проводить спортивные соревнования, как республиканского, так и межрегионального значения. Детский стадион необходим для развития и активации спортивной деятельности среди детей в г. Нальчике и республики в целом. - строительство проектируемой северо-восточной трибуны на 1500 мест с подтрибунными помещениями, - физкультурно-оздоровительный корпус &#40 ФОК-2&#41 , здание выполняется в виде двухэтажного единого блока с антресольным этажом, по т.п. 291-8-17 с.87, ал.I, ч.1. На территории детского стадиона работают следующие государственные казенные учреждения: -«СДЮСШ по настольному теннису и теннису»  -«ДЮСШ по легкой атлетике, спортивной гимнастике и плаванию»  -«ДЮСШ по футболу им. С.Х. Нирова». Увеличился интерес населения дошкольного и школьного возрастов к спортивной гимнастике, молодые команды по спортивной гимнастике выезжают на междугородние соревнования. Тренером по спортивной гимнастике выступает бронзовый призер Олимпийских Игр в Сиднее Продунова Елена Сергеевна. Футболисты, подготовленные и тренирующиеся в ГКУ «Детский стадион» выиграли Кубок Содружества в составе сборной России КБР.', 'В соответствии с проектом на территории спортивного комплекса проведены: - реконструкция юго-западной трибуны на 1500 мест, - строительство проектируемой северо-восточной трибуны на 1500 мест с подтрибунными помещениями, - физкультурно-оздоровительный корпус &#40 ФОК-2&#41 , здание выполняется в виде двухэтажного единого блока с антресольным этажом, по т.п. 291-8-17с.87, ал.I, ч.1.  Общая площадь территории составляет 30031,0 м2, в том числе: - площадь под зданиями и навесами 4010,0 м2, - площадь футбольного поля 7586,0 м2, - площадь под покрытиями 13197,0 м2, - площадь газона 5020,0 м2, - прочие 218,0 м2.  площадь спортивной составляющей: - здание теннисного зала – 910,0 м&#178    - футбольное поле &#40 мини&#41  – 999,0 м&#178    - футбольное поле &#40 мини&#41  – 998,0 м&#178    - футбольное поле &#40 стандартное&#41  –7425,7 м&#178    - баскетбольная площадка – 512,0 м&#178    - беговая дорожка 3822,0 м&#178    - теннисный корт – 659,9 м&#178    - теннисный корт – 649,0 м&#178    - физкультурно-оздоровительный комплекс – 1498,5 м&#178    - трибуны – 666,0 м&#178    - устройство освещения &#40 прожектор&#41  – 1,2 м&#178    - устройство освещения &#40 прожектор&#41  – 1,5 м&#178    - устройство освещения &#40 прожектор&#41  – 1,3 м&#178    - устройство освещения &#40 прожектор&#41  – 1,4 м&#178    - электронное табло – 4,7 м&#178   ИТОГО – 18150,2 м&#178 ', 'The modern sports complex for children that was constructed will enable to improve qualitatively the process of training of sportsmen, give a possibility to hold competitions both republic and inter-regional level. The children stadium is required for development and activation of sports activities among children in the city of Nalchik and in the Republic in the whole.- construction of projected north-eastern stand with 1,500 places and premises under the stand,- sports and recreation building (FOK-2), the building will be constructed as a two-storey integrated block with an attic storey in accordance with the standard design 291-8-17 p. 87, al. I, part 1.', 'According to the project the following works were performed in the territory of the sports center:- Renovation of the south-west stands for 1,500 seats - Construction of the planned north-east stands for 1,500 seats with rooms under the stands - Physical training and recreation building (PTRC-2), the building is constructed as a two-story single block with an attic floor according to the standard project 291-8-17 p.87, al. I, part 1.Total area of the territory is 30,031.0 m2, including:- Area under buildings and sheds is 4,010.0 m2 - Area of ??a football field is 7,586.0 m2 - Area under surfaces is 13,197.0 m2 - Lawn area is 5,020.0 m2 - Other 218.0 m2.', 'г.о. Нальчик', 'Республика Кабардино-Балкария ', NULL, 'г. Нальчик', 'Nalchik', 'Республика Кабардино-Балкария, г. Нальчик, ул. Пачева, 57', '57 Pachev str., Nalchik', 83701000, NULL, 'строительство', '2007-01-15', '2012-08-29', 182766000, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'баскетбол, волейбол, лёгкая атлетика, мини-футбол, настольный теннис, футбол', 43.6047, 43.4881, 2);
