INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (20449, 'Спортивно-оздоровительный комплекс «Кама»', 'Sports complex «Kama»', true, 'Спортивно-оздоровительный комплекс построен и введён в эксплуатацию в конце 2011 года. В целях создания условий для систематических занятий физической культурой и спортом населения города и района. Здесь проводится работа по двум направлениям: Учебно-тренировочная деятельность. Спортивно-оздоровительная работа с населением. Спортивно-оздоровительный комплекс располагает следующей материально-технической базой. Спортивный зал аэробики. Зал бокса. Тренажерный зал. Плавательный бассейн &#40 большая и малая чаша&#41 . Шахматный клуб. Для организации качественной работы коллектив спортивно-оздоровительного комплекса выполняет мероприятия по улучшению материально-технической базы учреждения, повышению уровня тренерско-преподавательского состава, привлечению большего количества населения к систематическим занятиям физической культурой и спортом, расширению объёма востребованных платных услуг. Действует гибкая система льготного посещения для социально незащищённых слоёв населения. Ежегодно проводятся шахматные турниры среди организаций и учреждений города на призы газеты &quot Камская новь&quot  и призы ООО &quot Атлантида&quot , спартакиада работников образования по настольному теннису, спартакиада пенсионеров, соревнования по плаванию. В комплексе работают оздоровительные группы: &quot Здоровья&quot , &quot Фитнес&quot  и группы общей физической подготовки, с числом занимающихся около 95 человек. Тренажёрный зал ежедневно посещают в среднем 45 человек. С начала 2014 года оздоровительными услугами воспользовались следующие организации: РОО, РУЭС, РАПО, РОВД и др. Серьёзное внимание уделяется спортивно-оздоровительной работе с детьми и подростками. Спортивно-оздоровительный комплекс проводит организационную работу по обеспечению доступности занятий физической культурой и спортом для разных категорий граждан,в том числе по разработке и применению механизма льготного и бесплатного посещения малообеспеченных категорий граждан. Так в отчётном году в рамках проведения Дня пожилого человека, Декады людей с ограниченными возможностями проводились благотворительные акции - День открытых дверей, когда все желающие посетители этой категории могли пользоваться бесплатными услугами , которые предоставляет спортивно-оздоровительный комплекс. В течении года лицам пенсионного возраста действует 30&#37  льготная скидка на посещение плавательного бассейна. Воспитанники детского дома и интерната, слабовидящие дети, дети из многодетных семей посещают бассейн на бесплатной основе. Каждый ребёнок, активно участвующий в культурной и спортивной жизни района, получил годовой абонемент в плавательный бассейн. В результате платной деятельности учреждения в 2013 года - заработано 334 тыс.руб. За счёт внебюджетных средств в текущем году были затрачены денежные средства на укрепление материально-технической базы. Даты крупных соревнований и значимых событий: 09.01.2014 - Шашечный турнир среди учащихся. 21.02.2014 - Открытое соревнование по плаванию среди учащихся спортивных школ республики. 15.03.2014 - Шахматный турнир среди организаций и учреждений г. Лаишево на призы газеты &quot Камская новь&quot . 25.04.2014 - Районная спартакиада пенсионеров &quot Третий возраст&quot . 30.05.2014 - День Нептуна, посвящённый Международному дню защиты детей.  ', 'Установлена система рекуперации тепла &quot МОНТЭЙР&quot ', NULL, NULL, 'Лаишевский район', 'Республика Татарстан', NULL, 'г. Лаишево', 'Laishevo', 'Республика Татарстан, г. Лаишево, ул. Первомайская, 39/а', 'The Republic of Tatarstan, the city Laishevo Street Day, Building 39 and', 92634101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-11-01', '2011-11-25', 40000000, NULL, NULL, NULL, NULL, 'Andrey.Karsalov@tatar.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'бокс, каратэ, настольный теннис, плавание, тяжёлая атлетика, шахматы', 49.5454, 55.4031, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2764, 'Многофункциональный зал и крытая хоккейная коробка', 'Multi-purpose sports hall', true, 'Проведение спортивных соревнований регионального значения, оздоровительная работа с населением,  работа со школами, дворовыми командами, ветеранами. Даты крупных соревнований и значимых событий: Зональные соревнования спартакиады школьников по баскетболу и волейболу. Влияние объекта на социальные показатели региона: рост показателя «Доля населения, систематически занимающегося физической культурой и спортом» в Знаменском муниципальном районе с 17,1 &#37   в 2007 году до 28,02 &#37  в 2013 году.', 'Строительство велось с использованием типовых технологий. Общая площадь здания/сооружения - 914,8 м2. Площадь спортивной составляющей - 702 м2. Максимальная пропускная способность - 45 человек/смену. ', 'Sporting events of regional importance, health improvement outreach, work with schools, amateur teams, veterans. &lt br&gt Dates of major competitions and significant events: &lt br&gt Zonal Games of schoolboys competitions in ice hockey in basketball and volleyball.', 'During the construction standard technological solutions were used.', 'Знаменский район', 'Омская область', NULL, 'с. Знаменское', 'Znamenskoe', 'Омская область, с. Знаменское, ул. Первомайская, 11', NULL, 52612404, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-16', '2007-12-28', 23799400, NULL, NULL, NULL, NULL, 'golubovichv@mail.ru', 'зал спортивный', NULL, NULL, 'баскетбол, волейбол', 73.8313, 57.1256, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2769, 'Плавательный бассейн с крытыми ваннами', 'Swimming pool with indoor baths', true, 'Формирование здорового образа жизни, привлечение население города и Республики Алтай к систематическим занятиям физической культурой и спортом  Укрепление здоровья и всестороннее физическое развитие &#40 развитие физических, интеллектуальных и нравственных способностей&#41   Достижение спортивно-оздоровительных успехов в соответствии с индивидуальными способностями людей. К спортивно-оздоровительной подготовке &#40 СОП&#41 .допускаются все лица, желающие заниматься спортом и не имеющие медицинских противопоказаний &#40 имеющие письменное разрешение врача&#41 . В группах СОП осуществляется физкультурно-оздоровительная и воспитательная работа, направленная на разностороннюю физическую подготовку преимущественно оздоровительной направленности и овладение основами техники плавания. Основные задачи подготовки: •    Укрепление здоровья и закаливание  •    Устранение недостатков физического развития  •    Овладение жизненно необходимым навыком плавания. На основании поставленных целей и задач: была произведена  работа по открытию набора в спортивно-оздоровительные группы по плаванию среди взрослого населения ,детей школьного, дошкольного и ясельного возраста .В дальнейшем планируется открытие группы грудничкового плавания и групп подводного дайвинга. Так же не первый год действуют группы по аквааэробики для женщин.  Для улучшения эффекта работы действует зал силовой направленности и зал для сухого плавания. В летний период бассейн требуется спросом для приезжих спортсменов  из других крупных городов для сборов. Такие города как Абакан, Новосибирск, Барнаул, уже стали постоянными гостями для нашего бассейна. На госучреждения и частные предприятия отправлены письма с привлечением к систематическим занятиям физической культурой и спортом, к получению заряда энергии, бодрости что приводит к повышению работоспособности и выполнению, поставленных перед ними более высоких целей и задач. Такие организации как: ГазПром, Сбербанк, компания МТС, Электро-профсоюз, компания ГТРК и мн.другие уже активно ведут здоровый образ жизни с помощью плавания. За 2014 год значительно увеличился поток посетителей плавательного бассейна и желающих научиться плавать, оздоровиться,  что требует все большей занятости спортивного сооружения.   На базе Плавательного бассейна активно начали вести свою работу такие организации как:  -ДЮСШ  адаптационные группы здоровья &#40 для людей с ограниченными возможностями&#41    -Федерация спортивного плавания  - Федерация спортивного и водного поло   -Федерация подводного плавания в ластах.  На данный момент существует в общей сложности 78 секций по плаванию. Разнообразие видов плавания дает населению огромный выбор и возможность реализации в спорте. Все это отличный старт для развития плавания – как вида спорта в Республике Алтай.', 'Строительство объекта велось с использованием типовых технологий.', 'The object is designed for teaching swimming, health activities and trainings.', NULL, 'г.о. Горно-Алтайск', 'Республика Алтай', NULL, 'г. Горно-Алтайск', 'Gorno-Altaysk', 'Республика Алтай, г. Горно-Алтайск, ул. Ленкина, 3', '3 Lenkin str., Gorno-Altaisk, Republic of Altai', 84701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-15', '2008-08-05', 188517000, NULL, NULL, NULL, NULL, 'sportkom-04@mail.ru', 'бассейн', NULL, NULL, 'аквааэробика, общая физическая подготовка, плавание, подводный спорт', 85.9719, 51.9599, 55);
