INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2936, 'СДЮСШОР', 'Artificial turf football field ', true, 'В связи с  недостаточным обеспечением населения плоскостными сооружениями в г. Прохладный строительство футбольного поля решило проблему доступа учащихся и молодежи к регулярным занятиям по футболу, организации спортивного досуга, как для жителей города, так и других близ лежащих населенных пунктов г. о. Прохладный, Кабардино-Балкарской Республики.', NULL, 'Because of insufficient provision of population with plane facilities in the city of Prokhladny construction of a football field solved a problem of access of pupils and young people to regular football activities, organization of sports leisure time for residents both of the city and other neighbouring populated localities of Prokhladny municipal community and the Kabardino-Balkarian Republic.', NULL, 'Прохладненский район', 'Республика Кабардино-Балкария ', NULL, 'г. Прохладный', 'Prokhladny', 'Республика Кабардино-Балкария, г. Прохладный, ул. Гагарина, 2', '2 Gagarin str., Prokhladny', 83710000, NULL, 'строительство', NULL, '2010-08-31', 18950000, NULL, NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 44.0122, 43.7542, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2945, 'Спортивный комплекс', 'Sports center ', true, 'Спортивный комплекс универсальный предназначен для проведения учебно-тренировочного процесса и соревнований по основным видам спорта &#40 волейбол, баскетбол, мини-футбол, теннис и спортивная гимнастика&#41 , плаванию, синхронному плаванию  районного, республиканского и регионального, всероссийского уровня, а также для предоставления физкультурно-оздоровительных услуг населению.  В связи с отсутствием большого спортивного сооружения в г. Нарткала строительство спорткомплекса решит проблему доступа учащихся и молодежи к регулярным занятиям физической культурой и спортом, обеспечит возможность проведения учебно-тренировочного процесса на высоком профессиональном уровне для спортсменов, проживающих в г. Нарткала и Урванском муниципальном районе Кабардино-Балкарской Республики, а так же проведение спортивных мероприятий  районного, регионального, всероссийского и международного уровня.', 'В объемно-планировочном отношении здание спорткомплекса представляет собой сложный в плане объем, с разной этажностью &#40 1, 2 этажа&#41  и разной высотой этажа &#40 3 000 мм, 3 900 мм от пола до потолка&#41 , с выступающими объемами и лоджиями, с подвалом в осях 6-17, П-У. Размеры здания в осях 74,10 х 59.50 м. Высота подвала 3м, объем здания – 34 123,7 м3, общая площадь здания 5 867,1 м2. Спортивный зал размером 36,18м с блокировкой подсобных помещений, бассейн 8,5х25 м.', 'Multi-purpose sports complex is designed for carrying on of educational and training process and competitions in the main sports (volleyball, basketball, mini-football, tennis and artistic gymnastics), swimming, synchronous swimming of district, republic, regional and all-Russian levels as well as for rendering of sports and recreational services for population.Because of non-availability of a large sports facility in the city of Nartkala construction of a sports complex will solve a problem of access of pupils and young people to regular physical trainings and sports activities, provide a possibility of educational and training process on a high professional level for sportsmen resided in the city of Nartkala and Urvansk municipal district of the Kabardino-Balkarian Republic as well as holding of sports activities of district, regional, all-Russian and international level.', 'According to the space and layout design a building of the sports center has a complex volume in plan, with different number of floors (1-2 floors) and different floor height (3,000 mm, 3,900 mm from floor to ceiling), projecting volumes and loggias, and basement 6-17 along the axes, P-U. The building size is 74.10 x 59.50 m along the axes. The basement height is 3m, building volume – 34,123.7 m3, total building area – ??5,867.1 m2. Sports hall with dimensions of 36.18 m with lockable utility rooms, swimming pool - 8.5 x 25 m. ', 'Урванский район', 'Республика Кабардино-Балкария ', NULL, 'г. Нарткала', 'Nartkala', 'Республика Кабардино-Балкария, г. Нарткала, ул. Кабардинская', 'Kabardinskaya str., Nartkala', 83640101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-01-15', '2015-12-30', 1680800000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, мини-футбол, плавание, спортивная гимнастика, теннис', 43.8525, 43.5545, 2);
