INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2985, 'Физкультурно-оздоровительный комплекс', 'Physical training and recreation center', true, 'В связи с отсутствием спортивных объектов в с.п. Урух строительство ФОК решило проблему доступа учащихся и молодежи к регулярным занятиям физической культурой и спортом, возможности проведения учебно-тренировочного процесса на высоком профессиональном уровне для спортсменов, проживающих как в указанном сельском поселении, так и других близ лежащих населенных пунктах Лескенского района, Кабардино-Балкарской Республики. Все занятия с детьми в учреждении проводятся бесплатно. Спорткомплекс в районе закладывает отличный потенциал в молодые таланты спортсменов для их будущего развития и  выступления на более высоких по уровню соревнованиях.  Результаты спортивных достижений: 1.    01-02.12.2012 г. – матчевая встреча сборных команд РСО-Алания – КБР 2.    08-10.02.2013 г. – первенство Лескенского района 3.    17-19 мая 2013 г. – Первый открытый региональный турнир по греко-римской борьбе среди юношей 1996-1997 г.р. 4.    19-21 июля 2013 г. –Первый открытый республиканский турнир по волейболу памяти МС СССР Эльджарова А.Б. 5.    04-06 октября 2013 г.  - Первый открытый республиканский турнир по греко-римской борьбе среди юношей 1993-1994 г.р. 6.    22-22 декабря 2013 г. – Республиканский новогодний турнир среди юношей «НАШИ НАДЕЖДЫ» 7.    14-16 февраля 2014 г. – открытое первенство ДЮСШ среди юношей 8.    23-25 мая 2014 г. - Второй открытый региональный турнир по греко-римской борьбе среди юношей 1997-1998 г.р. 9.    15-17 августа 2014 г. - открытый республиканский турнир по волейболу памяти МС СССР Эльджарова А.Б. 10.    26-28 декабря 2014 г. -  республиканский турнир памяти Тлехугова М.М. 11.    07-09 января 2015 г. - Третий открытый региональный турнир по греко-римской борьбе среди юношей 1998-1999 г.р.', 'Одноэтажное здание общей площадью 925,1 м. кв. Фундамент ж/бетон, стены кирпич, кровля шатровая, покрытие металлочерепица. общая площадь здания/сооружения: одноэтажное здание площадью 925,1 м&#178  площадь спортивной составляющей: спортивный зал – 540 м&#178   максимальная пропускная способность - 80 чел./см. ', 'Because of non-availability of sports objects in the rural settlement of Urukh construction of a sports and recreation complex solved a problem of access of pupils and young people to regular physical trainings and sport activities, possibility of educational and training process on a high professional level for sportsmen resided both in this rural settlement and other neighbouring populated localities of Leskensky district of the Kabardino-Balkarian Republic.', 'A one-storey building with a total area of 925.1 m2.Foundation - reinforced concrete, walls – bricks, hip roof with metal tile surface. ', 'Лескенский район', 'Республика Кабардино-Балкария ', NULL, 'с. Урух', 'Urukh', 'Республика Кабардино-Балкария, с. Урух, ул. Сундукова, 18', 'Zavodskya str., Urukh, Republic of Kabardino-Balkaria', 83618450, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-01-15', '2012-08-17', 29000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, вольная борьба, дзюдо, лёгкая атлетика', 44.0275, 43.2944, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (2996, 'ФОК «Псыгансу»', 'Physical Training and Recreation Center «Psygansu»', true, 'Строительство ФОК способствовало увеличению обеспеченности населения Кабардино-Балкарской Республики спортивными сооружениями, привлечению населения Кабардино-Балкарской республики к регулярным занятиям физической культурой и спортом, возможности проведения учебно-тренировочного процесса на высоком профессиональном уровне для спортсменов, проживающих в с.п. Псыгансу и других близ лежащих населенных пунктов Урванского района, Кабардино-Балкарской Республики. Все занятия с детьми в учреждении проводятся бесплатно.', 'Одноэтажное здание общей площадью 997,5 м.кв. Спортивный зал размером 30&#215 18 м. с блокировкой подсобных помещений размером 4,8&#215 25.0 м, - футбольное поле 75&#215 50 м с пристроенными трибунами на 500 мест и беговой дорожкой 100&#215 5 м. Открытая волейбольная и баскетбольная стационарные площадки.  Фундамент ж/бетон, стены кирпич, ж/б колоны и балки. Стальные межколонные связи. Мягкая кровля.  Трибуна на 500 мест. максимальная пропускная способность - 80 чел./см.', 'Construction of the Sports and Recreation Complex facilitated provision of the Kabardino-Balkarian Republic population with sports facilities, involvement of the Kabardino-Balkarian Republic population in regular sports activities, possibility of educational and training process on a high professional level for sportsmen resided in the rural settlement of Psygansu and other neighbouring populated localities of Urvansky district of the Kabardino-Balkarian Republic.', 'A one-storey building with a total area of 997.5 m2.Sports hall with dimensions of 30 x 18 m with lockable utility rooms with dimensions of 4.8 x 25.0 m, football field 75 x 50 m with added stands for 500 seats and running track 100 x 5 m.Outdoor volleyball and basketball stationary courts.Foundation - reinforced concrete, walls – bricks, columns and beams - reinforced concrete.Steel columns ties. Rolled roofing material.', 'Урванский район', 'Республика Кабардино-Балкария ', NULL, 'с. Псыгансу', 'Psygansu', 'Республика Кабардино-Балкария, с. Псыгансу, ул. Ленина, 180', '180 Lenin str., Psygansu', 83640445, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2005-01-01', '2007-01-01', 24000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, вольная борьба, дзюдо, кикбоксинг, лёгкая атлетика, футбол', 43.7907, 43.4115, 2);
