INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3165, 'Физкультурно-спортивный комплекс «Улап» на территории  ЯСОШ', 'Physical Training and Recreation Center «Ulap»', true, 'Объект представляет собой двухэтажное каркасно-панельное здание. Имеются универсальный спортивный зал, два тренажерных зала, малый борцовый зал, плавательный бассейн, зал для игры в настольный теннис, сауна.  Значимость - создание условий для укрепления здоровья населения путем развития инфраструктуры спорта, популяризации  массового и профессионального спорта &#40 включая спорт высших достижений&#41  и приобщения различных слоев общества к регулярным занятиям физической культурой и спортом. Расположение ФОК на территории ЯСОШ, который соединен с ней теплым переходом на уровне второго этажа, дает возможность охватить систематическими занятиями физической культурой и спортом детей и молодежи.', 'Разработка программы по строительству шло совместно с Нижегородским институтом. Использование новых технологий, которые применяются при строительстве таких объектов, позволило в кратчайшие сроки решить проблему строительства. Фундамент из железобетонных монолитных столбчатых блоков. Стеновое ограждение представляет собой металлокаркас, обшитый теплоизоляционными пенополиуретановыми сэндвич-панелями. Температурные условия эксплуатации: оптимальная температура внутри &#43 20&#186 С, температура внешней среды от -50&#186 С до &#43 40&#186 С.', 'The object represents a two-storey frame-panel building. There is a multi-purpose sports hall, two fitness rooms, small wrestling hall, swimming pool, hall for table tennis and sauna here.Importance: creation of conditions for strengthening of health of population by the way of development of sports infrastructure, popularization of mass and professional sports (including high performance sports) and involvement of different community groups in physical trainings and going in for sports on a regular basis. Location of the sports and recreation complex on the territory of Yalchiki secondary general education school joined to it by a warm passage on the level of the second storey gives a possibility to involve children and young people in physical trainings and going in for sports on a regular basis.', 'Development of a construction program was performed together with the Nizhniy Novgorod Institute. The use of new technologies that are used in the construction of such facilities allowed solving the problem of construction in the shortest possible time. A foundation is made of solid-cast reinforced concrete columnar blocks. A wall cladding is a metal frame coated with foam polyurethane insulation sandwich panels. Temperature operating environment: optimum temperature inside +20°C, ambient temperature -50°C to +40°C.', 'Яльчикский район', 'Республика Чувашия', NULL, 'с. Яльчики', 'Yalchiki', 'Республика Чувашия, с. Яльчики, ул. Юбилейная, 6/а', '6 «А» Yubileynaya str., Yalchiki, Yalchiksky area, Republic of Chuvashia', 97655440, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-10-31', '2008-06-27', 34700000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, вольная борьба, городошный спорт (городки), греко-римская борьба, дартс, дзюдо, лёгкая атлетика, мини-футбол, плавание, пляжный волейбол, самбо, стендовая стрельба, стритбол, танцевальный спорт, теннис, тяжёлая атлетика, футбол, хоккей, хоккей с мячом', 48.0095, 55.1611, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3185, 'Физкультурно-спортивный центр «Сывлах»', 'Sports and Physical Training Center «Syvlakh»', true, 'Описание объекта с точки зрения его социальной значимости: - осуществление спортивно-оздоровительной, воспитательной работы среди детей и подростков, направленной на укрепление их здоровья  - улучшение состояния здоровья, включая физическое развитие  - формирование морально-этических и волевых качеств  - пропаганда здорового образа жизни средствами физической культуры и спорта  - привлечение максимально возможного числа детей и подростков к систематическим занятиям спортом с целью снижения уровня преступности в Чувашской Республике  - обеспечение подготовки спортсменов высшего спортивного мастерства и спортивных сборных команд Чувашской Республики. ', NULL, 'Description of the facility in terms of its social significance:&lt br&gt - Implementation of sports and recreational, educational work among children and adolescents, aimed at strengthening their health,&lt br&gt &lt br&gt - Improvement of health, including physical development,&lt br&gt - The formation of ethical and volitional qualities,&lt br&gt &lt br&gt - promoting a healthy lifestyle by means of physical culture and sports,&lt br&gt - Attracting the greatest possible number of children and adolescents to systematic sports activities with the aim of reducing crime in the Chuvash Republic,&lt br&gt - Provision of training athletes high sports and sports national teams of the Chuvash Republic.', NULL, 'Моргаушский район', 'Республика Чувашия', NULL, 'с. Моргауши', 'Morgaushi', 'Республика Чувашия, с. Моргауши, ул. Мира, 8', 'No address', 97632435, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-02-05', '2008-11-27', 40000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, вольная борьба, настольный теннис, пауэрлифтинг, плавание, хоккей', 47.4892, 55.5166, 2);
