INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3314, 'Ледовый дворец', 'Ice sports palace for 3,000 seats', true, 'Обеспечивает гражданам Республики Марий Эл возможность заниматься физической культурой и спортом  Способствует формированию у населения устойчивого интереса к регулярным занятиям физической культурой и спортом, здоровому образу жизни. Способствует созданию эффективной системы подготовки спортивного резерва и условий для развития спорта высших достижений. Даты крупных и значимых соревнований и значимых событий: 17.01.2014-19.01.2014 - Чемпионат и первенство России по синхронному катанию на коньках. 20.02.2014-23.02.2014 - первенство России по шорт-треку среди юношей и девушек детского и младшего возраста. 04.20.14-04.2014 - Всероссийские соревнования по хоккею с шайбой среди сельских команд и команд малых городов. 05.2014-05.2014 - Всероссийские соревнования Клуба Золотая шайба им. А.В. Тарасова среди команд юношей 1997 - 1999 г.р., тренирующихся на открытом льду. 16.01.2015 – 18.01.2015 – Чемпионат и первенство России по синхронному катанию на коньках  23.01.2015 – 27.01.2015 – VII Спартакиада по шорт-треку среди учащихся России II Этап ПФО средний и старший возраст  4.02.15-7.02.15 – Первенство России по фигурному катанию среди юниоров 12.02.15-15.02.15 – Всероссийские соревнования среди юношей и девушек младшей возрастных групп по шорт-треку в многоборье. 21.04.15-29.04.15 – Финальные Всероссийские соревнования по хоккею среди команд допризывного возраста 1998-1999 г.р. ', '1.Архитектура фасадов – современные строительные и отделочные материалы – панели типа «Сэндвич», навесной вентилируемый фасад системы «ДЕКОТ XXI» и «ТАЛДОМ – SPIDI» из газосиликатных стеновых блоков. Полы выполнены из покрытия SportsFlooringcep.Taratlex 2. Система кондиционирования помещений на базе центральных кондиционеров К1 и К2 фирмы VORK. К1 работает  в 3-х режимах: тренировок, соревнований, зрелищных мероприятий. Воздух подается в верхнюю зону через воздухораспределители фирмы «Trox» марки DUK-V-315 с электроприводом. К2 обслуживает зрительную зону, воздух подается в верхнюю зону через диффузоры серии ТСА-400.', 'Provides citizens of the Republic of Mari El with opportunity to engage in physical culture and sports,&lt br&gt &lt br&gt Contributes to a sustainableяinterest among the population in regular physical training and sports, healthy lifestyle,&lt br&gt Contributes to the creation of an effective system of training of sports reserve and conditions for the development of high performance sport,', '1. Facade architecture - modern construction and finishing materials – sandwich panels, suspended ventilated facades &quot DEKOT XXI&quot  and &quot TALDOM - SPIDI&quot  made of gas and silicate building blocks.The floors are made of the SportsFlooringcep.Taratlex surface.2. A room air conditioning system is based on air handling units K1 and K2 supplied by VORK. K1 operates in three modes: practices, competitions, and entertainment events. Air is supplied to the upper area through Trox DUK-V-315 air diffusers with electric drive. K2 operates in the viewing area, and air is supplied to the upper area through TSA-400 air diffusers.', 'Город Йошкар-Ола', 'Республика Марий Эл', NULL, 'г. Йошкар-Ола', 'Yoshkar-Ola', 'Республика Марий Эл, г. Йошкар-Ола, ул. Карла-Маркса, 105/а', '105/а Karl Marx str., Yoshkar-Ola, Republic of Mariy El', 88701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2005-01-17', '2006-03-21', 356443000, '08:00-20:00', NULL, NULL, NULL, 'tehnadzor.minsportrme@mail.ru', 'арена ледовая', NULL, 'http://yccrme.ucoz.ru', 'кёрлинг, фигурное катание, хоккей, художественная гимнастика', 47.8949, 56.6243, 59);
