INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3464, 'Крытый каток с искусственным льдом «Олимпиец»', 'Indoor artificial ice rink', true, 'В настоящее время на территории г. Ижевска с населением 600 тыс. человек функционирует единственный имеющийся ледовый дворец «Ижсталь» построенный в 1971 году и требующий капитального ремонта.  Основную массу рабочего времени дворца занимает профессиональная команда «Ижсталь» для тренировок, которая играет в высшей хоккейной лиге страны, что не позволяет в достаточном объеме обеспечить льдом  потребность жителей республики в полном объеме, в том числе и детско-юношеские школы по хоккею с шайбой и фигурному катанию.   Учитывая вышеизложенное, а также и рекомендации Федерации хоккея России о наличии в субъектах РФ крытых ледовых арен из расчета 1 арена на 100 тыс. человек, считаем крайне важным принятое решение Министерства спорта Российской Федерации о введении объекта «Крытый каток с искусственным льдом в г. Ижевске» в 2012 году по линии федеральной целевой программы «Развитие физической культуры и спорта в РФ на 2006-2015 годы»', 'Объект выполнен в металлоконструкциях с использованием современных энергосберегающих материалов. При строительстве ледового поля использована технология модульной холодильной машины контейнерного типа расположенной за наружным контуром здания. Общая площадь здания/сооружения-6769,9 м2. Площадь спортивной составляющей-2039,6 м2. Максимальная пропускная способность-77 чел/смена. Наличие сооружений для размещения зрителей, вместимость-500 мест. ', 'Currently the only existing Ice Palace of “Izhstal” built in 1971 and required capital repair functions on the territory of the city of Izhevsk with 600 thousand residents.The main part of the working hours of the Palace is taken for trainings by “Izhstal” professional team playing in the high hockey league of the country. Such situation does not enable to provide demands of the republic residents for ice in sufficient amount including children and youth hockey and figure-skating schools. Taking into consideration mentioned above as well as recommendations of the Russian Hockey Federation on availability of covered ice arenas in the Russian Federation subjects based on 1 arena for 100 thousand people we consider the decision on putting of the “Covered skating-rink with artificial ice in the city of Izhevsk” into service in accordance with the Federal targeted programme of “Development of Physical fitness and sports in the Russian Federation in 2006-2012” to be absolutely essential.', 'A facility is built of steel structures using the latest energy-saving materials. During the construction of an ice rink, a container modular cooling machine located behind the outer contour of the building was used. ', 'Город Ижевск', 'Удмуртская Республика', NULL, 'г. Ижевск', 'Izhevsk', 'Удмуртская Республика, г. Ижевск, ул. Удмуртская', 'Udmurtskaya str., Izhevsk, Republic of Udmurtia', 94701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-01', '2013-01-15', 511900000, NULL, NULL, NULL, NULL, 'parahin@minsport18.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 53.2243, 56.8521, 60);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3492, 'Спортивно-оздоровительный лыжный комплекс имени Г. А. Кулаковой', 'Skiing Sports and Recreation Center named after G.A. Kulakova', true, 'Построенные лыжная база, стадион с трибунами и информационное табло активизировали развитие лыжного спорта в Удмуртской Республике, привлекают дополнительные инвестиции, способствуют развитию республиканского туристического бизнеса, обеспечили работой 60 человек. Появилась заинтересованность в проведении международных и российских соревнований и учебно-тренировочных сборов по зимним видам спорта на территории Удмуртской Республики. Удмуртская Республика является одним из ведущих центров подготовки спортсменов – лыжников для включения в состав сборной команды Российской Федерации. Строительство общежития для спортсменов с медико-восстановительным центром позволит создать более комфортные условия для размещения спортсменов и их медико-восстановительного обслуживания в периоды учебно-тренировочных сборов и проведения соревнований. Новые помещения позволят обеспечить работой дополнительно 25 человек. Повышается уровень привлекательности лыжного комплекса в проведении международных и российских соревнований по зимним видам спорта на территории Удмуртской Республики. Результаты спортивных достижений-2 место в эстафетной гонке среди мужчин на Олимпийских играх 2014 года в г.Сочи, 2 место в гонке на 50 км. среди мужчин на Олимпийских играх 2014 года в г.Сочи.  Спортивно-оздоровительный лыжный комплекс им. Г.А. Кулаковой является базовым центром подготовки сборных команд Удмуртской Республики по лыжным гонкам, спорту слепых и спорту инвалидов с нарушением опорно-двигательного аппарата, а&nbsp &nbsp также по велосипедному спорту. Кроме этого на комплексе проводятся все региональные соревнования, а также межрегиональные&nbsp &nbsp и Всероссийские соревнования по лыжным гонкам. Планируется с 2016 года открытие отделения Детско-юношеской спортивной школы олимпийского резерва с дневным графиком работы. В 2005 году Постановлением Правительства Удмуртской Республики было принято решение о строительстве нового здания Лыжной базы при Автономном учреждении Удмуртской Республики «Спортивно-оздоровительный лыжный комплекс им. Г.А. Кулаковой», приурочив ввод объекта в 2007 году к 65 – летию прославленной лыжницы Галины Алексеевны Кулаковой. В 2006 году был готов рабочий проект 1 –й очереди строительства «Спортивно-оздоровительный лыжный комплекс им. Г.А. Кулаковой». В январе 2007 года состоялось торжественное открытие новой Лыжной базы где прошли очередные республиканские сельские спортивные игры, где приняли участие команды всех сельских районов Удмуртской Республики с количеством участников свыше 1500 человек.', 'Возможно использование стадиона, здания лыжной базы и информационного табло в зимнее и летнее время. Зимой, во время соревнований по лыжным гонкам, стадион служит стартовой площадкой и финишной прямой. Летом стадион используется для тренировок спортсменов и проведения оздоровительных мероприятий для отдыхающих. Беговые дорожки стадиона переходят в сертифицированные лыжероллерные и лыжные трассы. Общежитие с медико-восстановительным центром располагается в непосредственной близости с сертифицированными лыжными и освещенными лыжероллерными трассами, стадионом и столовой. По функциональному назначению здание разделено на два блока: медико-восстановительный центр и общежитие, что создает спортсменам более благоприятные условия для их проживания и восстановления. Площадь спортивной составляющей &#40 лыжные и лыжероллерные трассы&#41 - 54000м2 Максимальная пропускная способность - 120 чел/смену Наличие сооружений для размещения зрителей, вместимость  200 мест.', 'Construction of hostel for sportsmen with a medical convalescent centre will enable to create more comfortable conditions for accommodation of the sportsmen and their medical convalescent service in the periods of training camps and competitions. New premises will enable to provide 25 persons more with job. The level of attractiveness of the skiing complex for delivering of international and Russian winter sports competitions in the territory of the Udmurtian Republic increases.', 'A hostel with a medical rehabilitation center is located in close proximity to certified ski and lighted ski roller runs, stadium and cafeteria. In terms of functional purpose the building is divided into two blocks: medical rehabilitation center and hostel, which provides athletes with more favorable conditions for their stay and recovery.', 'Город Ижевск', 'Удмуртская Республика', 'регионально-значимый', 'г. Ижевск', 'Izhevsk', 'Удмуртская Республика, г. Ижевск, Якшур-Бодьинский тракт, 14/7', '14/7 Yakshur-Bodyinskiy tract, Izhevsk, Republic of Udmurtia', 94701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2006-06-01', '2013-12-30', 370000000, NULL, NULL, NULL, '219733', 'vam-61@mail.ru', 'комплекс лыжный', '8(3412)58-70-69', 'www.solk14km.ru', 'лыжные гонки', 53.1566, 56.879, 61);
