INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3654, 'Детский спортивный оздоровительный центр', 'Children’s sports and recreation center', true, 'Увеличение обеспеченности населения Республики Коми спортивными сооружениями, привлечение населения региона к регулярным занятиям фихической культурой и спортом, достижение высоких результатов спортсменами Республики Коми на всероссийских и международных соревнованиях. Единственный современный спортивный комплекс в муниципальном образовании &quot Ижемский&quot . Является центром проведения спортивно-зрелищных мероприятий МО МР &quot Ижемский&quot . Появление данного объекта спорта позволило улучшить условия для занятий игровыми и силовыми видами спорта. Современное спортивное сооружение позволит проводить соревнования районного и регионального уровня. Даты крупных соревнований и значимых событий: Фестиваль по национальным видам спорта &quot Изъваса вермасемъяс&quot  Ижемские состязания – 27-29 марта 2015 года - На спортивном объекте проводились соревнования регионального уровня. ', NULL, 'Increase of provision of the Komi Republic population with sports facilities, involvement of residents of the region in physical trainings and sports on a regular basis, achievement of high results by the Komi Republic sportsmen on all-Russian and international competitions. The only modern sports complex in “Izhemsky” municipal community. It is a centre of sports and entertaining activities of “Izhemsky” municipal community. Appearance of this object enabled to improve conditions for going in for playing and combat sports. The modern sports facility will enable to hold competitions of district and regional levels.', NULL, 'Ижемский район', 'Республика Коми', NULL, 'с. Ижма', 'Izhma', 'Республика Коми, с. Ижма, ул. Чупрова, 142', '142 Chuprov str., Izhma, Izhemsky area, Republic of Komi', 87604420, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-14', '2010-12-29', 49500000, '9:00-21:00', NULL, NULL, NULL, 'sportagentrk@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, гиревой спорт, мини-футбол, пауэрлифтинг, тяжёлая атлетика', 53.9249, 65.0148, 64);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (3662, 'Ледовый дворец «Звездный»', 'Ice Sports Palace «Zvyozdny»', true, 'Увеличение обеспеченности населения Республики Коми спортивными сооружениями, привлечение населения региона к регулярным занятиям физической культурой и спортом, достижение высоких результатов спортсменами Республики Коми на всеросийских и международных соревнованиях. Крытый каток является единственным спортивным сооружением с искуственным льдом в муниципальном районе &quot Сосногорск&quot . Появление данного объекта спорта позволило улучшить условия для занятий хокеем с шайбой и создать благоприятные условия для занятий фигурным катанием. Современное спортивное сооружение позволит проводить соревнования по фигурному катанию и хоккею с шайбой различного уровня, в том числе зонального. Даты крупных соревнований и значимых событий: - Республиканские соревнования по хоккею на призы клуба &quot Золотая шайба&quot  среди юношей 2002-2003 г.р. в зачет XVI Коми республиканской Спартакиады учащихся образовательных учреждений &quot За здоровую РК в 21 веке&quot  – 21-25 января 2015 - Республиканские соревнования по хоккею на призы клуба &quot Золотая шайба&quot  среди юношей 2000-2001 г.р. в зачет XVI Коми республиканской Спартакиады учащихся образовательных учреждений &quot За здоровую РК в 21 веке&quot  – 29 января-1 февраля 2015. ', NULL, 'Increase of provision of the Komi Republic population with sports facilities, involvement of residents of the region in physical trainings and sports on a regular basis, achievement of high results by the Komi Republic sportsmen on all-Russian and international competitions. The covered skating-rink is the only sports construction with artificial ice in the “Sosnogorsk” municipal district. Appearance of this sports object enabled to improve conditions for ice hockey activities and create favourable conditions for figure skating trainings. The modern sports facility will enable to hold figure skating and ice hockey competitions of different levels including zonal.', NULL, 'Район Сосногорск', 'Республика Коми', NULL, 'г. Сосногорск', 'Town Sosnogorsk', 'Республика Коми, г. Сосногорск, ул. Парковая, 37', '37 Parkovaya str., Sosnogorsk, Sosnogorsky area, Republic of Komi', 87626122, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-01-01', '2011-03-21', 230800000, NULL, NULL, NULL, NULL, 'sportagentrk@yandex.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 53.8642, 63.5995, 64);
