INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (4959, 'Плавательный бассейн', 'Swimming pool', true, 'В спортивном комплексе созданы все условия для занятий плаванием маломобильных групп населения. Для инвалидов-колясочников предусмотрена подъемная платформа. В настоящее время в городе Шуе полностью отсутствуют плавательные басейны.', 'Спроектированный спортивный комплекс включает в себя два бассейна: большая ванна –размером 25 х 11 на 4 дорожки, с переменной глубиной от 1,8 м до 4 м, малая ванна – размером 5 х 8, предназначена для обучения плаванию детей до 14 лет. Большой бассейн оборудован вышкой для прыжков в воду высотой  5 метров и трамплином высотой 3 метра. Есть зал подготовительных занятий &#40 сухое плавание&#41 . Пропускная способность спортивного комплекса составляет 48 человек в смену. Трибуны рассчитаны на 200 мест.  ', 'The only football field with artificial turf on the territory of the municipality &quot Pribaikalskiy area.&quot  Every year, on the football field held 125 sports-cultural events of intraschool, school settlement, intra-settlement, district and national importance.', 'Designed sports complex includes two swimming pools, a large tub -size 25 x 11 withп 4 lanes, with a variable depth from 1.8 m to 4 m, the small bath - 5 x 8, designed for swimming training of children under 14 years old. The large swimming pool is equipped with diving board into the water with height of 5 meters and a trampoline 3 meters height. There is a gym for preparatory classes (dry swimming). Capacity of the sports complex is 48 people per shift. Grandstands have theп capacity of 200 seats.', 'Шуйский район', 'Ивановская область', NULL, 'г. Шуя', 'city Shuya', 'Ивановская область, г. Шуя, ул. Спортивная', 'Ivanovo region, Shuya, ul.', 24711000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-01-15', '2013-05-16', 128800000, NULL, NULL, NULL, NULL, NULL, 'бассейн', NULL, NULL, 'плавание', 41.3684, 56.8483, 2);
