INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (4975, 'ДЮСШ', 'Youth Sports school', true, 'Объект  имеет достаточно большое значение для развития спорта в г. Алексеевка, в частности футбола. Все крупные областные мероприятия проводятся на данном стадионе. Достаточно большое количество детей занимаются тренировочным процессам, для повышения своих собственных навыков в данном виде спорта.', 'Современное искусственное покрытие с использованием резинового гранулянта  Дренажная система   Синтетическое волокно 6см.', 'The facility has a large enough value for the development of sport in Alekseyevka, particularly football. All major regional events are held at this stadium. Quite a number of children are involved in the training process, to improve their own skills in this sport.', 'Modern artificial turf with rubber granulates  The drainage system  Synthetic fiber 6cm.', 'Алексеевский район', 'Белгородская область', NULL, 'г. Алексеевка', 'city Alekseevka', 'Белгородская область, г. Алексеевка, ул. Свердлова, 10/г', 'Alexeevka city, street. Sverdlov, 10/g', 14605000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-05-01', '2009-11-30', 28450000, NULL, NULL, NULL, NULL, 'ofsalex@yandex.ru', 'футбольное поле ', NULL, NULL, 'бейсбол, регби, русская лапта, футбол', 38.669, 50.6154, 2);
