INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5081, 'Лыжно-биатлонный комплекс «Ангарский»', 'Skiing and biathlon complex «Angarskiy»', true, 'Анализ эффективности реализуемых в рамках Программы мероприятий показал, что заложена основа для развития лыжных гонок, биатлона и массового катания на лыжах для всех слоев населения города – спортсмены и любители лыжных прогулок пользуются прокатом лыжного инвентаря и профессионально подготовленными лыжными трассами.   МАУ «ЛБК «Ангарский» реализует программы по направлениям лыжные гонки и биатлон на бесплатной основе. В учреждение постоянно открыт набор мальчиков и девочек в возрасте с 9 лет для занятия профильными видами спорта. Количество групп на всех этапах спортивной подготовки во всех отделениях устанавливается руководством Учреждения в зависимости от специфики вида спорта, наличия квалифицированных педагогических кадров, инвентаря, условий для занятий. Занятия проводятся на двух спортивных комплексах, принадлежащих учреждению на праве оперативного управления: – Лыжно-биатлонный комплекс «Ангарский» введен в эксплуатацию в феврале 2010г, расположен по адресу: Иркутская область, Ангарский район, 5,28 км автодороги Подъезд к с. Савватеевка, строение 1 &#40 в районе оздоровительного лагеря «им. Героев космонавтов»&#41  - Лыжная база по адресу: квартал 189, дом 1 &#40 рядом с поймой реки Китой&#41  юго-западная часть города. Одной из сильных и уникальных сторон МАУ «ЛБК «Ангарский» является наличие лыжероллерной трассы длиной 2,3 км, позволяющей осуществлять непрерывный тренировочный процесс лыжников. ', 'При проектировке строительства был предусмотрен и применен опыт строительства, уже имеющихся современных лыжно-биатлонных комплексов, в таких городах как Ханты-Мансийск, Саранск, Красноярск и др. ', 'Analysis of the effectiveness of implemented under the program events showed that the foundationя&lt br&gt was laid for the development of cross-country skiing, biathlon and mass skiing for all segments of the population of the city - the athletes and enthusiasts enjoy  rental ski equipment and professionally prepared ski slopes.', 'When designing the building experience in constructionпwas appliedпof the existing modern skiing and biathlon complex, in cities such as Khanty-Mansiysk, Saransk, Krasnoyarskпand others.', 'Ангарский г.о.', 'Иркутская область', NULL, 'г. Ангарск', 'city Angarsk', 'Иркутская область, Ангарский район, 5,28 км. Автодороги Ангарск-Савватеевка', 'Irkutsk region, Angarsk area of 5.28 km. Highway Angarsk-Savvateevka', 25703000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2007-08-01', '2010-02-27', 33500000, NULL, NULL, NULL, NULL, 'a.mesenko@mail.ru', 'комплекс биатлонно-лыжный', NULL, NULL, 'биатлон, лёгкая атлетика, лыжные гонки, общая физическая подготовка', 103.6402, 52.3324, 2);
