INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5091, 'Республиканский спортивный комплекс «Курган»', 'The republican sports complex «Kurgan»', true, 'Строительство объекта дало возможность развитию лыжных гонок и биатлона на более высоком уровне, увеличилось число занимающихся.&nbsp &nbsp &nbsp &nbsp Появилась возможность проведения соревнований российского уровня. Объект востребован всеми категориями граждан для проведения активного отдыха. Расположен в черте г. Петрозаводска между  микрорайонами. Это учебно-тренировочная база ШВСМ и любимое место отдыха горожан. На объекте проводилось первенство России среди молодежи по лыжным гонкам. Регулярно проходят региональные и городские  соревнования по лыжным гонкам, биатлону, спортивному ориентированию, кроссу. За зимний период 2011-2012 года на республиканском спортивном    комплексе прошло около 350 спортивных мероприятий.', 'Лыжные трассы  соответствуют  международным правилами проведения соревнований по лыжным гонкам и биатлону. Пропускная способность объекта в зимний период до 15.000 человек: участники тренировочного процесса, участники соревнований и зрители. Единовременная пропускная способность объекта 138 чел. Состав спортивного объекта: – Биатлонное стрельбище &#40 28 стрелковых направлений&#41   – Просторная оружейная комната &#40 емкость 210 ед. оружия&#41   – Лыжероллерная трасса 2,5 км.&#40 ширина 7,80м.&#41   – Освещенная трасса 2,5 км.  – Лыжная трасса: 1   2   2,5   3   3,3   4   5   7,5   10 км.  – Трибуны для зрителей  – Прокат спортивного инвентаря. Административное здание: – офис соревнований  – комментаторская  – пресс-конференц-зал  – столовая/кафе на 60 мест   – гостиница на 30 мест  – тренажерный зал. Раздевалки  Wax – кабины Тайминговое оборудование &#40 хронометраж&#41 : электронное сопровождение соревнований уровня Кубка России  – лицензионная программа SIWIDATA.   ', 'Facility area of ??64 hectares  is located within the city of Petrozavodsk between neighborhoods. This is a training base of Sports School of high sportsmanship  and a favorite vacation spot of townspeople. The facility held championship of Russia among young people in ski racing. It regularly hosts regional and city competitions in skiing, biathlon, orienteering, cross', 'Ski slopes are consistent with international rules of competition in cross country skiing and biathlon.', 'Петразоводский г.о.', 'Республика Карелия', NULL, 'г. Петрозаводск', 'Petrozavodsk', 'Республика Карелия, г. Петрозаводск, пр. Курганский, 3', 'The Republic of Karelia, Petrozavodsk, Kurgan travel, 2', 86701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2004-01-05', '2015-12-30', 123600000, NULL, NULL, NULL, '64', 'pushkina@gockomsportrk.ru', 'комплекс биатлонно-лыжный', '(8142) 532926', NULL, 'биатлон, лыжные гонки', 34.3491, 61.7643, 2);
