INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5099, 'Ледовая арена «Юность»', 'Ice arena «Yunost»', true, 'Строительство нового крытого ледового катка в г. Кургане позволило иметь свою круглогодичную тренировочную базу для воспитанников отделения фигурного катания ДЮСШ №6 г. Кургана, и сыграло положительную роль в развитии в регионе таких видов спорта, как хоккей с шайбой, фигурное катание, дало возможность проводить на более высоком уровне соревнования по данным видам спорта и послужило массовому привлечению населения области к занятиям зимними видами спорта', 'Для теплоснабжения объекта построена блочно-модульная котельная. Для заливки и уборки льда приобретены две льдоуборочные машины «ZAMBONI 445».', 'Construction of a new indoor ice rink in Kurgan allowed to have a year-round training base for pupils of Skating of Youth Sports School ь6 of Kurgan, and played a positive role in the development of regional sports such as ice hockey, figure skating, has made it possible to conduct at a higher level competitions for these types of sports and served as massive attraction of population of the region to  winter sports', 'For the heating of the object is built a modular boiler. For filling and cleaning the ice machine two ice cleaning machines ?ZAMBONI 445&quot  were built.', 'Город Курган', 'Курганская область', NULL, 'г. Курган', 'Kurgan', 'Курганская область, г. Курган, ул. Савельева, 5', 'City Barrow Street. Savelyev, 5', 37701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-03-19', '2010-12-24', 267900000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 65.3619, 55.4403, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5101, 'Спортивный комплекс «Молодежный»', 'Sports complex «Molodezhniy»', true, 'В спортивном комплексе «Молодежный» открыты региональные тренировочные центры по базовым олимпийским &#40 биатлон, греко-римская борьба&#41  и сурдлимпийским &#40 греко-римская борьба&#41  видам спорта. Спортивный комплекс стал базой для проведения соревнований областного, межрегионального и всероссийского уровня. В свободное от учебно-тренировочных занятий и спортивных соревнований время спортивный комплекс «Молодежный» функционирует в качестве межшкольного спортивного центра по проведению третьего урока физической культуры и внешкольной спортивной работы. Спортивный комплекс «Молодежный» стал первым спортивным объектом в Курганской области, где созданы все условия для занятий физической культурой и спортом маломобильных групп населения.', 'Над кровлей на выносной площадке расположена блочно-модульная котельная. В качества топлива используется природный газ.', 'The sports complex &quot Youth&quot  opened regional training centers for basic Olympic sports (biathlon, Greco-Roman wrestling) and Deaf Sports (Greco-Roman wrestling) . The sports complex was the base for the regional competitions, inter-regional and all-Russian level In its free fr om training sessions and sporting events  the sports complex &quot Molodezhny&quot  functions as interschool sports center for the third lesson of physical education and extracurricular sports activities. Sports complex &quot Molodezhny&quot  was the first sports facility in the Kurgan region, wh ere there are all the conditions for physical culture and sports people with limited mobility', 'Above the roof at the remote site there is a modular boiler. The fuel used is natural gas.', 'Город Курган', 'Курганская область', NULL, 'г. Курган', 'Kurgan', 'Курганская область, г. Курган,ул. Сибирская, 1/а', 'City Barrow Street. Siberian 1a', 37701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-10-07', '2009-12-04', 229000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, бокс, волейбол, гандбол, греко-римская борьба, мини-футбол, спортивная аэробика, тхэквондо, художественная гимнастика', 65.3628, 55.4398, 2);
