INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5533, 'Спорткомплекс с плавательным бассейном', 'Sports complex with a swimming pool', true, 'Детская юношеская спортивная школа, является центром дополнительного образования детей, а так же «кузнецой» кадров спортсменов Бобровского муниципального района.  Даты крупных соревнований и значимых событий:  В течении года - Финалы XII Спартакиады обучающихся Воронежской области среди городов и районов по волейболу, гандболу. Первенства Воронежской области по волейболу, каратэ  Финалы среди ССУЗ Воронежской области по волейболу.', 'При строительстве использовались типовые технологические решения.', 'Children''s and Youth Sports School is the center of additional education of children, as well as &quot smith&quot  which frames athletes of Bobrovsky municipal area.&lt br&gt Dates of major events and significant events:&lt br&gt During the year - Finals of XII Games of the students of the Voronezh region among cities and districts in volleyball, handball. Championship of the Voronezh region in volleyball, karate,&lt br&gt Finals of the Voronezh region inя volleyball.', 'During the construction of typical technological solutions were used.', 'Бобровский район', 'Воронежская область', NULL, 'г. Бобров', 'Town Bobrov', 'Воронежская область, г. Бобров, ул. Парижской Коммуны, 59/а ', 'Voronezh region, city Bobrov, street  of the Paris Commune, 59/a', 20604101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2005-05-23', '2007-12-29', 36900000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'бокс, волейбол, гандбол, каратэ, настольный теннис, плавание, спортивная гимнастика, тхэквондо, футбол, шахматы', 40.0367, 51.0981, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5540, 'Спортивно-оздоровительный центр «Дельфин»', 'Fitness center «Delphin»', true, 'Деятельность учреждения является удовлетворение потребностей населения в физическом и духовно-нравственном развитии посредством оказания услуг физкультурно-спортивной и оздоровительной направленности. Даты крупных соревнований и значимых событий: 1.« Большой  спортивный праздник «С Днём рождения, «ДЕЛЬФИН!»».-27 декабря 2.Рождественские старты» -  4-12 января 3. «Здоровый я ,здоровая семья, здоровая Россия!»-  ежегодные семейные соревнования по плаванию - март 4.Районнные соревнования по плаванию, посвящённые Дню Победы- 8 мая 5.. Районные соревнования по плаванию, посвящённые Дню защиты Детей.- 1 июня 6..Первенство Хохольского муниципального района по плаванию, посвящённое   Дню физкультурника Воронежской области - сентябрь  Помимо этого подробная информация о мероприятиях содержится в Приложении 3 •    Ежегодно в дни школьных каникул  с детьми проводятся мероприятия и соревнования, оздоровительной направленности. ', 'Здание представляет собой  спортивное сооружение – крытый плавательный бассейн спортивно-оздоровительного типа &#40 учебно-тренировочный&#41  с ванной 25*13,5 глубиной 1,1 до 2,2 м. Предназначен для обучения плаванию, оздоровительных занятий, тренировок, а также для проведения соревнований местного значения в присутствии ограниченного числа зрителей. Предусмотрены трибуны для зрителей на 100 мест. Конструкция каркаса из легких металлоконструкций со стеновым ограждением из панелей типа «Сэндвич». Размеры здания в осях 30*38,6 м. На первом этаже размещены раздевалки для мужчин и женщин, санузлы, мини-кафе, подсобные помещений, тренерские, медицинская комната, лаборатория. На втором этаже – спортивный зал для подготовительных занятий, трибуны, административные помещения.  В подвальной части расположены насосная и венткамера. В проекте предусматривается ванна бассейна из монолитного железобетона. Стальные конструкции каркаса рамы – из сварных и прокатных профилей. Для электроснабжения предусматривается комплектная трансформаторная подстанция. 1. Фундаменты под каркас здания – сборно- монолитный железобетонный столбчатого типа. 2. Каркас здания – одно- 2-х этажная многопролетная рама с наклонными скатными ригелями покрытия, в коньковой части которой предусмотрена возможность для прокладки воздуховодов вентиляции, воздушного отопления и др.инженерных коммуникаций  одноэтажная часть рамы имеет горизонтальный ригель  основные элементы рамы приняты из стальных прокатных двутавров по ГОСТ 260020-83  ригель покрытия – двускатная ферма. Устойчивость каркаса здания  обеспечивают вертикальные и горизонтальные стальные связи из уголков по ГОСТ 8509-93 и жесткие диски перекрытий и покрытия. 3. Стены:  3.1.наружные – многослойные  типа «Сэндвич», которые устанавливаются по цоколю из блоков ФБС. 3.2.внутренние из силикатного кирпича и гипсокартона 4.Перегородки: на 1-м этаже из гипсовых пазогребенных плит марки ПлГ, а в душевых, сауне, туалетах &#40 на 1-м и 2-х этажах&#41  марки ПлГВ, гидрофобизированных по ТУ 5742-007-16415648-98 На 2-м этаже перегородки каркасные с обшивкой плитами ГКЛ марки ПГКМ-4 по серии 1.231.9-10 вып.2 &#40 кроме душевых&#41  5. Перекрытие – перекрестная решетка из оцинкованных профилей ПО-40-0.6 ТУ 5262-032-02494680-95. 6. Покрытие над 2-м этажом и залом с плавательным бассейном – из оцинкованного профнастила С-1.0 ТУ 5260-199-02495680-00 7.  Крыша &#40 кровля&#41  – оцинкованный профиль &#40 финский профиль – под черепицу&#41  с дополнительным лакокрасочным покрытием ТУ 14-106-606-2000. 8.   Полы: керамогранит, керамическая плитка, бетон, линолеум  В зале для подготовительных занятий полы из деревянных реек толщиной 50 мм по деревянным лагам из брусьев сечением 50*60 мм. 9.   Проемы: 9.1.  оконные блоки – пластиковый стеклопакет с тройным остекление.        9.2.  дверные блоки - пластик 10.   Внутренняя отделка: Ванна бассейна, согласно задания на проектирование, облицовывается пленкой ПВХ – голубого цвета&#40 чаша&#41 , а по верхнему краю бассейна и обходные дорожки – керамической плиткой. Отделка раздевальных и душевых – полы и стены – керамическая плитка, потолки масляная покраска. В коридорах стены и перегородки нитроэмалями, полы – керамогранит. В служебных и административных помещениях – линолеум. В зале подготовительных занятий стены – покраска. 11.  Архитектурное оформление фасада – прямоугольное в плане, размерами в осях 30*38,600 метров, двухэтажное, с техническим подвалом. Разработано по проекту  повторного применения, разработанного ОАО «Липецкгражданпроект», и представляет собой современное сооружение с использованием новых современных материалов в отделке и технологией, обеспечивающей комфортность пребывания посетителей в бассейне. Выразительность достигается за счет применения своеобразных по форме оконных проемов и витражей, а также активно выдвинутых из основного объема здания лестничных клеток. Главный вход акцентрируется мощным полукруглым навесом на 4-х опорах &#40 столбах&#41  и развитой лестницей с большой площадкой перед входом в здание. 12.   Наружная отделка 13.   Прочие конструктивные элементы 13.1.   балконы, лоджии 13.2.   лестницы из сборных  железнобетонных ступеней по ГОСТ 8717.1-84 13.3.   прочие системы', 'Activity of the institution is meeting the needs of the population in the physical, spiritual and moral development through the provision of services to sports and sports and recreational nature.&lt br&gt Dates of major events and significant events:&lt br&gt 1. &quot Big sports holiday&quot  Happy Birthday, &quot Dolphin!&quot  &quot .- December 27 2. &quot Christmas starts&quot  - January 4-12&lt br&gt 3. &quot I''m healthy, healthy family, healthy Russia!&quot  - Annual family swimming competition - March&lt br&gt 4. District swimming competition_ dedicated to the Day of Victory May 8&lt br&gt 5 . District swimming competition dedicated Detey.-''s Day on June 1&lt br&gt 6. Championship of Khokholsky municipal district in swimming, devoted to the Day of the Voronezh region athletes - September&lt br&gt Besides detailed information on the activities is provided in Annex 3&lt br&gt ? Each year, during the school holidays with kids activities and competitionsяimproving orientation are held .', 'The building is a sports facility - indoor swimming pool of a sports club type (a training one) with bath 25 * 13.5 depth of 1.1 to 2.2 m.&lt br&gt &lt br&gt Designed for swimming training, health activities, training, as well as for the competition of local importance in the presence of a limited number of viewers.&lt br&gt &lt br&gt There are grandstands for spectators with 100 seats.&lt br&gt &lt br&gt The design of the frame is of light steel with wall fence panels &quot sandwich&quot .&lt br&gt &lt br&gt Sizes of the building in the axes areп 30 * 38.6 m.&lt br&gt &lt br&gt On the first floor there are changing rooms for men and women, bathrooms, mini-cafe, utility room, coaching, medical room, a laboratory.&lt br&gt &lt br&gt On the second floor - the sports gym for training sessions, bleachers, administrative offices.&lt br&gt &lt br&gt In the basement the pump and the technical rooms are located.&lt br&gt &lt br&gt The project envisages bath basin of reinforced concrete.&lt br&gt &lt br&gt Steel frame construction are joinery - of welded and rolled profiles.&lt br&gt &lt br&gt To provide electricity there is a transformer substation.&lt br&gt &lt br&gt 1. Foundations under the frame of the building - pre- fabricated reinforced concrete of columnar type.&lt br&gt &lt br&gt 2. The frame of the building - one 2-storey multispan frame with sloping _ cover bolts in the ridge portion which provides the opportunity for laying duct ventilation air heating and other engineering communications  single-storey part of the frame has a horizontal crossbar  the main elements of the frame are taken from the steel mill Tauris in accordance with state standard 260020-83  bolt cover - gable truss. Resistance of building frame provides vertical and horizontal steel ties of the corners in accordance with state standard 8509-93, and hard disks overlap and cover.&lt br&gt &lt br&gt 3. Walls:&lt br&gt &lt br&gt 3.1. Outer - layered &quot sandwich&quot , which are installed on the plinth of PBS blocks _&lt br&gt &lt br&gt 3.2. Inner are is of silica brick and drywall&lt br&gt &lt br&gt 4. Partitions: on the 1st floor is of gypsum boards PLG brands, and in the shower, sauna, toilet on the 1st and 2nd floors PlGV brand hydrophobized technical conditions- 5742-007-16415648-98&lt br&gt &lt br&gt On the 2nd floor walls are framed with trim plates GCR brand PGKM-4 series 1.231.9-10 issue 2 (except the shower&lt br&gt &lt br&gt 5. Overlap - is a cross lattice of galvanized profiles ON-TU 5262-032-02494680-95 40-0.6.&lt br&gt &lt br&gt 6. Coverage of the 2nd floor and a hall with a swimming pool - is made of galvanized corrugated board C-1.0 TU 5260-199-02495680-00&lt br&gt &lt br&gt 7. The roof (roof - galvanized profile (Finnish profile - under the shingles) with additional lacquer coating TU 14-106-606-2000.&lt br&gt &lt br&gt 8. Floors: granite, ceramic tile, concrete, linoleum &lt br&gt &lt br&gt In the hall for training sessions floors are made of wooden slats 50 mm thick on the wooden joists of beam with cross section 50 x 60 mm.&lt br&gt &lt br&gt 9. Openings:&lt br&gt &lt br&gt 9.1. window units - plastic windows with triple glazing.&lt br&gt &lt br&gt 9.2. door frames - plastic&lt br&gt &lt br&gt 10. Inside:&lt br&gt &lt br&gt Bath basin,is according to the design assignment, it is coated with PVC film - blue bowl), and the upper edge of the pool and circuitous paths - ceramic tiles.&lt br&gt &lt br&gt Finishing cloakroom and shower - floors and walls - terracotta tiles, ceilings oil painting.&lt br&gt &lt br&gt In the corridors of walls and partitions nitroenamels, flooring - granite. In the office and administrative buildings - linoleum. In the hall of the preparatory sessions of the wall - painting.&lt br&gt &lt br&gt 11. Appearance of the facade - rectangular, sized in axes 30 * 38,600 meters, two floors, with the technical basement. Designed by re-use projects, developed by JSC ?Lipetskgrazhdanproekt&quot , and is a modern building with new modern materials in the decoration and technology ensures a comfortable stay for visitors in the pool. Expressiveness is achieved by using unique in shape and stained glass window openings, and actively pushed out of the main volume of the building stairwells. The main entrance is accentuated by powerful semicircular canopy on 4 pillars columns) and the developed ladder with large platform in front of the building.&lt br&gt &lt br&gt 12. Exterior Finish&lt br&gt &lt br&gt 13. Other structural elements&lt br&gt &lt br&gt 13.1. balconies, loggias&lt br&gt &lt br&gt 13.2. prefabricated stairs of metal and concete steps in accordance with state standard 8717.1-84&lt br&gt &lt br&gt 13.3. other systems', 'Хохольский район', 'Воронежская область', NULL, 'пгт Хохольский', 'settlement Khokholsky', 'Воронежская область, пгт Хохольский, ул. Ленина, 14/а', 'Voronezh region, working village Khokholsky, Lenin Street, 14/а', 20656151, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-11-01', '2009-12-30', 84172129, '10:00-21:50', '10:00-20:50', '10:00-20:50', NULL, 'Bassein.delfin@yandex.ru', 'бассейн', NULL, NULL, 'плавание', 38.7836, 51.5766, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5545, 'Ледовый Дворец спорта «Космос»', 'Sports and fitness complex «Cosmos»', true, 'Ледовый Дворец спорта предназначен для проведения массовых мероприятий на льду &#40 катание на коньках&#41 , тренировок и выступлений спортсменов, а так же, для проведения массовых мероприятий и концертов. Социальная значимость ледового дворца подтверждает удовлетворенность потребности профессионального и массового спорта в физкультурно-оздоровительных, спортивных и спортивно-зрелищных услугах по следующим видам спорта: хоккей с шайбой и фигурное катание. Даты крупных соревнований и значимых событий: 1.    Январь 2010, Март 2011, Январь 2012, Март 2013 гг. –  «Ледовое шоу города Калуги». 2.    Ноябрь 2011 г. – Ледовое шоу Ильи Авербуха «Город Диско». 3.    Апрель 2011, 2012, 2013 гг. – Кубок Губернатора Калужской области. 4.    Сезон 2009-10, 2010-11, 2011-12, 2012-13 гг. – Чемпионат Калужской области по хоккею с шайбой. 5.    Сезон 2011-2012-2013 гг. – Первенство России среди юношей по хоккею с шайбой &#40 зона «Центр»&#41 . 6.    Июнь 2012 г. – спортивный праздник «День защиты детей», «Всероссийский олимпийский день». 7.    Март 2013 г. – мастер-класс Александра Абта, бронзового призера Чемпионата мира по фигурному катанию. 8.    Май-июнь 2013 г. – кубок по хоккею ЗАО «ВОЛЬВО-Восток». ', '1. С целью ускорения производства работ в зимний период времени подпольные каналы выполнены не в монолитном, а в сборном варианте из железобетонных лотков ЛП 4-30 серии 3-06-1287. 2. С целью применения новых прогрессивных строительных материалов, а также уменьшения вертикальных нагрузок на фундаменты произведена замена материала кирпичной стены толщиною 250 мм с полнотелого керамического кирпича марки М75 на блоки стеновые из ячеистых бетонов марки Д1000 и В5-В7,5. 3. С целью достижения более эффективного покрытия пола, покрытие полов в помещениях 1-го и 2-го этажа заменено на полимерное покрытие «Макро». 4. Для обеспечения своевременного ввода объекта в эксплуатацию, уменьшения трудозатрат и применения более прогрессивных технологий строительного производства произведена замена кирпичных перегородок 1-го этажа административно-бытового здания на перегородки из гипсоволокнистых листов по металлическому каркасу с заполнением их минеральными плитами. 5. Вентиляция ледового зала катка – приточно-вытяжная с механическим побуждением воздуха. Вытяжная вентиляция полностью компенсируется подачей приточного воздуха. Поддержание заданной температуры приточного воздуха обеспечивается с помощью водяных калориферов приточных установок. 6. Для улучшения воздухораспределения в зоне входной группы и силового зала воздухораспределители типа 1ВН заменены на анемостаты типа 4АПР в количестве обеспечивающем необходимый воздухообмен. 7. Автоматическое управление холодильной установкой осуществляется по температуре обратного потока хладоносителя. Система оснащена всеми средствами, необходимыми для автоматического управления холодильным контуром, конденсатором и насосами. Контроль работы, защита, регулирование производительности и автоматическое управление агрегатом осуществляется с помощью микропроцессорных блоков управления «ADAP KOOL» фирмы Danfoss. ', 'Ice Palaceя is designed for mass events on the ice (ice skating), training and performance of athletes, as well as, for public events and concerts. The social significance of the Ice Palace confirms that it meets the needs of professional and mass sports in sports and recreation, sports and sports-entertainment services in the following sports: ice hockey and figure skating.&lt br&gt Dates of major events and significant events:&lt br&gt 1. January 2010, March 2011, January 2012, March 2013. -&lt br&gt ?Ice show of the city of Kaluga.&quot &lt br&gt 2. November 2011 - Ice show of Ilya Averbuch &quot City of Disco&quot .&lt br&gt 3. April 2011, 2012, 2013. - Cup of the Governor of the Kaluga region.&lt br&gt 4. Season 2009-10, 2010-11, 2011-12, 2012-13. - Championship of Kaluga Region on ice hockey.&lt br&gt 5. Season 2011-2012-2013 - Championship of Russia among boys in ice hockey (zone &quot Center&quot ).&lt br&gt 6. June 2012 - sports festival &quot Children''s Day&quot , &quot Russian Olympic Day.&quot &lt br&gt 7. March 2013 - Master Class by Alexander Abt, a bronze medalist at the World Figure Skating Championships.&lt br&gt 8. May-June 2013 - Hockey cup of &quot Volvo-East.&quot ', '1. In order to accelerate the production of works in the winter time underground passages are not monolithic and in prefabricated state are reinforced concrete of trays 4-30 PL series 06/03/1287.&lt br&gt &lt br&gt &lt br&gt 2. For the purpose of the application of new advanced building materials, as well as reducing the vertical loads on foundations material brick of wall thickness of 250 mm was replaced with a corpulent ceramic brick model M75 blocks of cellular concrete wall brand D1000 and B5-B7.5.&lt br&gt &lt br&gt &lt br&gt &lt br&gt 3. In order to achieve more effective flooring, flooring in the rooms of the 1st and 2nd floor is replaced by a polymer coating &quot Macro&quot .&lt br&gt &lt br&gt &lt br&gt 4. To ensure the timely commissioning, reduce labor costs and the use of more advanced technologies of building production , the brick partition of the 1st floor of administrative and service buildings was replaced on the partition of gypsum sheets on metal frame with mineral filling their plates.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt 5. Ventilation of the ice rink - supply and exhaust of air with mechanical drive. Exhaust ventilation is fully compensated by ventilation of air. The desired temperature is maintained by the supply air with the help of water heaters air handling units.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt 6. To improve the air distribution in the zone of entrance hallпand gymthe diffusers 1VN were replaced by diffusers type 4APR in amounts providing the required ventilation.&lt br&gt &lt br&gt &lt br&gt 7. Automatic control of a refrigeration system is performed by reflux temperature coolant. The system is equipped with all facilities needed for the automatic control of cooling circuit, the condenser and pumps. Control work, protection, capacity control and automatic control unit by means of microprocessor is controlled by ?ADAP KOOL? of firm Danfoss.', 'г.о. город Калуга', 'Калужская область', NULL, 'г. Калуга', 'Kaluga', 'Калужская область, г. Калуга, ул. Генерала Попова, 17', '248023, Kaluga region, Kaluga, street Gen. Popov, 17', 29701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-01-08', '2009-12-30', 281012600, NULL, NULL, NULL, NULL, 'terekhin_va@adm.kaluga.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 36.2103, 54.4899, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5546, 'ФОК «Вымпел»', 'Sports and fitness complex «Vympel»', true, 'Расположена в центральной части города, что обеспечивает возможности тесного взаимодействия с образовательными и культурными учреждениями  города. Это многопрофильное учреждение, одна из важных составляющих образовательного пространства г. Калуги. Общая площадь комплекса – 2626 кв.м. спортивных залов – 4, методический центр – 1, оздоровительный центр – 1, психологический центр с комнатой релаксации -1, Спортивно-оздоровительный лагерь «Смена» - 1, гостиница – 12 мест, студийный центр, студия дизайн, музыкальная студия, фотостудия, издательская студия, центр досуга, актовый зал, кафе. Учебно-материальная база комплекса позволяет организовано, на современном оборудовании проводить учебно-воспитательную работу с обучающимися. Школа оснащена компьютерной и оргтехникой, музыкальной и осветительной аппаратурой, оборудовано 9 компьютерных рабочих мест. Все спортивные залы имеют специальное оборудование.', NULL, 'Located in the central part of the city, which provides the possibility of close cooperation with educational and cultural institutions of the city. This multi-institution, is one of the important components of the educational space of Kaluga. Total area - 2626 sq.m. gyms - 4, Methodological Centre - 1, a wellness center - 1, the psychological center with relaxation room -1 Fitness Camp &quot Smena&quot  - 1, hotel - 12 seats, a studio facility, design studio, music studio, photo studio, publishing studio, a leisure center, assembly hall, cafe. Training facilities of the complex allow you to organize events using modern equipment to carry out educational work with students. The school is equipped with a computer and office equipment, musical and lighting equipment, equipped with 9 computers. All gyms have special equipment.', NULL, 'г.о. город Калуга', 'Калужская область', NULL, 'г. Калуга', 'Kaluga', 'Калужская область, г. Калуга, ул. Телевизионная, 18', '248010 Kaluga, ul.Televizionnaya, 18', 29701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2002-01-07', '2007-01-01', 10000000, NULL, NULL, NULL, NULL, 'vympel.kaluga@mail.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'волейбол, дзюдо, пауэрлифтинг, тхэквондо', 36.2608, 54.5299, 2);
