INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5649, 'СДЮСШОР «Олимпиец»', 'Specialized Children and Youth Sports School of the Olympic Reserveя «Olympian»', true, 'Объект включает: футбольное поле с трибунами, рассчитанными на 6000 зрителей, легкоатлетические дорожки с современным искусственным покрытием, секторы для прыжков в длину и высоту, зал для занятий боксом, зал рукопашного боя, тренажёрный зал. Эти ресурсы обеспечивают возможность для населения города вести здоровый образ жизни, повышают интерес различных категорий граждан к регулярным занятиям физкультурой и спортом. Многофункциональный спортивный комплекс широкого спектра действия направленный на оздоровление и улучшение физической подготовки детей и подростков.', 'Строительство велось с использованием типовых технологий.', 'The facility includes a football field with bleachers, designed for 6,000 spectators, athletics track with modern artificial turf, sectors for the long jump and the height, gym boxing hall of martial arts, gym. These resources provide an opportunity for the city''s population to live a healthy lifestyle, increase the interest of the various categories of citizens to regular physical education and sports. &lt br&gt Multi-sports complex broad-spectrum aimed at improving and improving physical fitness of children and adolescents.', 'During the construction standard technological solutions were used.', 'Город Рязань', 'Рязанская область', NULL, 'г. Рязань', 'Ryazan', 'Рязанская область, г. Рязань, ул. Маяковского, 46', '390000, Ryazan region, the city of Ryazan, street Mayakovsky, 46',