INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5657, 'Дворец спорта «Олимпийский»', 'Sports Palace «Olympic»', true, 'Дворец спорта «Олимпийский» — многоцелевой спортивный комплекс, в котором можно проводить спортивные соревнования по различным видам спорта,  выступление звезд эстрады, ледовые шоу, праздничные мероприятия. Во Дворце спорта проводятся Всероссийские и международные соревнования: Чемпионат России по спортивным танцам, всероссийские соревнования по тхэквондо, Чемпионат мира по кикбоксингу. Ледовый дворец строился специально для активизации спортивно-массовой, физкультурно-оздоровительной, культурно-массовой работы, в целях организации досуга, привлечения населения г.Рязани и Рязанской области к систематическим занятиям физической культурой и спортом. Для посетителей во Дворце спорта работают: ледовая арена &#40 оздоровительное катание и дискотека на льду&#41 , тренажерный зал, теннис, бильярд, финская и инфракрасная сауны. В январе 2011г. на базе Дворца спорта «Олимпийский» по распоряжению Правительства Рязанской области создана Детско-юношеская спортивная школа. Работа в которой ведется по двум зимним олимпийским видам спорта: хоккей с шайбой и фигурное катание на коньках. Даты крупных соревнований и значимых событий: 2014г. Май-июнь – Чемпионат области по хоккею с мячом. 27-30 апреля - Первенство России по тхэквондо &#40 ВТФ&#41  среди юношей и девушек 12-14 лет. 23-26 апреля - Лично-командный чемпионат МВД России по дзюдо. 26-28 марта – Выставка Стройиндустрия. 24 марта – ледовое шоу И.Авербуха «Ледниковый период». 11-16 марта - Всероссийские соревнования юных хоккеистов клуба «Золотая шайба» среди команд Центрального, Южного и Северо- Кавказского федеральных округов. Январь – март - Чемпионат, Первенство и Кубок Рязанской области по хоккею с шайбой. Январь-март - Отборочный этап 3-го Всероссийского фестиваля по хоккею среди любительских команд Рязанской области Дивизион «Любитель 40&#43 » сезона 2013-2014 г.г. Январь-февраль - Первенство Молодежной хоккейной лиги- Всероссийского соревнования среди молодежных команд 2013-2014 г. Январь – февраль - Чемпионат Высшей хоккейной лиги  Всероссийского соревнования по хоккею. 2013 г. Сентябрь-декабрь - Чемпионат Высшей хоккейной лиги  Всероссийского соревнования по хоккею. 13-15 ноября – Выставка «Стройиндустрия - Энергосбережение» Октябрь-декабрь -   Отборочный этап 3-го Всероссийского фестиваля по хоккею среди любительских команд Рязанской области Дивизион «Любитель 40&#43 » сезона  2013-2014 г.г. 13 октября – концерт группы ДДТ. Сентябрь-декабрь - Первенство Молодежной хоккейной лиги- Всероссийского соревнования среди молодежных команд 2013-2014 г. Сентябрь – декабрь - Первенство России по хоккею с шайбой среди команд юниоров, юношей и младших юношей  МКС ФХР НУ «Хоккей «Регион-Центр». Июль-август - товарищеские матчи по хоккею среди команд Высшей лиги. 12-14 апреля - Всероссийские соревнования по тхэквондо «Звезда России» 27-29 марта - XIII специализированная выставка  «СТРОЙИНДУСТРИЯ-2013». 13-20 марта - Всероссийские соревнования юных хоккеистов клуба «Золотая шайба» среди команд Центрального и южного федерального округов &#40 12-13 лет&#41 . 12.03.2013 г. – ледовое шоу И.Авербуха Январь-февраль - Чемпионат Высшей хоккейной лиги Всероссийского соревнования по хоккею в сезоне 2012-2013гг. Январь – февраль - Первенство Молодежной хоккейной лиги- Всероссийского соревнования по хоккею среди молодежных команд 2012-2013 г. Январь-апрель 2013 г. - Первенство России по хоккею среди команд юниоров, юношей и младших юношей в сезоне 2012-2013г. МКС ФХР НУ &quot Хоккей &qu