INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5703, 'СДЮСШОР «Академия футбола», стадион «Локомотив»', '«Locomotive» stadium', true, 'В последнее время у населения и особенно у молодежи, возрос интерес к занятием физической культурой, в том числе и футболом. Тамбовские футболисты имеют высокие показатели в связи, с чем им хотелось бы проводить тренировки на современном стадионе. Так же на стадионе «Локомотив» проводятся региональные соревнования школ и университетов города  и области. До 2009 года в Академии футбола занимались 250 чел., а сейчас 1650. ', NULL, 'Recently, population and especially young people, have a growing interest in physical activity, including football. Tambov players have high rates in connection with which they would like to conduct training in the modern stadium. The stadium &quot Locomotive&quot  holds regional competitions of schools and universities in the city and region.&lt br&gt Until 2009, the Football Academy engaged 250 peopleand now 1650.', NULL, 'Город Тамбов', 'Тамбовская область', NULL, 'г. Тамбов', 'Tambov', 'Тамбовская область, г. Тамбов, ул. Гастелло, 46', '392000, the city of Tambov, ul.Gastello, 46', 68701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-03-16', '2009-12-16', 24823135, NULL, NULL, NULL, NULL, NULL, 'стадион', NULL, NULL, 'футбол', 41.4063, 52.7146, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5711, 'Футбольное поле ДЮСШ «Арсенал»', 'Youth Sports School «Arsenal»', true, 'Реконструкция футбольного поля стадиона МУДОД ДЮСШ «Арсенал» &#40 укладка искусственного покрытия с системой обогрева&#41  дало возможность осуществлять круглогодичный полноценный учебно-тренировочный процесс для всех групп обучающихся в ДЮСШ «Арсенал» &#40 453 чел.&#41 .     Кроме этого, футбольное поле круглогодично используется для проведения турниров и соревнований по футболу среди различных категорий населения г. Тулы, а так же, занятий групп здоровья. Даты крупных соревнований и значимых событий: - июль 2014г. – май 2015г. - матчи  Первенства России среди команд  Второго дивизиона зоны &quot Центр&quot  &#40 «Арсенал-2»&#41   - август 2014г. – май 2015г. – матчи Первенства России среди молодежных команд клубов РФПЛ &#40 «Арсенал-дубль»&#41   - апрель 2014г. – июнь 2014г.,  - апрель 2015г. – июнь 2015г. – матчи Первенства России МОА «Черноземье среди команд ДЮСШ, СДЮСШОР и ФК &#40 команды ДЮСШ «Арсенал» 4-х возрастов&#41 .  ', 'Уложено покрытие из искусственной травы Euro Grass M60 b1, соответствующее «Стандарту РФС – Требования к устройству и эксплуатации футбольных полей с искусственным.', NULL, NULL, 'Город Тула', 'Тульская область', NULL, 'г. Тула', 'Tula', 'Тульская область, г. Тула, пос. Косая Гора, ул. Генерала Горшкова, 15/а ', NULL, 70701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2012-07-02', '2012-12-26', 33221000, '09:00-22:00', '10:30-18:00', '10:30-18:00', NULL, 'kalitin08@rambler.ru', 'футбольное поле ', '8(4872) 24-36-79  35-73-72.', 'www.dussh-arsenal.ru', 'футбол', 37.5396, 54.1206, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5712, 'Стадион «Локомотив»', '«Locomotive» stadium', true, 'На базе Спортивного комплекса «Стадион «Локомотив»  проводятся учебно-тренировочные занятия по 8-ми видам спорта, в том числе спортивных секций детско-юношеской спортивной школы, спортивных федераций Уссурийского городского округа. По состоянию на 01 апреля 2015 года в детских спортивных секциях на объекте СК «Стадион «Локомотив» занималось 448 чел. Спортивные объекты без оплаты предоставляются членам обществ инвалидов Уссурийского городского округа, спортсменам спортивных сборных команд Уссурийского городского округа. На базе спортивных объектов проводятся занятия по физической культуре для учащихся общеобразовательных школ и студентов учреждений профессионального образования. На спортивных объектах тренируются футбольные и волейбольные команды предприятий и организаций Уссурийского городского округа. Единовременная пропускная способность спортивного комплекса  составляет 109 чел. Проходимость спортсменов и зрителей мероприятий, проводимых на базе СК «Стадион «Локомотив»,  в 1 кв. 2015 года составила – 11545 человек. Даты крупных соревнований и значимых событий: Первенство ДВФО по настольному теннису - 14.01 -18.01.2015г. Первенство Приморского края по мини-футболу - 24.01,25.01., 21.02,22.02., 01.03-05.03., 07.03., 13.03-15.03.2015г. Первенство Дальневосточного федерального округа по боксу среди юношей 1999-2000 г.р. - 25.02-28.02.2015г. Краевой этап Турнира «Мини-футбол в школу» - 30.01-31.01., 01.02, 07.02-08.02.2015г. Первенство Уссурийского городского округа  по мини-футболу – январь-март 2015г. Открытое Первенство Уссурийского городского округа  по баскетболу - 19.01.2015г. Турнир по мини-футболу «Зимние каникулы» - 05.01-10.01.2015г. Рождественский блицтурнир по волейболу - 11.01.2015г. Турнир по волейболу памяти Г.М. Носовича- 14.02-15.02.2015г. Турнир по мини-футболу памяти В.А. Матюшина - 09.02-12.02.2015г. Турнир по мини-футболу, посвященный Дню Защитника Отечества- 21.02-22.02.2015г. Турнир по мини-футболу «Уссурийская весна-2015» - 28.03-29.03.2015г. Открытое Первенство Приморского края по каратэ - 21.03-22.03.2015г. Открытый Чемпионат Уссурийского городского округа по Дартс - 27.03-29.03.2015г.. ', 'Многофункциональный спортивный комплекс, включающий: - стадион: футбольное поле с искусственным покрытием -1 шт. &#40 111м х 70 м&#41   беговые дорожки с искусственным покрытием - 6 дорожек &#40 110 м&#41  и 4 дорожки &#40 400 м&#41   сектор для прыжков в длину – 1 шт.   трибуны для зрителей на 1880 мест  - универсальный игровой спортивный зал &#40 1223,1 кв.м.&#41  с трибунами для зрителей на 235 посадочных мест   - спортивный зал для единоборств – 2 шт &#40 173 кв.м.&#41 .  - тренажерный зал – 1 шт &#40 170 кв.м.&#41   - зал фитнеса &#40 170 кв.м.&#41   - хоккейная коробка – 1 шт. &#40 30 м х 60 м&#41 . Футбольное поле – искусственный газон. Спортивное покрытие  в универсальном спортивном зале – «плавающий» паркет, изготовленный из северо-канадского клёна. ', NULL, NULL, 'Уссурийский г.о.', 'Приморский край', NULL, 'г. Уссурийск', 'Ussuriysk', 'Приморский край, г. Уссурийск, ул. Слободская, 6', 'Ussuriysk,', 5723000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2010-01-11', '2012-11-30', 83838900, '09:00-22:00', '09:00-22:00', NULL, NULL, 'usmolpol@yandeks.ru', 'стадион', '8 (423)433-51-26', NULL, 'армспорт (армрестлинг), бадминтон, баскетбол, волейбол, гандбол, единоборства (разное), лёгкая атлетика, мини-футбол, спортивная гимнастика, тяжёлая атлетика, футбол, хоккей, художественная гимнастика', 131.9852, 43.8022, 79);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5713, 'Административное здание в комплексе Центра подготовки лыжников и биатлонистов', 'Administrative building in the complex of ski track', true, 'Лыжный стадион, включающий в себя лыжную трассу длиной 2,5 и 1,0 км, стрельбище. Административное здание представляет собой здание переменной этажности, площадью около 360 м2. Размеры здания 24м х 12 м, высота до карниза – 6,25м, в двухэтажной части здания расположено судейское помещение, в одноэтажной- административно-бытовые помещения.  Регулярно проводятся соревнования по лыжным гонкам и биатлону,в том числе на призы двукратного олимпийского чемпиона Анатолия Алябьева. 21-22 марта - открытое лично-командное первенство по биатлону на призы двукратного олимпийского чемпиона А.Н. Алябьева. Сентябрь - 5 этап Кубка области. Чемпионат и Первенство области по биатлону. Даты крупных соревнований и значимых событий: Ежегодно с 2008 года на базе «Центра подготовки лыжников и биатлонистов в д. Кудринская» проводятся следующие соревнования: - В марте месяце - Открытое первенство Вологодской области по пневматическому биатлону160-180 участников от Вологодской, Архангельской областей и г.Санкт-Петербурга   - В октябре месяце- Кубок Губернатора Вологодской области по пневматическому биатлону160-180 участников от Вологодской, Архангельской областей  - В феврале месяце- Первенство Верховажского района по лыжным гонкам 70-80 участников  - В феврале месяце- Открытое первенство Верховажского района по пневматическому биатлону 70-80 участников  - В феврале месяце- Спартакиада школ Верховажско-го района &#40  3 соревнования- Лыжная гонка, «Юнный лыжник» Эстафета  100-130 участников  - В феврале месяце- «Лыжня России» до 100 участников  - Декабрь- Открытие сезона по лыжным гонкам 70-80 участников  - Декабрь- Открытие сезона по пневматическому биатлону 70-80 участников  - В марте месяце Верховажский лыжный марафон  - Сентябрь – Кросс- Спартакиада школ района, 120-130 участников  - Сентябрь- Первенство Верховажского района по летнему биатлону до 100 участников  - Учебно- тренировочные сборы &#40 кроме летних месяцев&#41  до 60 человек.  ', 'Здание переменной этажности, включающее в себя административно-бытовые помещения, судейскую, находится на территории лыжного стадиона &#40 лыжная трасса длиной 2,5, 1,0 км, стрельбище&#41 .  Описание значимых технологических решений, примененных при строительстве - металлоконструкции и сендвичпанели.', 'Ski stadium includes the ski run with length of 2.5 and 1.0 km, the shooting range. The administrative building is a building of variable height, the area of it is ??about 360 m2. Dimensions of the buildings are 24m x 12 m, from height to the eaves - 6,25m, in a two-storey part of the building judicial room, administrative buildings', NULL, 'Верховажский район', 'Вологодская область', NULL, 'дер. Кудринская', 'Kudrinskaya', 'Вологодская область, дер. Кудринская, 2/а', 'Vologda region, village Kudrinskaya', 19616000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-14', '2010-02-26', 1595500000, '10.00-17.30', '09.00-18.00', '09.00-18.00', NULL, 'verhzakaz@yandex.ru', 'комплекс биатлонно-лыжный', '9218263161', NULL, 'биатлон, лыжные гонки', 42.2971, 59.984, 80);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5906, 'Специализированная детско-юношеская спортивная школа дзюдо', 'Specialized Children and Youth Sports School of Judo', true, '«Центр дзюдо» будет способствовать  росту спортивных достижений, прослужит делу физического воспитания молодежи, откроет подрастающему поколению путь в массовый спорт и в спорт высших достижений. Муниципальное автономное образовательное учреждение дополнительного образования детей «Специализированная детско-юношеская спортивная школа дзюдо» открыта в июне 2012 года на базе первого в Приволжском Федеральном округе физкультурно-оздоровительного комплекса «Центр дзюдо». В новом ФОКе есть три татами с тренажерным залом, современные тренажеры для силовой подготовки дзюдоистов. История развития борьбы дзюдо в Оренбурге начинается с 1975 года. За эти годы в нашем регионе подготовлено два мастера спорта России международного класса, 27 мастеров спорта, 468 кандидатов в мастера спорта. Оренбургские спортсмены неоднократно становились победителями и призерами чемпионатов и первенств мира и Европы – это Ирина Уйманова, Михаил Мачин, Дмитрий Куликов и другие. На сегодняшний день в Оренбуржье дзюдо занимается 2270 человек, из них детей и подростков – 1115 человек. И открытие нового спортивного «Центра дзюдо» создало все необходимые условия для занятий этим видом спорта и его популяризации в нашей области. В процессе строительства и  оборудования открытия ФОК приняли огромное участие Губернатор Ю.А. Берг, который является Председателем Попечительского Совета Федерации дзюдо Оренбургской области  Глава Администрации г. Оренбурга Е.С. Арапов, руководство Федерации дзюдо. Школу возглавил Юсупбаев Ранит Рашитович, заслуженный тренер Российской Федерации, мастер спорта по дзюдо.', 'Фасад алюкобонд, кровля применена тепло-эффективная, легкая. В ограждающей конструкции здания была применена облегченная сэндвич панель, что способствует уменьшению нагрузки на фундамент и метало-каркас.', '&quot Judo Center&quot  will contribute to the growth of sports achievements will serve the cause of physical education of youth, will open the way to the younger generation in mass sport and elite sport. Municipal autonomous educational institution of additional education of children &quot Specialized Children and Youth Sports School of Judo&quot  was opened in June 2012 on the basis of the first in the Volga Federal District, a fitness center &quot Center of judo.&quot  New Fitness and Sports Centre has three tatami with a gym, modern fitness equipment for strength training wrestlers. History of the development of judo in Orenburg starts since 1975. Over the years in our region produced two Russian masters of sports of international class, 27 masters of sports, 468 candidates for master of sports. Orenburg athletes repeatedly became winners of the championships and World and European championships -я Irina Uymanova, Mike Machin, Dmitry Kulikov and others. Today in the Orenburg 2270 people are engaged in judo, among them children and adolescents - 1115. And the opening of the new sports &quot Judo Center&quot  has created all necessary conditions for this sport and promoting it in our area. In the process of constructing and equipping the opening Sports and Fitness Centre took a huge part Governor Y. A. Berg, who is the Chairman of the Board of Trustees of the Judo Federation of Orenburg region, Head of the Administration of Orenburg E.S. Arapov, heads of Judo Federation. School headed by Yusupbaev Ranit Rashitovich, Honored coach of the Russian Federation Master of Sports in judo.', 'Alucobond facade,п heat-efficient, easy roofing was applied. In the building envelope lightweight sandwich panel was applied, which helps reduce the load on the foundation and meta-framework.', 'Город Оренбург', 'Оренбургская область', NULL, 'г. Оренбург', 'The city of Orenburg', 'Оренбургская область, г. Оренбург, ул. Новая, 17/1', 'Orenburg region, Orenburg, New Street, 17/1', 53701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2006-01-01', '2007-11-09', 58400000, '08:00-18:30', NULL, NULL, NULL, 'orengorsportcom@mail.ru', 'зал спортивный', NULL, NULL, 'дзюдо', 55.0988, 51.8056, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5718, 'Дом спорта', 'Municipal Institution «Sports House»', true, 'Имеет большое общественное значение. Является единственным общедоступным спортивным сооружением круглогодичного использования на территории города Белозерск. Самое посещаемое учреждение в городе Белозерске.  Даты крупных соревнования и значимых событий: 05 апреля - первенство области по гиревому спорту среди юношей и девушек. Межрегиональный турнир среди юношеских команд по мини-футболу «Кубок памяти И. Чистякова». Первенство области по гиревому спорту.  Первенство области по мини-футболу &#40 отборочные туры&#41 . ', 'При реконструкции использовались типовые технологические решения.', 'It has a great public importance. It is the only public sports facility of year-round use in the city of Belozersk. Most visited place in Belozyorsk.', NULL, 'Белозерский район', 'Вологодская область', NULL, 'г. Белозерск', 'Belozersk', 'Вологодская область, г. Белозерск, ул. Карла Маркса, 35/а', '161200, Vologda Region, Belozersk, Karl Marx Street d.35a', 19610000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2012-01-16', '2014-08-28', 26683000, NULL, NULL, NULL, NULL, 'bibiksarovaog@gmail', 'многофункциональный спортивный комплекс', '81756 2-15-18', 'http://www.d03301.edu35.ru/', 'баскетбол, волейбол, гиревой спорт, лёгкая атлетика, мини-футбол, теннис, футбол, хоккей, шахматы', 37.7988, 60.029, 81);
