INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5732, 'Спортивный комплекс', 'Sports complex', true, 'Строительство комплекса позволило укрепить материально-техническую спортивную базу, увеличить количество занимающихся физической культурой и спортом. Даты крупных соревнований и значимых событий: 19-20 апреля 2013 г. – Чемпионат ЮФО по минифутболу.', 'Типовой проект.', 'Construction of the complex allowed strengthening material and technical sports base, increase the number of engaged in physical culture and sports.', 'Typical project', 'Павловский район', 'Краснодарский край', NULL, 'ст. Павловская', 'village Pavlovskaya', 'Краснодарский край, ст. Павловская', 'Krasnodar region, village Pavlovsk', 3639428, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2005-04-02', '2007-10-23', 55394800, '08:00-22:00', NULL, NULL, NULL, 'sportstroy@mail.ru', 'многофункциональный спортивный комплекс', '8 918 328-000-9 ', NULL, 'баскетбол, бокс, волейбол, гандбол, дзюдо, каратэ, лёгкая атлетика, мини-футбол, настольный теннис, тяжёлая атлетика, шахматы', 39.791, 46.1305, 7);
