INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5745, 'Многофункциональный зал СОШ', 'Multipurpose gym general education secondary school ', true, 'Объект представляет собой современное многофункциональное спортивное сооружение, на котором возможно проводить занятия по игровым видам спорта &#40 футбол, волейбол и т.д.&#41 . На данный момент физкультурно-оздоровительный комплекс пользуется огромной популярностью у детей и взрослых. Позволяет проводить соревнования республиканского уровня.', 'При строительстве объекта применялись современные технологии, материалы и оборудование.', 'The object is a modern multi-purpose sports facility, which makes it possible to conduct classes in team sports (football, volleyball, etc.). Currently fitness center is very popular with children and adults. Allows to hold competitions at the national level.', 'During the construction standard technological solutions, materials and equioment were used.', 'Ковылкинский район', 'Республика Мордовия', 'регионально-значимый', 'с. Рыбкино', 'village Rybkino', 'Республика Мордовия, с. Рыбкино, ул. Королева, 2', 'Republic of Mordovia, village Rybkin, Queen Street, 2', 89629467, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2007-12-29', 18937000, NULL, NULL, NULL, NULL, NULL, 'зал спортивный', NULL, NULL, 'бадминтон, баскетбол, бокс, волейбол, гандбол, гиревой спорт, лёгкая атлетика, мини-футбол, настольный теннис, шахматы, шашки', 43.7677, 54.253, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5752, 'Бассейн СОШ № 170', 'Swimming Pool of school № 170', true, 'Плавательный бассейн является социально значимым объектом, открытие которого дало новый импульс для развития массовой физической культуры и спорта среди различных категорий населения.  Деятельность данного учреждения позволяет развивать и популяризировать плавание.      Плавательный бассейн с ванной 25*11м - для проведения:  - тренировочные занятия  -  спортивно-массовые мероприятия по плаванию  - предоставление бассейна для занятий команд ветеранов спорта,  любителей плавания, массового плавания.     Бассейн для обучения детей плаванию 10*6м - для проведения: - тренировочные занятия детей, - физкультурно-оздоровительные занятия по детей. Для удобства спортсменов внутри бассейна предусмотрено размещение 2 раздевалок,  кабинет тренера, медицинский кабинет. Современная и продуманная внутренняя инфраструктура позволяет бассейну стать полноценной и комфортной ареной для начинающих спортсменов.   Вместимость человек в смену - 52 человека.  Количество посещений - 520 человек. ', 'Приточно-вытяжная вентиляция с системой осушения воздуха.  Очистка воды с использованием автоматизированных систем дозирования, УФ-стерилизация и грубой очистки кварцевым песком на современном оборудовании для плавательных бассейнов зарубежных признанных производителей.   ПВХ-мембрана – покрытие ванны бассейна сверхпрочным, долговечным, износостойким и хим. стойким материалом на основе поливинилхлоридов. - Применение металлических конструкций и высокая степень готовности позволило сократить сроки  строительства и  объемы бетонных работ. - Система водоподготовки с использованием автоматизированных систем дозирования, УФ-стерилизация и грубой очистки кварцевым песком позволяет комфортно эксплуатировать оборудование и получать воду высокого качества очистки. - Система вентиляции позволяет получать комфортный климат в помещении ванны бассейна и его профессиональную эксплуатацию. ', 'Swimming pool is a socially significant object, the opening of which has given a new impetus to the development of mass physical culture and sports among the various categories of the population.&lt br&gt The activities of this institution allow us to develop and popularize swimming.&lt br&gt Swimming pool with bath 25 * 11m - to hold :&lt br&gt - Training sessions,&lt br&gt - Sports events in swimming,&lt br&gt - Provision of pool Sports Team sports veterans, swimmers, mass swimming &lt br&gt Pool for kids learning to swim 10 * 6m - to hold :&lt br&gt - Training sessions of children&lt br&gt - Health and fitness classes for children.&lt br&gt &lt br&gt &lt br&gt &lt br&gt For the convenience of the athletes in the pool there are 2 dressing rooms, an office manager, medical office Modern and well thought out internal infrastructure allows the pool to become a full and comfortable arena for beginners.', 'Ventilation system with dehumidification.&lt br&gt &lt br&gt &lt br&gt &lt br&gt Water purification using automated dosing systems, UV sterilization and coarse quartz sand with modern equipment for swimming pools of recognized foreign manufacturers.&lt br&gt &lt br&gt &lt br&gt &lt br&gt PVC membrane - coating bath basin heavy-duty, durable, wear-resistant and chemical. resistant material based on polyvinyl chloride.&lt br&gt &lt br&gt - Use of metal structures and high availability has reduced construction time and the volume of concrete work.&lt br&gt &lt br&gt &lt br&gt &lt br&gt - Water treatment system using automated dosing systems, UV sterilization and coarse quartz sand makes it comfortable to operate the equipment and obtain high quality water purification.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt - The ventilation system allows to obtain a comfortable indoor climate bath basin and its professional service.', 'Город Новосибирск', 'Новосибирская область', NULL, 'г. Новосибирск', 'Novosibirsk', 'Новосибирская область, г. Новосибирск, ул. Новогодняя, 20/3', 'Novosibirsk region, Novosibirsk, street Christmas, 14/1', 50701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2007-02-02', '2009-12-30', 89957588, '09.00 - 21.00', '09.00 - 21.00', '09.00 - 21.00', NULL, 'ahch_wws@mail.ru', 'бассейн', 'тел/факс: +7 (383) 328-33-85  моб.тел.: +7 (923) 244-58-56 ', NULL, 'плавание', 82.9066, 54.9816, 82);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5763, 'Крытый каток с искусственным льдом «Юность»', 'Indoor ice rink with artificial ice «Youth»', true, 'Ледовая арена  - включает в себя ледовую площадку, трибуну для зрителей на 250 посадочных мест, которая позволяет проводить:  - тренировочные занятия отделений хоккея с шайбой, фигурного катания  -  спортивно-массовые мероприятия по хоккею с шайбой, фигурному катанию  - предоставление ледовой арены для занятий команд ветеранов спорта,  любителей хоккея, массового катания. Оздоровительная зона - включает в себя  тренажерный зал, который позволяет организовывать и проводить:  - тренировочные занятия отделений хоккея с шайбой, фигурного катания, - физкультурно-оздоровительные занятия по фитнесу, аэробике. Для удобства спортсменов внутри дворца предусмотрено размещение 6 раздевалок,  кабинет тренера, отдельный  разминочный зал с тренажерами, медицинский кабинет, мастерская  для точки коньков и ремонта экипировки, прачечная. Современная и продуманная внутренняя инфраструктура позволяет ледовому дворцу стать полноценной и комфортной ареной для спортсменов. ', 'Общая площадь – 3 113 кв.м. Ледовая площадка 28х56 м, трибуны на 250 мест, тренажерный зал, зал хореографии. Основными принципами эффективности технологических решений являются: - модульность - многофункциональность - компактность архитектурных решений - применение  конструкций, не требующих отделочных работ. Благодаря этому были  достигнуты максимальные показатели пропускной способности на единицу финансово-временных показателей. ', 'Ice Arena - includes an ice rink, a platform for audience up to 250 seats, which allows:&lt br&gt - Training sessions of ice hockey, figure skating,&lt br&gt - Sports events on ice hockey, figure skating,&lt br&gt - Provision of the ice arena for training ofя sports veterans teams, fans of hockey, public skating.&lt br&gt Wellness area - includes a gym, which allows you to organize and carry out:&lt br&gt &lt br&gt &lt br&gt &lt br&gt - Training sessions of ice hockey, figure skating,&lt br&gt - Health and fitness classes, aerobics.&lt br&gt For the convenience of the athletes there are 6 changing rooms inside the palace, a trainer''s room, a separate warm-up room with fitness equipment, medical office, a workshop to sharpen the skates and equipment repair, laundry. Modern and well thought out internal infrastructure allows the ice palace to become a full and comfortable arena for athletes.', 'Total area - 3113 sq.m. 28x56 m ice rink, stands at 250 people, gym, choreography.&lt br&gt &lt br&gt Core principles for effective technological solutions are:&lt br&gt &lt br&gt - Modular&lt br&gt &lt br&gt - Multifunctional&lt br&gt &lt br&gt - Compact architectural solutions&lt br&gt &lt br&gt - The use of designs that do not require finishing.&lt br&gt &lt br&gt With this has been achieved the maximum throughput capacity per unit of time and financial metrics.', 'Татарский район', 'Новосибирская область', NULL, 'г. Татарск', 'Tatarsk', 'Новосибирская область, г. Татарск, ул. Олега Кошевого, 63/а', 'Novosibirsk region city Tatar street Oleg Mishka, 63/а', 50650101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-08-07', '2013-04-05', 151200000, '9.00 - 19.00', NULL, NULL, NULL, '0019314@mail.ru', 'арена ледовая', '8(383)64-26-540', NULL, 'конькобежный спорт, хоккей', 75.9463, 55.2219, 83);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5775, 'Ледовый спортивный  комплекс «Локомотив»', 'Ice Sports Complex «Locomotive»', true, 'В 2012 году был введен в эксплуатацию ледовый дворец спорта, в связи с этим был дан огромный толчок для развития зимних видов спорта хоккей, фигурное катание, шорт-трек. В связи с открытием нового ледового дворца возросло количество занимающихся спортом детей в возрасте от 4-х до 18 лет насчитывается 330 человек в том числе: хоккей – 155 человек, фигурное катание – 145 человек, шортрек – 30человек. По воскресеньям еженедельно проводятся массовые катания, что влечет за собой новое количество людей привлеченных к здоровому образу жизни. ', '- Применение металлических конструкций по системе «укрупнительная сборка»  и высокая степень готовности конструкций в заводских условиях, позволило в условиях строительства в удаленном районе от промышленного центра максимально сократить сроки строительства, объемы бетонных работ. - Применение сэндвич-панелей, не требующих дополнительных расходов на отделку фасадов, существенно сократило сроки строительства. Здание АБК согласно проектного решения также выполнено из укрупненного металлокаркаса с заполнением из кирпича и облицованным вентилируемым фасадом из керамогранита. Кровля двухскатная из профлиста.  Спортивный комплекс состоит из здания ледовой арены и здания АБК. Ледовая арена представляет собой – ледовую площадку и трибуну для зрителей на 200 посадочных мест.  Здание АБК включает в себя гимнастический зал, зал хореографии, раздевалки с душевыми для занимающихся, кафе &#40 буфет&#41 . ', 'In 2012 an ice palace was put into operationя, in this connection there was a huge boost for the development of winter sports- ice hockey, figure skating, short track. In connection with the opening of a new Ice Palace the number of children involved in sports at the age of 4 to 18 years increased, there are 330 people, including, hockey - 155 people, figure skating - 145 people, shortrek - 30 people. On Sundays weekly public skating is held that entails a new number of people attracted to a healthy lifestyle.', '- Use of metal structures by the &quot pre-assembly&quot  and a high degree of readiness of structures in the factory, allowed in the conditions of construction in a remote area of the industrial center to shorten the construction time', 'Город Новосибирск', 'Новосибирская область', NULL, 'г. Новосибирск', 'Novosibirsk', 'Новосибирская область, г. Новосибирск, ул. Первомайская, 154', 'Novosibirsk region, Novosibirsk, st. Day, 154.', 50701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-12-01', '2012-06-01', 212200000, '08.00 - 20.00', '08.00 - 20.00', '08.00 - 20.00', NULL, 'nsksport2007@mail.ru', 'арена ледовая', '227-44-52', NULL, 'пауэрлифтинг, хоккей, художественная гимнастика', 83.0909, 54.9709, 84);
