INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5843, 'Футбольное поле стадиона «Торпедо»', 'Football stadium «Torpedo»', true, 'Данный объект находится в густонаселенном жилом районе Ижевска, вокруг в шаговой доступности располагаются пять общеобразовательных школ, два средне - специальных образовательных учреждения. Учитывая повышенную неблагополучную обстановку данного микрорайона, спортивный объект призван отвлечь как можно большее количество детей и подростков, прежде всего, от дурного влияния сложившегося устоя жизни района. Стадион находится в удобном месте с точки зрения транспортной развязки: недалеко находятся остановки общественного транспорта, удобные подъездные пути для личного автотранспорта. ', 'Используется современное искусственное футбольное покрытие. В ходе капитального ремонта стадиона «Торпедо» применялись типовые решения.', NULL, 'Modern artificial football coverage is used.', 'Город Ижевск', 'Удмуртская Республика', NULL, 'г. Ижевск', 'Izhevsk', 'Удмуртская Республика, г. Ижевск, ул. 9-Января, 231/а', '426050, Udmurtia, Izhevsk, ul. 9-January, 231', 94701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-07-01', '2012-10-20', 34078000, 'с 8.00 до 21.00', 'с 8.00 до 21.00', 'с 8.00 до 21.00', NULL, 'sportschool9@yandex.ru', 'футбольное поле ', '8(3412) 40-36-60 ', NULL, 'лёгкая атлетика, футбол, хоккей', 53.2501, 56.8797, 2);
