INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (5929, 'Легкоатлетический комплекс им. Е. Елесиной', 'Track and field complex named after E. Elesina', true, 'Единственный в городе уникальный легкоатлетический комплекс с покрытием  мирового класса – это спортивный и архитектурный престиж города., массовость и доступность занятий спортом, возможность заниматься спортом людям с ограниченными возможностями. Он расположен в центре города на границе городского реликтового бора. Наличие  лыже-роллерной трассы, трасс для бега по пересеченной местности и кроссов, уникальный микроклимат соснового бора привлекают на тренировки и сборы профессиональных спортсменов а также  жителей города на прогулки и оздоровительные пробежки Комплекс должен стать международным лицом легкой атлетики города Челябинска и всей России, выражая современные тенденции развития спорта.', 'Наружные стены здания выполнены из сочетания витражных  конструкций  и стен с утепленным  вентилируемый фасадом. Витражи предусмотрены с двухкамерным  стеклопакетом  с наружным закаленным  тонированным  стеклом, внутренним  закаленным стеклом и с  заполнением  аргоном.  Окна  выполнены в пластиковых  переплетах  с двухкамерным  стеклопакетом, что придает зданию современный , архитектурно цельный вид, и обеспечивает хорошую теплоизоляцию, витражное остекление обеспечивает превосходную светопроницаемость. Кровля здания мягкая, балластная, выполненная по последним технологиям с использований  водоизоляционной ПВХ-мембраны. При замене основания ядра и покрытия беговой дорожки было использовано профессиональное спортивное покрытие «MONDO - SUPER X PERFORMANCE»отвечающее самым высоким мировым стандартам.  ', 'The only unique in the athletics complex with a world class coating - a sports and architectural prestige of the city. Popularity and accessibility of sport, the opportunity to engage in sports for people with disabilities. It is located in the city center on the border of the urban relict forest. Ski-roller tracks are available, trails for cross-country run and cross, the unique microclimate of a pine forest attracted to workout and charges of professional athletes as well as residents of the city to walk and run . &lt br&gt &lt br&gt &lt br&gt Complex should become the international face of athletics in Chelyabinsk and all Russia, expressing the modern trends in sports.', 'The exterior walls of the building are made from a combination of stained glass designs and walls with the warmed ventilated facades.&lt br&gt &lt br&gt Stained glass windows are provided with triple-pane windows with outer tempered tinted glass, internal tempered glass and filled with argon. The windows are made in plastic bindings with triple-pane windows, which gives the building a modern, architecturally solid appearance, and provides good insulation, stained glass provides excellent light transmission.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt The roof is soft, ballast, made by the latest technology using water isolating PVC membrane.&lt br&gt &lt br&gt When the base core and cover of the treadmill was replacedп professional sports coverage ?MONDO - SUPER X PERFORMANCE? was used which meets the highest international standards.', 'Челябинский г.о.', 'Челябинская область', NULL, 'г. Челябинск', 'Chelyabinsk', 'Челябинская область, г. Челябинск пр-т Ленина, 84', '454080 the city of Chelyabinsk Lenin av. 84', 75701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2005-01-03', '2010-03-18', 316235700, NULL, NULL, NULL, NULL, 'gaev@lk74.ru', 'манеж легкоатлетический', N