INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6016, 'Стадион «Юность»', 'Stadium', true, 'Футбольное поле центрального стадиона г. Мурманска – одно из самых востребованных и посещаемых спортивных объектов. На каждом матче футбольной команды «Север»  его посещают от 1,5 до 2,0 тысяч человек. Даты крупных соревнований и значимых событий: Регулярные игры футбольной команды «Север» в Первенстве России и игры Чемпионата области.', 'При строительстве использовались типовые технологии.', 'Football field of the central stadium of Murmansk - one of the most popular and most visited sports facilities. Every match of the football team, &quot Sever&quot  is visited by 1.5 to 2.0 thousand people. &lt br&gt Dates of major events and significant events: &lt br&gt Regular games are of the football team, &quot Sever&quot  in the Russian Championship, Championship games of oblast.', 'During the construction there were used standard technologies.', 'Город Мурманск', 'Мурманская область', NULL, 'г. Мурманск', 'Murmansk', 'Мурманская область, г. Мурманск, ул. Челюскинцев, 1', 'Murmansk region, Murmansk, street Chelyuskintsev 1', 47701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2010-11-01', 27540000, NULL, NULL, NULL, NULL, 'afanasyev@gov-murman.ru', 'стадион', NULL, NULL, 'футбол', 33.0788, 68.9746, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6033, 'ФОК «Олимпийский»', 'Sports center with a universal game room and a swimming pool', true, 'Физкультурно- оздоровительной комплекс имеет в наличие плавательный бассейн, крытую ледовую арену, универсальный игровой зал, тренажерный зал, два зала сухого плавания. Основная деятельность- проведение тренировочных занятий . Комплекс посещают обучающиеся учреждений дошкольного образования, образовательных учреждений, среднего профессионального учреждения, воспитанники детских домов района, воспитанники социально- реабилитационного центра, и население Балахнинского района среди которого: пенсионеры, многодетные и малоимущие семьи, инвалиды и лица с ограниченными возможностями здоровья. ФОК вошел в программу строительства спорткомплексов по инициативе Губернатора Нижегородской области и запущен в эксплуатацию 30.10.2009. За 5 лет работы стал спортивным центром Балахнинского района, его посетили свыше 2млн.человек. Это жители района, Нижегородской области, ближнего и дальнего Зарубежья.  На спортивных площадках ФОКа были проведены многочисленные соревнования областного и российского уровня по различных видам спорта. Воспитанники комплекса являются чемпионами и призерами областных, российских и международных турниров по плаванию, баскетболу, хоккею с мячом, борьбе  и т.п. Спорткомплекс культивирует 16 видов спорта, которыми занимаются спортсмены от детского возраста до лиц с ограниченными возможностями. Ведется большая работа с пенсионерами  и людьми, страдающими алкогольной и наркотической зависимостью.  На данном спортивном комплексе проводились следующие спортивные соревнования: 1. Турнир по мини-хоккею с мячом на призы Святейшего Патриарха Московского и всея Руси. Второй этап. ПФО 24.01.-25.01.2014 г. &#40 ледовая арена&#41  2. Открытое Первенство ФОК «Олимпийский» по дзюдо. 09.03.2014 г. &#40 спортивный зал&#41  3. Открытое Первенство Нижегородской области по баскетболу среди девушек 2001 -2002 г.р. «ДЮБЛ» 03.04.-04.04.2014 г.  &#40 спортивный зал&#41  4. Первенство Нижегородской области по плаванию среди спортсменов 2003-04 г.р. 16.04.-17.04.2014 г. &#40 бассейн&#41  5. Первенство области по хоккею с    шайбой среди мальчиков 2003-2004г.р.  сезона 2013-2014 гг. февраль &#40 ледовая арена&#41  ', 'Строительство велось с использованием типовых технологических решений.', 'Sports and fitnessl complex has a swimming pool, a covered ice arena, versatile game room, gym, two gyms for dry swimming _ . Summary activity- conduct training exercises. Complex is visited by students who attend pre-school institutions, educational institutions and secondary vocational institutions, orphanages of the area,, students of social and rehabilitation center, and the population of that area, of Balakhninsky pensioners, large families and low-income families, disabled people _ . &lt br&gt Sports and Fitness Centre entered the program of construction of sports complexes on the initiative of the Governor of the Nizhny Novgorod region and put into operation 30.10.2009. Over 5 years of work became a sports center of Balakhninsky area,, it was visited by more than 2 milliion people. They are residents of the district, Nizhny Novgorod region, near and far abroad. Sports fields and fitness center has held many regional competitions and Russian level competitions in various sports. Pupils of the complex are the champions and winners of regional, Russian and international competitions in swimming, basketball, hockey, wrestling, etc. Sports Complex cultivates 16 sports, which involved athletes from childhood to persons with disabilities. Extensive work is done with pensioners and people suffering drug and alcohol addiction. &lt br&gt &lt br&gt This sports complex holds the following sporting events: &lt br&gt &lt br&gt 1. Tournament on mini-hockey for the prizes of Patriarch of Moscow and All Russia. The second stage. 24.01.-25.01.2014, (theяIce Arena) &lt br&gt &lt br&gt &lt br&gt 2. Open Championship of Sports and Fitness Centre &quot Olympiyskiy&quot  in judo. &lt br&gt 03.09.2014, (theяgym) &lt br&gt &lt br&gt &lt br&gt 3. Open Championship of Nizhny Novgorod region in basketball among girls born in 2001 -2002 , 03.04.-04.04.2014, (theяgym) &lt br&gt &lt br&gt 4. Nizhny Novgorod Region Championship in swimming among athletes born in 2003-04 16.04.-17.04.2014, (theяpool) &lt br&gt &lt br&gt 5. Championship in field hockey among boys 2003-2004 season of 2013-2014. February (Ice Arena)', NULL, 'Балахнинский район', 'Нижегородская область', NULL, 'г. Балахна', 'Balakhna', 'Нижегородская область, г. Балахна, ул. Олимпийская, 2', 'Nizhny Novgorod region, Balakhna town, street Olympic d.2', 22605101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-02-12', '2009-11-11', 67000000, '08.00 - 22.00', '08.00 - 20.00', NULL, NULL, 'fok-bal@mail.ru', 'многофункциональный спортивный комплекс', '8-831-44-9-77-96', NULL, 'баскетбол, волейбол, гандбол, лыжные гонки, настольный теннис, плавание, пляжный волейбол, спортивная аэробика, стритбол, танцевальный спорт, теннис, тхэквондо, фигурное катание, флорбол, футбол, хоккей, хоккей с мячом', 43.5502, 56.5112, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6083, 'Стадион-площадка', 'Stadium Courts', true, 'Данный объект является современным многофункциональным спортивным сооружением, предназначенным для проведения учебного процесса учащихся муниципального общеобразовательного учреждения «Лицей № 1». Также на данном объекте будет проходить учебно-тренировочный процесс районной детско-юношеской спортивной школы. После введения объекта в эксплуатацию ожидается увеличение желающих заниматься физической культурой и спортом, футболом, игровыми видами спорта и легкой атлетикой, открытие дополнительных групп  в ДЮСШ. Комплекс позволил увеличить количество спортсменов, занимающихся спортом, привлечь большее количество людей к здоровому образу жизни.  Стадион площадью 6,8 тысячи квадратных метров стоимостью 22 миллиона рублей открылся сегодня в лицее №1 города Камызяк Астраханской области.  Поле для мини-футбола, беговые дорожки, прыжковая яма, спортивная и гимнастическая площадки - в Южном Федеральном округе это первый стадион многофункционального назначения.  Стадион-площадка, как его называют в Камызяке, построен в рамках федеральной и районной программ по развитию физкультуры и спорта. Новый стадион относится к лицею №1, но заниматься здесь смогут и дети, и взрослые. Кроме жителей города, на новом стадионе ждут гостей и из близлежащих школ.  ', 'Плоскостное специализированное сооружение с трибунами на 100 мест.', 'This place is a modern multifunctional sports facility designed for the educational process of students of the municipal educational institution lyceumя ь 1. Also a this facility will hold the training process of the district youth sports school. After the introduction of object in operation it is expected to increase the number of people wanting to do sports, football, playing sports and athletics, the opening of additional groups in Youth.', NULL, 'Камызякский район', 'Астраханская область', NULL, 'г. Камызяк', 'Town Kamyzyak', 'Астраханская область, г. Камызяк, ул. Горького, 91/а', 'Astrakhan region, Kamyzyak Street. Gorky, 91/а', 12625101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-09-10', '2012-12-28', 22000000, NULL, NULL, NULL, NULL, NULL, 'стадион', '(8512) 495500', NULL, 'баскетбол, лёгкая атлетика, мини-футбол, футбол', 48.0749, 46.1047, 14);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6095, 'Крытый тренировочный каток «Ока»', 'Indoor training rink «Oka»', true, 'С вводом в эксплуатацию крытого тренировочного катка «Кристалл» с 2011 года, который является структурной единицей ДЮСШ «Ока», стал центром спортивной жизни Мурома.  На сегодняшний день на катке работают 2 тренера-преподавателя, которые обучаю бесплатно хоккею 4 группы &#40 80 детей&#41  в возрасте от 5 до 10 лет.  Помимо хоккея на катке обучаются фигурному катанию – 26 детей, шорт-треку и конькобежному спорту – 80.   Каждую неделю в воскресенье на катке для жителей организован социальный час массового катания по цене входного билета 10 рублей. В  утренние и дневные часы лед бесплатно предоставляется учащимся общеобразовательных школ округа, детским домам и обществу инвалидов. Почти за 2 года эксплуатации катка проведено  свыше 70  хоккейных матчей, 4 соревнования по шорт-треку  и 3 мероприятия для детей и взрослых «Семейные старты». Проведены областные соревнования по шорт-треку среди детских домов области. ', NULL, 'With the construction of indoor training rink &quot Crystal&quot  in 2011, which is a structural unit of the Youth Sports School &quot Oka&quot , became the center of the sports life of Murom.&lt br&gt Today the rink has 2 coaches who teach free hockey -4 groups (80 children) aged 5 to 10 years.&lt br&gt In addition to the hockey rink _ figure skating is taught - 26 children, and short-track speed skating - 80 children.&lt br&gt Every week _ _ the residents public skating is organized for the residents at the rink at the price of the entrance ticket 10 rubles. In the morning and afternoon hours the ice is free of charge for students of secondary schools of the districts, orphanages and people with disabilities. Almost 2 years of use over 70 roller hockey matches were held, 4 competitions in short-track and 3 activities for children and adults &quot Family Starts&quot . Regional competitions held in short track for orphanages of the area.', NULL, 'Муромский район', 'Владимирская область', NULL, 'г. Муром', 'City of Murom', 'Владимирская область, г. Муром, бул. Тихомирова', 'g. Murom, Boulevard Tikhomirov', 17735000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-01-15', '2011-08-31', 310500000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, 'конькобежный спорт, фигурное катание, хоккей', 42.0167, 55.5782, 2);
