INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6162, 'Футбольное поле с искусственным покрытием', 'Football field with artificial turf', true, '    Спортивный комплекс «Непецино» рассчитан на регулярные занятия физической культурой и спортом более 300 человек.      Спортивный комплекс служит для систематизации работы с молодежью сельского поселения, направленной на воспитание, образование, оказание помощи в социальной адаптации и оздоровление молодого поколения.     СК «Непецино»  включает в себя все необходимые помещения для проведения тренировочных занятий, спортивных и массово зрелищных мероприятий. Кроме того, имеет футбольное поле с искусственным покрытием, мини-футбольную, волейбольную площадку, стритбольную площадки и площадку для занятий ОФП, хоккейную коробку. Единовременная пропускная способность Спортивного комплекса «Непецино» составляет 154 человека. Данное учреждение является единственным на территории Коломенского района, в котором спортивный зал и футбольное поле с искусственным покрытием  отвечает всем стандартам для проведения областных и общероссийских соревнований по футболу и мини-футболу. Географическое расположение комплекса &#40 в непосредственной близости от трассы М5-Урал&#41  позволяет привлекать к себе большое количество спортсменов, как из Коломенского района, так и из близлежайших городов.  Даты крупных соревнований и значимых событий: 17.08.2014 – Районный турнир по футболу среди детей 2000-2004 г.р. 30.08.2014 – Районный турнир по футболу среди юношей 1996-2000 г.р. 06.09.2014 – Спортивный праздник, посвященный вводу в эксплуатацию футбольного поля с искусственным покрытие 21.09.2014 – Районный турнир по футболу среди ветеранов старше 40 лет 28.09.2014 – Спортивный праздник «День футбола», посвященный закрытию футбольного сезона в Коломенском районе 05.04.2015 - Районный турнир по футболу среди юношей 1996-2000 г.р., посвященный 70-летию победы в ВОВ  26.04.2015 – Открытый турнир ДЮСШ «Фортуна» по футболу, посвященный 70-летию победы в ВОВ  02.05.2015 – Турнир Открытия футбольного сезона 2015 года в Коломенском муниципальном районе,  посвященный 70-летию победы в ВОВ  04.05.2015 – Районный турнир по футболу среди ветеранов старше 40 лет, посвященный 70-летию победы в ВОВ  10.05-04.10.2015 - Первенство Московской области по футболу среди юношеских команд &#40 ДЮСШ «Фортуна»&#41  09.05-22.10.2015 Первенство Московской области по футболу среди подростковых команд &#40 ДЮСШ «Фортуна»&#41  18.04-25.10.2015 Первенство России по футболу среди команд III дивизиона &#40 ФК «СтАрс»&#41  11.05-24.10.2015 – Первенство Московской области по футболу среди мужских команд &#40 ФК «СтАрс-2»&#41  17.05-30.08.2015 - Первенство Коломенского муниципального района среди взрослых 28.06-02.08.2015 - Первенство Коломенского муниципального района среди юношей 1997-2001 г.р.', 'При строительстве применялись типовые технологии.', 'This object is the only one in the Kolomna district, in which the football field with artificial turf meets all the standards for the regional and Russian competitions in football and mini football. Geographical location (very close to the M5 highway-Ural) allows you to attract a large number of athletes, both from the Kolomna district and from nearby towns.&lt br&gt &lt br&gt &lt br&gt The main functions of a football field with artificial turf are: physical education, sports and recreation activities, sports training and competition. Systematic physical culture and sports will involve different groups: preschool children, youth, adults, people with disabilities. Construction of artificial turf is a priority, as it is football is the most popular sport in Kolomenskoye area, about 2 thousand people play football. Constructing of all the sports facilities will increase the number of people engaged in physical education and sports up to 13 thousand people. This facility will become the home stadium of Youth Sports School &quot Fortune&quot  to conduct Championship of Moscow Region in football among children and youth teams and FC &quot Stars&quot  to conduct the Championship of Russia among the teams of Division III.', 'During the construction there were applied standard technologies.', 'Коломенский район', 'Московская область', NULL, 'с. Непецино', 'Netsepino', 'Московоская область, с. Непецино, ул. Тимохина, 19', '140473, Moskovoskaya region, Kolomna district, village Nepetsino street Timokhina, 19.', 46622419, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-08-01', '2014-08-31', 18634768, '17:30-21:30', '17:30-21:00', '19:00-21:00', NULL, 'sk_nepecino@mail.ru', 'футбольное поле ', '8(916)725-70-48', NULL, 'мини-футбол, футбол', 38.6075, 55.2022, 94);
