INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6330, 'ФОК «АЙС»', 'Sports and Fitness Complex «ICE»', true, 'Улучшение состояния здоровья жителей города  снижение расходов бюджетных средств на лечение  профилактика преступности, наркомании и алкоголизма  увеличение числа занимающихся физической культурой и спортом  проведение активного досуга в свободное от учебы или работы время  улучшение уровня физического развития  стремление к спортивному образу жизни  выявление талантливых спортсменов  Повышение производительности труда сотрудников  создание здоровой социально-психологической атмосферы в производственных коллективах, возможность заниматься спортивно-оздоровительной деятельностью возле дома и вести здоровый образ жизни. ', 'Компоновка основных и вспомогательных зон спортивного центра решена с учетом специфики их функционирования. В составе спортивного центра для обеспечения учебно-тренировочного процесса предусмотрен зал для общефизической подготовки и методический кабинет. При спортивном зале с ледовым покрытием предусмотрены трибуны на 200 зрительских мест. Трибуны запроектированы стационарные, на основе металлического каркаса, в качестве мест для зрителей применены индивидуальные пластиковые сидения.', 'Improving the health of city dwellers, reduction of budgetary expenditures for treatment, crime prevention, drug and alcohol abuse, increase in the number engaged in physical culture and sports, holding leisure activities in their free time or work, improve the level of physical development, commitment to sports lifestyle, identification of talented athletes&lt br&gt Increase of employee productivity, create healthy social and psychological atmosphere in the production teams the opportunity to engage in sports and recreational activities outside the home and lead a healthy lifestyle.', 'The layout of the main and auxiliary areas of the sports center is worked out taking into account the specifics of their operation. As part of a sports center for the training process there is a general physical training gym and methodical study. At the gym with ice covering there are grandstands for 200 seats. Grandstands are designed as stationary, based on the metal frame, as seats for spectators are applied individual plastic seats.', 'Город Екатеринбург', 'Свердловская область', NULL, 'г. Екатеринбург', 'Ekaterinburg', 'Свердловская область, г. Екатеринбург, ул. Щербакова, 2/г', '620076, Sverdlovsk region, Ekaterinburg, street Shcherbakov, 2d', 65701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-01-10', '2013-10-28', 204627600, NULL, NULL, NULL, NULL, 'fok_ise@mail.ru', 'многофункциональный спортивный комплекс', '289-69-01', NULL, 'кёрлинг, фигурное катание, фитнес-аэробика, хоккей', 60.6469, 56.7914, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6339, 'Спортивный комплекс с 50-метровым бассейном', 'Sports complex with a 50-meter swimming pool', true, 'Строительство спортивного комплекса решает задачи: увеличение обеспеченности населения Томской области спортивными сооружениями, привлечение населения Томской области к регулярным занятиям физической культурой и спортом, достижение высоких результатов спортсменами Томской области на всероссийских и международных соревнованиях. Обеспечение возможности проведения учебно-тренировочного процесса на высоком профессиональном уровне для спортсменов, проживающих в г.Томске и Томской области и приезжающих из других регионов РФ и стран ближнего и дальнего зарубежья для сезонных тренировок, а так же проведения спортивных мероприятий регионального, всероссийского и международного уровня.  Количественные показатели: - повышение уровня обеспеченности  населения г.Томска бассейнами на 5&#37  к уровню обеспеченности до реализации проекта &#40 было 6&#37  от норматива, станет - 11&#37 &#41 ,  - увеличение мест для занятий физической культурой и спортом на 246 единиц,  - количество создаваемых рабочих мест – 50 единиц. ', 'В составе основных помещений: два зала бассейнов с ваннами размерами 50х25м и 25х25 м, с трибунами для зрителей на 1 000 и 468 мест соответственно, конференц-зал, комната совещания судей, залы «сухого» плавания и хореографии с инвентарными и методическим кабинетом, кабинет врача, комнаты дежурных медсестер с окнами на ванны, фитнес-центр с тренажерным залом, массажными кабинетами, саунами, кафе на 48 мест. ', 'The construction of a sports complex solves the tasks: an increase of availability of sports facilities of the Tomsk region, attracting population to regular physical training and sports in Tomsk region, athletes of Tomsk region achieve good results at national and international competitions. Ensuring the possibility of training process in a professional manner for athletes living in Tomsk and visitors from other regions of Russia and the countries of near and far abroad for seasonal workouts, as well as sporting events of regional, national and international level .&lt br&gt Quantitative indicators:&lt br&gt - increase the level of of availability of of Tomsk pools by 5% to a level availability before the project (it was 6% of the norm, would be - 11%),&lt br&gt - increase places for physical culture and sports to 246 units,&lt br&gt - the number of jobs created - 50 units.', 'As a part of the basic premises: two rooms with pools and tubs 50x25m size and 25x25 m, with tribunes for spectators at 1000 and 468 seats respectively, conference room, meeting room of judges rooms of &quot dry&quot  swimming and choreography with the inventory and methodical study, doctor`s office, nurses rooms with windows facing the baths, fitness center with gym, massage rooms, sauna, cafe for 48 seats.', 'Город Томск', 'Томская область', 'регионально-значимый', 'г. Томск', 'Tomsk', 'Томская область, г. Томск, ул. Энтузиастов', 'Tomsk region., Tomsk, ul.Entuziastov', 69701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2015-12-30', 865000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'водное поло, плавание, подводный спорт, синхронное плавание', 85.062, 56.4945, 2);
