INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6368, 'ДЮСШ по футболу «Кожаный мяч»', 'Municipal Educational Establishment of Supplementary Education of Children Youth Sports School «Kozhany Myach»', true, 'Футбольное поле расположено в жилом микрорайоне, помимо проведения учебно–тренировочных занятий спортшколы по футболу, соревнований, турниров, в свободное время площадки предоставляются для занятий спортом жителям микрорайона. На базе комплекса проводят занятия физической культурой близ- лежащие общеобразовательные школы. За время существования были проведены следующие спортивные мероприятия: детский турнир по мини - футболу, чемпионат и первенство ЮФО/СКФО по футболу,  турнир по футболу памяти В.Бубукина,  первенство края среди ДЮСШ, ДЮКФП, СДЮСШОР, «Кубок города Ставрополя», краевые соревнования по футболу среди ДЮСШ. Детский турнир по мини - футболу, чемпионат и первенство ЮФО/СКФО по футболу,  турнир по футболу памяти В.Бубукина,  первенство края среди ДЮСШ, ДЮКФП, СДЮСШОР, «Кубок города Ставрополя», краевые соревнования по футболу среди ДЮСШ.', 'Объект введен в эксплуатацию в 2012 г. Проведены работы по устройству искусственного футбольного покрытия размером 110х70, устройству ограждения.', 'The football field is located in the residential district, in addition to conducting training sessions of sports school, competitions, tournaments these leisure sites are available for sports residents of the neighborhood. PE classes of local public schools are carried out on the basis of the complex.&lt br&gt During its existence the following sports activities were conducted here: children''s tournament on mini - football championship and championship Southern Federal District / North Caucasus Federal District Football Football Tournament commemorating V.Bubukin, regional championship among Sports Schools , &quot Cup of the city of Stavropol '' competition among Youth Sports Schools.', 'The facility was put into operation in 2012. The works on construction of artificial football coverage of 110x70 , fencing were performed.', 'Город Ставрополь', 'Ставропольский край', NULL, 'г. Ставрополь', 'Stavropol', 'Ставропольский край, г. Ставрополь, ул. Готвальда, 6', 'Stavropol, ul.Gotvalda 6', 7701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2012-07-30', 9030000, '9.30 – 21.00', '9.30 – 21.00', '9.30 – 21.00', NULL, 'kskskcp@rambler.ru', 'футбольное поле ', NULL, NULL, 'футбол', 41.9691, 45.0445, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6456, 'Футбольное поле с искусственной травой на запасном поле стадиона «Десна» ДЮСШ «Смена»', 'Football field with artificial grass lawn on the spare field of the stadium «Desna»', true, 'Искусственное футбольное поле на запасном поле стадиона «Десна» является социально-значимым спортивным сооружением для жителей Бежицкого района г. Брянска и близлежащих населенных пунктов. На базе указанного спортивного сооружения осуществляют учебно-тренировочный и соревновательный процессы государственное бюджетное образовательное учреждение Брянская областная ДЮСШ по футболу «Смена», оказываются спортивно-оздоровительные услуги населению. Проводятся соревнования по футболу в рамках чемпионата России МОА «Черноземье» среди детей и юношей, областные и городские соревнования среди школьников и дворовых футбольных команд.', 'Искусственное футбольное поле размером 90&#215 60 м с ограждением, обходными дорожками и местами для участников соревнований. ', 'Artificial football field on the spare field of stadium &quot Desna&quot  is a  socially significant sporting facility for residents of Bezhitsk district of the city of Bryansk and nearby settlements. PE classes and training and competitive process of state budget educational institution of Bryansk Regional Youth Sports School &quot Smena&quot  are held here, sport and health services are provided to the population. Football competitions - the championship of Russia &quot Chernozemye&quot  among children and youth, regional and city competitions among schoolchildren and yard football teams.', 'Artificial football field size of 90 ? 60 m with fencing, circuitous paths and places for participants.', 'Брянский район', 'Брянская область', NULL, 'г. Брянск', 'Bryansk', 'Брянская область, г. Брянск, ул. Кромская, 48/а ', 'Bryansk region, Bryansk, st. Krom, 48/а ', 15701000, NULL, 'реконструкция', '2010-01-01', '2010-10-10', 18015000, '9.00-21.00', NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 34.2832, 53.318, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6373, 'Футбольное поле ДЮСШ № 17 ', 'Football field for the Municipal Educational Establishment of Supplementary Education of Children Youth Sports School number 17', true, 'Обустройство футбольного поля стадиона «Политехник» для МОУ ДОД ДЮСШ № 17 по футболу г.Томска искусственным покрытием  позволило увеличить количество учащихся на отделении футбола на 218 чел. Наличие оборудованного футбольного поля позволило более качественно организовать учебный процесс в ДЮСШ и проведение соревнований по футболу городского и областного уровня.   ', 'На футбольном поле уложено современное искусственное футбольное покрытие. Футбольное поле прошло сертификацию.', 'Construction of a football field of the stadium &quot Polytechnic&quot  for Yoyth Sports School number 17 of Tomsk with artificial surface will increase the number of students in the Department of football up to 218 people. Having equipped football field allowed to improve the organization of the teaching process in the Youth Sports School and conduct football competitions in football city and regional level.', 'On the football field there is modern artificial football coverage. The football field was certified.', 'Город Томск', 'Томская область', 'регионально-значимый', 'г. Томск', 'Tomsk', 'Томская область, г. Томск, ул. 19 Гвардейской дивизии, 20/1', 'Tomsk region., Tomsk, 19 Partsiezd Guards Division, 20/1', 69701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', NULL, '2010-09-30', 17950000, NULL, NULL, NULL, NULL, NULL, 'футбольное поле ', NULL, NULL, 'футбол', 84.9772, 56.448, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6375, 'ФОК «Арена»', 'Sports and Fitness complex «Arena», village Il''inskii', true, 'ФОК «Арена» - единственный большой всесезонный спортивный объект на территории Ильинского района &#40 около 19 тыс. чел.&#41 . Все спортивные мероприятия проходят в ФОКе либо на расположенным рядом стадионе, так же данный комплекс дает возможность постоянно заниматься спортом и физ. культурой более 500 детей и 200 взрослых. Ильинский район имеет возможность принимать Краевые и Всероссийские соревнования. ФОК является одним из главных мест посещения населения. Значимые соревнования, проводимые на базе ФКС «Арена» 2013-2014 гг: 06.01.2014 - Районные соревнования по баскетболу. 11.01.2014 - Первенство ПК среди мужских и женских команд по волейболу лига «Запад». 12.01.2014 - Краевые зональные соревнования по волейболу среди ветеранов. 18.01.2014 - Первенство ПК по волейболу среди женских и мужских команд лига «Запад». 02.03.2014 - Районные соревнования по волейболу, посвященные 8 Марта. 24.04.2014 - Всероссийские соревнования школьников «Президентские состязания». 17.05.2014 - Районные соревнования по легкой атлетике, в зачет спартакиады школ района. 24.05.2014 - Открытый краевой турнир по вольной борьбе Ильинского района. 09.08.2014 - Спортивный праздник «День физкультурника». 11.10.2014 - Районные соревнования по баскетболу, в зачет  «Спартакиады поселений -2014». 19.10.2014 - Зональные отборочные соревнования «Сельские спортивные игры -2014». 01.11.2014 - Районные соревнования «Папа, мама, я –спортивная семья» среди ДОУ. 16.11.2014 - ЮБЛ среди девушек 2002-2003 г.р.– 4 тур. 22.11.2014 - Районные соревнования по волейболу. 17.11.2014 - Фестиваль спорта и здоровья  «Быть здоровым - это стильно!». 10.08.2013 - Районный спортивный праздник День физкультурника. 29.09.2013 - Первенство Пермского края по футболу на приз клуба «Кожаный мяч» 2013-2014 Ильинский - Кудымкар. 14.12.2013 - Спортивный праздник «Добрые старты». 22.12.2013 - Краевой турнир по вольной борьбе среди юношей и девушек рамках Кубка края. Краевой турнир по вольной борьбе &#40 28.05.2015&#41    Фестиваль по спорту и творчеству среди людей с ограниченными возможностями детей &#40 05.06.2015&#41 . Спартакиада людей с ограниченными возможностями &#40 июнь, август, декабрь&#41 . Спартакиада поселений &#40 баскетбол&#41  &#40 октябрь&#41 . Спортивный праздник, посвященный Дню пожилого человека &#40 01.10.2015&#41 . Соревнования «Веселые старты» в зачет смотра-конкурса на лучшую организацию спортивно-массовой работы образовательных учреждений &#40 ноябрь&#41 . Добрые старты, посвященные Дню рождения Пермского края &#40 декабрь&#41 . Первенство района по волейболу, баскетболу &#40 декабрь&#41 .', 'Здание &#40 комплекс состоит из административных, хозяйственных и спортивных помещений. Имеет три этажа. Выполнен из сендвичпанелей на металлическом каркасе. На третьем этаже располагаются административные помещения. На втором этаже располагаются: кабинет врача, младшего обслуживающего персонала, АХЧ, буфет. Так же расположен тренажерный зал. раздевалка и душевой и сан узлом. Выходы на трибуны универсального зала. На первом этаже расположены 2 малых зала для занятий единоборствами &#40 борьба и рукопашный бой&#41 , офп, спортивные бальные танцы, черлидинг. Универсальный спортивный зал &#40 игровой&#41  для волейбола, баскетбол, футзала. 3 раздевалки. Трибуны в универсальном зале сделаны выше на 3 метра заход со второго этажа. Комплекс соответствует всем нормам и доступен для людей из маломобильных групп.', NULL, NULL, 'Ильинский район', 'Пермский край', NULL, 'пос. Ильинский', 'Il`insky village', 'Пермский край, пос. Ильинский, ул. Герцена, 18', 'Perm region, Ilyinsky district, pos. Il''inskii Street. Herzen, 18', 57620401, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2009-01-12', '2012-12-24', 72302600, 'Секции: 19:00-23:00', 'Секции: 19:00-23:00', NULL, NULL, 'lebver@yandex.ru', 'многофункциональный спортивный комплекс', NULL, 'http://и-спорт.рф/contacts', 'баскетбол, бокс, волейбол, вольная борьба, гиревой спорт, дартс, лёгкая атлетика, лыжное двоеборье, лыжные гонки, настольный теннис, пауэрлифтинг, рукопашный бой, фитнес-аэробика, футбол, черлидинг', 55.698, 58.5661, 97);
