INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6462, 'Спортивно-оздоровительный центр', 'Health and Fitness Center', true, 'Строительство нового спортивного объекта будет способствовать созданию временных рабочих мест в процессе строительства, а по окончании строительства при сдаче объекта повлечет за собой создание новых рабочих мест, как для специалистов сферы физической культуры и спорта, так и обслуживающего персонала объекта.  Строительство спортивно-оздоровительного центра непосредственно в жилом квартале обеспечит шаговую доступность для жителей разного возраста, а также доступность для инвалидов.  Спортивно-оздоровительный центр &#40 далее – СОЦ&#41 , предназначенный к функционированию круглогодично, при выходе на расчетную эксплуатационную нагрузку, будет оказывать платные услуги населению, которые будут востребованы, поскольку сооружение будет находиться в непосредственной близости от мест проживания, будет иметь современное оборудование, комфортные и безопасные условия для занятий. В первые два года функционирования СОЦ планируется направить его деятельность на организацию работы с населением и формирование материальной базы &#40 проката&#41 , чтобы в дальнейшем осуществлять деятельность за счет средств, вырученных от предоставления платных услуг.  Наличие отвечающего современным требованиям спортивного сооружения повысит интерес различных категорий граждан к занятиям физической культурой и спортом, будет способствовать развитию массового спорта, спартакиадного движения, увеличению двигательной активности населения, улучшению физической подготовленности молодежи допризывного возраста. Расходы общества на занятия физической культурой и спортом граждан следует рассматривать как экономически выгодное вложение в развитие человеческого потенциала и улучшение качества жизни граждан.  Спортивно-оздоровительный комплекс  построен по современным технологиям,  укомплектован новейшим   спортивным оборудованием и  инвентарем для проведения полноценных тренировочных занятий,  соревнований, чемпионатов и других спортивных мероприятий. Созданные условия и высокая квалификация инструкторов и тренеров позволят занимающимся комплекса достигать высоких спортивных результатов. ', 'Основное здание каркасного типа разработано на основе типового проекта 291-3-4 с.88 из легких металлоконструкций с наружными стенами из трёхслойных стеновых сэндвич-панелей. Ванна бассейна – монолитная железобетонная с армированием. Пристройка к спортзалу с несущими кирпичными стенами – слоистая кладка.  Внутренняя отделка помещений из современных материалов. общая площадь здания/сооружения - 3548,71 м2. площадь спортивной составляющей - 24384 м2. максимальная пропускная способность - бассейн – 160 чел./смену  спортивный зал – 120 чел./смену. Наличие сооружений для размещения зрителей - Бассейн  &#40 балкон для зрителей&#41  – 40 чел  Спортивный зал &#40 балкон для зрителей&#41  – 60 чел. Единовременная пропускная способность объекта спорта:  76 человек: - спортивный зал - 36 чел  - тренажерный зал -12 чел  - бассейн  - 28 чел.  ', 'Construction of a new sports facility will contribute to the creation of temporary jobs during construction and after construction it would entail the creation of new jobs, both for specialists in the sphere physical education and sports, as well as maintenance staff of the object.&lt br&gt Construction of sports and fitness center directly in the residential quarter will provide a walking distance for residents of all ages, as well as accessibility for people with disabilities.&lt br&gt Health and Fitness Center isя designed to functioning year round when entering the rated operating load it will provide paid services to the population which will be demanded, as the facility will be located in close proximity to places of residence, it will have modern equipment, comfortable and safe conditions for employment. In the first two years of operation of Health and Fitness Centre it is planned to direct its work on the organization of work with the community and the formation of a material base to carry out activities in the future due to the income received from the paid services.&lt br&gt &lt br&gt Having a sports facility meeting all modern requirements will increase the interest of different groups of citizens to physical culture and sport, will promote the development of mass sports, increase motor activity of the population, improve physical fitness of youth .&lt br&gt &lt br&gt &lt br&gt The cost for society to engage in physical training and sports people should be seen as a cost-effective investment in human development and improving the quality of life of citizens.', 'The main building of frame type is designed on the basis of a model project 291-3-4 p.88 of light metal with exterior walls of the three-layer wall panels. Bath basin -is пreinforced concrete . Extension to the gym with bearing brick walls - is layered masonry. Interior decoration is of modern materials.', 'Вязниковский район', 'Владимирская область', NULL, 'г. Вязники', 'Vyaznili', 'Владимирская область, г. Вязники, ул. Владимирская', 'Vladimir region, city Vyazniki, street Vladimir', 17610101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2011-09-22', '2014-03-14', 126870000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, каратэ, плавание, футбол', 42.1264, 56.2463, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6477, 'Физкультурно-оздоровительный комплекс', 'Sports and Fitness Complex', true, 'В настоящее время завершаются отделочные работы, и уже нынешней зимой жители поселка смогут опробовать спортивную инфраструктуру нового комплекса. В новом ФОКе будет работать универсальный спортивный зал размером 24 м х 42 м с трибунами на 197 зрительских мест.', NULL, 'Currently the work is being completed and already this winter the villagers will be able to try out the sports infrastructure of the new complex. A new sports gym will work in the new sports club of the size 24 m x 42 m with stands for 197 seats.', NULL, 'Светлинский район', 'Оренбургская область', NULL, 'пос. Светлый', 'settlement Svetliy', 'Оренбургская область, пос. Светлый', 'n. Bright', 53640428, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2012-01-02', '2014-04-02', 64000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'хоккей', 55.1355, 52.0688, 2);
