INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6536, 'Спортивный центр', 'Sports center', true, NULL, 'На первом этаже располагается спортивный зал для игры в баскетбол и волейбол, раздельные комнаты для спортсменов, комнаты инструкторов, душевые комнаты, вестибюль, гардероб посетителей, кабинет директора, кабинет врача, инвентарная, санузлы. На втором этаже располагаются два холла, венткамеры, балконы для зрителей, санузлы.  В цокольном этаже здания располагаются тренажёрный зал, зал для занятия фитнесом, раздевалки, душевые, санузлы, холл, комнаты инструкторов, сауна, комната массажа, комната отдыха, венткамера, электрощитовая, комната под оборудование бассейна. ', NULL, 'On the ground floor there is a gym for basketball and volleyball, separate rooms for athletes, trainers room, shower room, entrance hall, cloakroom for visitors, the principal''s office, doctor''s office, inventory, bathrooms.&lt br&gt &lt br&gt &lt br&gt &lt br&gt &lt br&gt On the second floor there are two halls, ventilation chamber, balconies for spectators, bathrooms. In the ground floor are located gym, fitness facilities, locker rooms, showers, bathrooms, lounge, rooms of instructors, sauna, massage room, recreation room, technical rooms, switchboard, room for equipment of pool.', 'г.о. город Улан-Удэ', 'Республика Бурятия', NULL, 'г. Улан-Удэ', 'Ulan-Ude', 'Республика Бурятия, г. Улан-Удэ, ул. Жердева ', 'Ulan-Ude, ul. Zherdev ', 81701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2012-01-01', 1323020500, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, NULL, 107.647, 51.8154, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6543, 'Физкультурно-оздоровительный комплекс', 'Sports and Fitness Complex', true, 'ФОК это единственное спортивное сооружение в районе. На базе ФОКа «Атлант» есть возможность проведения учебно – тренировочных сборов по различным видам спорта. В ФОКе культивируется 12 видов спорта, в которых занимаются 700 детей разного возраста.  ФОК удобно расположен в центре р.п.Шатки. По территории Шатковского района проходят две крупные транспортные магистрали – ж/д ветка с прямым сообщением Арзамас – Нижний Новгород – Москва – С.Петербург, Лукоянов – Саранск – Пенза и автомобильная трасса федерального  значения. В 200 метрах от здания ФОКа размещены кафе «Встреча» и ресторан, полноценное трехразовое питание в которых обойдется в 300 рублей, номер в местной гостинице будет стоить 500-600 рублей за сутки. Не далеко от ФОКа находится родник «Кипячий ключ», источник с лечебными грязями и лесная зона, где расположены профессиональные лыжные трассы с хорошим рельефом на дистанциях 2, 3, 5 км. Прекрасно, когда есть возможность выбрать тот режим физических нагрузок, который оптимально подходит вашему образу жизни, соответствует вашим предпочтениям и состоянию здоровья. ФОК стал украшением посёлка и гордостью его жителей. Приезжающие в Шатки гости из крупных городов удивляются наличию такого сооружения в глубинке и низким, по их понятиям, ценам на посещение катка, бассейна и других физкультурно-развлекательных объектов. С утра до позднего вечера возле комплекса можно встретить много людей, особенно детей, молодёжи, пенсионеров, многие посетители приезжают издалека. Даты крупных соревнований и значимых событий: 26 января - Традиционный турнир по мини-футболу среди команд девочек, посвященные памяти Т.Савичевой  9 февраля - Спортивный праздник, посвященный XXII  зимним Олимпийским играм в Сочи  31 мая - 1 июня - Традиционные областные соревнования по плаванию, посвященные Дню защиты детей  Июнь - Спортивный праздник «Всероссийский олимпийский день»  13-14 сентября - Традиционный турнир по хоккею среди юношеских команд, посвященный памяти заслуженного строителя России, уроженца Шатковского района Бажак Н.Г. ', 'Физкультурно-оздоровительный комплекс входит в разряд оздоровительных, досуго-развлекательных центров. Главная цель при строительстве ФОКов – объединение разнообразных видов спорта &#40 таких как хоккей, фигурное катание, волейбол, теннис, гандбол, баскетбол, футбол, плавание и т.д.&#41  с рядом досугово-развлекательных видов отдыха &#40 кинотеатр, кафе и т.д.&#41 , что обеспечит развитие массового спорта и разнообразие оздоровительных программ и видов отдыха населения районов Нижегородской области. В состав проекта ФОКа входит здание комплекса и футбольное поле с искусственным покрытием. ФОК состоит из 3-х блоков: 1-й блок: универсальный спортивный зал  2-й блок: бассейн, тренажёрные залы, кинозал  3-й блок: крытая ледовая арена, предназначенная для хоккея и фигурного катания.', 'Sports and Fitness Clubя is the only sports facility in the area. PE classes and health and fitness center &quot Atlas&quot  there is a possibility to conduct training sessions in various sports. 12 kinds of sports are practiced herein which 700 children of different ages are involved.&lt br&gt Sports and Fitness Clubя is conveniently located in the center Shatki. On the territory of Shatkovsky District there are two major highways - train branch with via direct Arzamas - Nizhny Novgorod - Moscow - St. Petersburg, Lukoyanov - Saransk - Penza and highway of federal importance. 200 meters from the building is located a caf? health and fitness center &quot Vstrecha&quot  and a restaurant, full meals will cost 300 rubles, a room in a local hotel will cost 500-600 rubles per day. Not far from the health and fitness center thereя is a spring _&quot Kipyathiy Klutch&quot , a source of therapeutic mud and forest areawhere there are professional ski runs with good relief at ranges 2, 3, 5 km. It is great to have the opportunity to choose the mode of physical activity that best suits your lifestyle ,meets your preferences and health. Sports and Fitness Clubя is the decoration of the village and the pride of its inhabitants. Visiitors to Shatki come from big cities are surprised to find such a place with low prices in the country .&lt br&gt From morning until late at night , you can meet a lot of people, especially children, youth seniors, many visitors come from afar near the complex.', 'Fitness center is included in the category of recreation, leisure and entertainment centers. The main objective in the construction of sports and recreation centers - the union of different sports (such as hockey, figure skating, volleyball, tennis, handball, basketball, soccer, swimming, etc.) with a number of leisure and recreational leisure activities (cinema, cafes, etc .) that will ensure the development of mass sports and a variety of wellness programs and types of recreation areas of the Nizhny Novgorod region.&lt br&gt &lt br&gt &lt br&gt &lt br&gt The project will include the building and fitness center and a football field with artificial turf. Fitness and health centre consists of 3 blocks:&lt br&gt &lt br&gt &lt br&gt 1st block: a universal gym &lt br&gt &lt br&gt &lt br&gt 2nd block: pool, exercise rooms, a cinema &lt br&gt &lt br&gt &lt br&gt Unit 3: indoor ice arena, designed for hockey and figure skating.', 'Шатковский район', 'Нижегородская область', NULL, 'р.п. Шатки', 'settlement Shatki', 'Нижегородская область, р.п. Шатки, ул. Советская,  25', 'Nizhny Novgorod region, Shatkovsky District, working village shaky street. Sovetskaya Str. 25.', 22657151, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2008-09-01', '2009-10-30', 110000000, NULL, NULL, NULL, NULL, 'atlant.fok@mail.ru', 'многофункциональный спортивный комплекс', '(83190) 4-32-40', NULL, 'аквааэробика, баскетбол, волейбол, лыжные гонки, мини-футбол, настольный теннис, пауэрлифтинг, плавание, пляжный волейбол, рукопашный бой, синхронное плавание, спортивная аэробика, теннис, фигурное катание, фитнес-аэробика, футбол, хоккей', 44.1229, 55.1913, 2);
