INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6836, 'Спортивный комплекс «Олимп» («Труд»)', '«Olymp» sports centre', true, 'Во Владикавказе на сегодняшний день нет спортивного комплекса с таким широким выбором услуг. Здесь предусмотрены: плавательный бассейн, сауна, тренажерный зал, бильярд с зоной отдыха, боулинг с зоной отдыха, магазин спортивных восстановительный центр, теннисный корт, летняя игровая площадка и др. Удачное расположение спортивного комплекса «Олимп» будет способствовать увеличению числа посетителей. Рядом находится стадион «Спартак», через дорогу легкоатлетический комплекс «Манеж», «Комсомольский парк» парк отдыха. ', NULL, 'In Vladikavkaz, today there is no sports complex with such a wide choice of services. Here there are: a swimming pool, sauna, gym, pool with seating area, a bowling alley with a sitting area sporting and recreation center, a tennis court, a summer playground, and others.&lt br&gt &lt br&gt The convenient location of the sports complex &quot Olympus&quot  will increase the number of visitors. Nearby is the stadium &quot Spartak&quot , across the athletics complex &quot Arena&quot , &quot Komsomol Park&quot  recreation park.', NULL, 'Город Владикавказ', 'Республика Северная Осетия-Алания', NULL, 'г. Владикавказ', 'Vladikavkaz', 'Республика Северная Осетия-Алания, Владикавказ, ул. Шмулевича, 6', 'Vladikavkaz, ul. Shmulevich 6', 90701000, NULL, 'реконструкция', '2000-01-15', '2014-12-01', 21000000, NULL, NULL, NULL, NULL, NULL, 'многофункциональный спортивный комплекс', NULL, NULL, 'аквааэробика, боулинг, вольная борьба, плавание, рукопашный бой, теннис', 44.6965, 43.0242, 2);
