INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6905, 'Губернский центр сноуборда и горных лыж на горе Туманная ', 'Provincial center of skiing and snowboarding on the mountain Tumannaya', true, 'Привлечение детей и молодежи к занятиям горнолыжным спортом и сноубордом, обеспечение подготовки спортсменов высокого уровня и олимпийского резерва, создание условий для организации учебно-тренировочного процесса, проведение соревнований в соответствии с требованиями современных международных стандартов, а также в целях эффективного развития туристской индустрии юга Кемеровской области Всероссийские соревнования по сноуборду -декабрь 2015г., Этап кубка России по сноуборду -февраль 2016г.,  Всероссийские соревнования по сноуборду  среди детей -март 2016г., Кубок Губернатора  &#40 Финал кубка России&#41 - март 2016г., Всероссийские соревнования по горнолыжному спорту на «Кубок Главы Таштагольского района»- март 2016г. многофункциональный спортивный комплекс: &#40 гостиница, кафе, игровой, тренажерный залы, мини футбольное поле, легко атлетические  беговые дорожки, 2-тенисных корта, баскетбольная площадка, волейбольная площадка,  настольный теннис, большой теннис, зал батутной подготовки, сноуборд парк, горнолыжные трассы,  лыжня здоровья. ', 'Учебная трасса: длина - 1600 м, перепад высот - 360 м  Трасса для сноуборда: длина - 1100 м, перепад высот - 330 м, система искусственного снегообразования. Трасса для профессиональных горнолыжников: длина - 1300 м, перепад высот - 330 м, сложный уровень, система искусственного снегообразования. Все трассы обслуживаю бугельные подъемники &#40 1 одноместный и 2 двухместных&#41  и ратраки. Имеется переход на гору Буланже. Инфраструктура На территории Центра только строятся – гостиница, спортинтернат на 70 мест и тренажерный зал. Спортсмены, которые приезжают на склон Туманной, останавливаются в санатории-профилактории «Ромашка» &#40 1 километр от Центра&#41 . У подножия горы, для гостей Центра — прокат любого горнолыжного инвентаря, а также имеется уютное кафе и чайная. Административно-спортивное здание запроектировано для проживания и обслуживания спортсменов, занимающихся сноубордом и горнолыжным спортом. Здание состоит из трех блоков. Блок «А» запроектировано в двух уровнях. На первом этаже запроектировано спортивный зал для общефизической подготовки, на втором этаже: тренажерный зал с инвентарной, раздевальные с душевыми кабинами и санузлами, две тренерские с душами, кабинет, санузел, помещение уборочного инвентаря. Блок «Б» запроектирован одноэтажным. В блоке размещена столовая на 80 мест, гардероб и санузлы для посетителей, комната охраны, помещение для спасателей, помещение для хранения лыж и для уборочного инвентаря. Работа столовой предусмотрена на сырье. Блок «В»  запроектирован в трех уровнях. На первом этаже размещены: фойе с местом для вахтера, СДЮШОР по горнолыжному спорту, СДЮШОР два методических кабинета для проведения теоретических занятий со спортсменами, др.служебные помещения. На втором и третьем этажах размещены жилые отделения для проживания 36-ти человек на каждом этаже, служебные помещения. ', NULL, NULL, 'Таштагольский район', 'Кемеровская область', 'регионально-значимый', 'г. Таштагол', 'Tashtagol', 'Кемеровская область, г. Таштагол, ул. Скворцова, 42', NULL, 32627101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2010-03-01', '2015-12-30', 383313000, NULL, NULL, NULL, NULL, NULL, 'комплекс горнолыжный', '8(38473) 3-31-49', NULL, 'баскетбол, волейбол, горнолыжный спорт, мини-футбол, настольный теннис, общая физическая подготовка, сноуборд, теннис', 87.8477, 52.7594, 2);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6909, 'Ледовая арена ', 'Ice arena ', true, 'В городе Шебекино 28 декабря 2013 года состоялся большой праздник – открылась Ледовая арена «Айсберг». В канун Нового года, на новом ледовом поприще собрались не только любители коньков, но и те, кто весело и с пользой для здоровья  желал провести свободное время, стать свидетелями яркой и запоминающейся церемонии открытия спортивного комплекса, который предназначен для оказания физкультурно-оздоровительных услуг населению, проведения хоккейных матчей, выступлений фигуристов, проведения различного рода спортивных, культурных, зрелищных и развлекательных мероприятий. В торжественной церемонии открытия приняли участие Губернатор Белгородской области Евгений Степанович Савченко, члены Правительства, глава администрации Шебекинского района Алексей Николаевич Калашников и почетные гости. Евгений Степанович вручил благодарности Губернатора Белгородской области тем, кто внес наиболее весомый вклад в строительство Ледовой арены. После торжественных речей, на свежий лед ступили юные спортсмены — фигуристы и хоккеисты из Белгорода. Грациозно совершив круг почета, спортсмены ознаменовали открытие Ледовой арены. Показательные выступления хоккеистов никого не оставили равнодушными. Зрители очень радовались, что самый мужественный и красивый вид спорта теперь будет доступен и шебекинцам. Не осталось без внимания организаторов и самое модное развлечение, сочетающее спорт и красоту — фигурное катание, которое захватило не только детей, но и взрослых. С замиранием сердца все смотрели на яркое катание. Не только сама арена была центром внимания. На ледовой сцене всех гостей комплекса приветствовали и шебекинские творческие коллективы. Их выступления были, как всегда, неповторимыми и запоминающимися. Заключительным аккордом стало массовое катание всех желающих на новом льду. Открытие состоялось, а праздник удался на славу. Ледовая арена оценена по достоинству. Это уже пятая аналогичная спортивная площадка в регионе после Белгорода, Старого Оскола, Губкина и Алексеевки.  Площадь дворца составляет 4 тысячи кв. метров, единовременная пропускная способность – 120 человек в смену. Помимо ледового поля в двухэтажном спорткомплексе расположены трибуны на 197 зрительских мест, а также тренажёрный зал.  В здании ледовой арены располагается детско-юношеская спортивная школа №3, на базе которой в 2014 году планируется открытие двух новых отделений: хоккея и фигурного катания.  На строительство объекта было направлено свыше 240 млн. рублей из различных источников финансирования.', NULL, NULL, NULL, 'Шебекинский район', 'Белгородская область', NULL, 'г. Шебекино', 'Schebekino ', 'Белгородская область, г. Шебекино', NULL, 14656101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', NULL, '2013-12-26', 110000000, NULL, NULL, NULL, NULL, NULL, 'арена ледовая', NULL, NULL, NULL, 36.8879, 50.4005, 2);
