INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6924, 'Физкультурно-оздоровительный комплекс', 'Sports and fitness complex', true, 'ФОК предполагается для создания условий, обеспечивающих гражданам Юхновского района  возможность систематически заниматься физической культурой и спортом для улучшения состояния здоровья  и  повышения конкурентоспособности юхновских спортсменов на официальных региональных и российских соревнованиях.', 'При проектировании применялась следующая нормативная документация: -СНиП 31 — 06 — 2009 -&quot Общественные здания и сооружения&quot    -СП 31-112-2004 -&quot   Свод правил по проектированию и строительству. Физкультурно-спортивные залы»   -СНиП 21-01-97&#40 2002&#41  - «Пожарная безопасность зданий и сооружений»  -СниП 35-01- 2001 - « Доступность зданий и сооружений для маломобильных групп населения». Запроектирован тренажерный зал, универсальный зал,     зал физкультурно- оздоровительных занятий с элементами борьбы,  вестибюль, гардероб для верхней одежды  помещения санитарно-гигиенического назначения, в том числе раздевальные, душевые, баня сухого жара с микробассейном, комната отдыха  помещение медицинского назначения — кабинет врача с ожидальной, тренерская с душем, санузлом  административное помещение, комната для обслуживающего персонала с душевой сеткой и санузлом. Для комплексной безопасности и защищенности   спортивного центра предусмотрено помещение для охраны с установкой  систем   видеонаблюдения, пожарной и охранной сигнализации. ', 'Sports and Fitness Complex is supposed to create conditions for citizens of Yukhnovsky District to systematically engage in physical culture and sports to improve the health andя enhance the competitiveness of Yukhnovskii athletes at the official Russian andя regional competitions', 'In the design there were used the following normative documents: -SNiP 31 - 06 - 2009 - &quot Public buildings and facilities&quot   -SP 31-112-2004 - &quot Code of practice for design and construction. Athletic and sports facilities&quot  &lt br&gt п -SNiP 21-01-97 (2002) - &quot Fire safety of buildings and structures&quot   -SniP 35-01- 2001 - &quot Accessibility of buildings for people with limited mobility.&quot  There is a project ofп a gym, multipurpose hall, sports and health activities with elements of wrestling, lobby, cloakroom for coats  premises for sanitary purposes, including locker, shower, sauna of dry heat with mikropool, dayroom  premises for medical purposes - the doctor''s office with waiting room, coach room with shower, WC  administrative offices, room for staff with a shower and bath mesh. For a comprehensive safety and security of the sports center there is space for protection with the installation of video surveillance systems, fire and burglar alarm systems.', 'Юхновский район', 'Калужская область', NULL, 'г. Юхнов', 'Yukhnov', 'Калужская область, г. Юхнов, ул. Карла Маркса, 24/а', 'Kaluga region, city Yukhnov, Karl Marx Street, 24a', 29650101, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-06-03', '2014-08-08', 25000000, NULL, NULL, NULL, NULL, 'silae.oksana@yandex.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'бадминтон, баскетбол, волейбол, вольная борьба, гандбол, гиревой спорт, мини-футбол, настольный теннис, общая физическая подготовка, пауэрлифтинг, спортивная гимнастика, спортивный туризм, теннис, тяжёлая атлетика, фитнес-аэробика, черлидинг, шахматы, шашки', 35.2205, 54.7413, 2);
