INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6926, 'Крытый тренировочный каток ', 'Indoor training rink', true, 'В последние годы климатические условия нашего региона создают трудности в содержании открытых ледовых площадок с естественным льдом. Темнее не менее, успехи российских хоккеистов и фигуристов способствуют большому росту популярности данных видов спорта среди населения. В настоящее время в городе работает один крытый каток с искусственным льдом в Советском районе. . Но даже при таком напряженном графике каток может посетить только часть желающих. Строительство крытого катка с искусственным льдом позволит во многом решить проблему создания условий для занятий катанием на коньках, хоккеем с шайбой и фигурным катанием жителям Заводского района, а также открыть дополнительные секции в целях привлечения большого количества детей и молодежи к здоровому образу жизни, создаст условия для развития новых олимпийских видов спорта, таких как шорт-трек, кёрлинг  ', 'При строительстве использовались типовые технологические решения.', NULL, NULL, 'Город Орел', 'Орловская область', NULL, 'г. Орел', 'Oryol', 'Орловская область, г. Орёл, ул. Зеленина', 'Orel region, Orel city, street Zelenina', 54701000, 'Развитие ФК и спорта в РФ (2006-2015)', 'реконструкция', '2012-01-16', '2015-12-30', 370100000, '06:00-01:00', NULL, NULL, NULL, 'dvboev@mail.ru', 'арена ледовая', NULL, NULL, 'фигурное катание, хоккей', 36.0192, 52.9533, 45);
INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6928, 'Спортивный центр с универсальным игровым залом и плавательным бассейном', 'Sports center with a universal sports gym and swimming pool', true, 'В двухэтажном спорткомплексе будут оборудованы два плавательных бассейна &#40 25 и 10 метров&#41 , универсальный спортивный зал с трибунами на 760 мест, а также зал для занятий аэробикой и настольным теннисом.', 'При проектировании и строительстве объекта применены типовые для данного вида спортивных сооружений технологические решения.', 'The two-storey sports complex will be equipped with two swimming pools (25 andя 10 meters long), all-purpose sports gym with stands for 760 seats, as well as an aerobics room and table tennis room.', 'In the design and construction of the object were appliedпtechnological solutions typical for this type of sport facilities.', 'Дорогобужский район', 'Смоленская область', NULL, 'г. Дорогобуж', 'Dorogobuzh', 'Смоленская область, г. Дорогобуж', 'Smolensk region, city Dorogobuzh', 66614101, NULL, 'строительство', '2011-01-10', '2015-12-27', 60000000, NULL, NULL, NULL, NULL, 'sport@admin.smolensk.ru', 'многофункциональный спортивный комплекс', NULL, NULL, 'баскетбол, волейбол, вольная борьба, плавание, футбол', 33.3024, 54.9138, 2);
