INSERT INTO public.sport_objects (id, name_ru, name_en, is_active, short_description_ru, detail_description_ru, short_description_en, detail_description_en, municipality, subject_of_federation, matter, city_ru, city_en, address_ru, address_en, oktmo, fcp, action, action_start_date, action_compliton_date, full_finance, working_hours, working_hours_sat, working_hours_sun, area, email, type, phone, url, kinds_of_sports, coor_x, coor_y, curator_id) VALUES (6945, 'Стадион-площадка', 'Stadium-courts', true, 'Данный объект является современным спортивным сооружением, отвечающий всем требованиям  проведения игр по футболу, волейболу,  легкой атлетике. Устройство искусственного покрытия последнего поколения, отвечающее стандартам Российского футбольного союза и требованиям РФС и FIFA. После введения объекта в эксплуатацию ожидается увеличение желающих заниматься спортом. Комплекс позволил увеличить количество спортсменов, занимающихся спортом, привлечь большее количество людей к здоровому образу жизни.', 'Плоскостное специализированное сооружение с трибунами на 100 мест.', 'This object is a modern sports facility, meeting all requirements of the games in football, volleyball, athletics. The device has the latest generation of artificial turf that meets the standards of the Russian Football Union and the requirements of the RAF and FIFA. After the introduction of object in operation is expected to increase willing to play sports.', NULL, 'Камызякский район', 'Астраханская область', NULL, 'пос. Кировский', 'Kirovsky settlement', 'Астраханская область,  пос. Кировский, ул. Калинина, 12', NULL, 12625162, 'Развитие ФК и спорта в РФ (2006-2015)', 'строительство', '2013-07-03', '2014-12-25', 23889830, NULL, NULL, NULL, NULL, 'mail@starcomplex.ru', 'стадион', '(8512) 495500', NULL, 'баскетбол, лёгкая атлетика, мини-футбол, футбол', 48.1221, 45.8468, 14);
